} from "../constants/GelatoRelay.sol";

abstract contract GelatoRelayERC2771Base is GelatoRelayContractsUtils {
    modifier onlyGelatoRelayERC2771() {
        require(_isGelatoRelayERC2771(msg.sender), "onlyGelatoRelayERC2771");
        _;
