    function settings2(
        address _improvements1,
        address _improvements2,
        address _improvements3,
        address _improvements4,
        address _wonders1,
        address _wonders2,
        address _wonders3,
        address _wonders4
    ) public onlyOwner {
        improvements1 = _improvements1;
        improvements2 = _improvements2;
        improvements3 = _improvements3;
        improvements4 = _improvements4;
        wonders1 = _wonders1;
        wonders2 = _wonders2;
        wonders3 = _wonders3;
        wonders4 = _wonders4;
    }

    ///@dev this function is only callable by the contract owner
    ///@dev this function will be called immediately after contract deployment in order to set contract pointers
    function settings3(
        address _military,
        address _forces,
        address _navy,
        address _navy2,
        address _navalActions,
        address _fighters,
        address _bombers
    ) public onlyOwner {
        military = _military;
        forces = _forces;
        navy = _navy;
        navy2 = _navy2;
        navalActions = _navalActions;
        fighters = _fighters;
        bombers = _bombers;
    }

    ///@dev this is a public function that allows the caller to mint a nation
    ///@notice this function allows the caller to mint a nation
    ///@notice each wallet address can only contain one country
    ///@param ruler this is a string that is the nation ruler name
    ///@param nationName this is a string that is the name of the nation
    ///@param capitalCity this is a string that is the name of the capital city of the nation
    ///@param nationSlogan this is a string that represents that slogan of the nation
    function generateCountry(
        string memory ruler,
        string memory nationName,
        string memory capitalCity,
        string memory nationSlogan
    ) public nonReentrant {
        require(
            ownerCountryCount[msg.sender] < MAX_NATIONS_PER_WALLET,
            "Cannot own more than 20 nations"
        );
        require(bytes(ruler).length <= 64, "Ruler Name too long");
        require(bytes(nationName).length <= 64, "Nation Name too long");
        require(bytes(capitalCity).length <= 64, "Capital Name too long");
        require(bytes(nationSlogan).length <= 128, "Slogan too long");
        _safeMint(msg.sender, countryId);
        idToOwner[countryId] = msg.sender;
        ownerCountryCount[msg.sender]++;
        ownerCountryIds[msg.sender].push(countryId);
        BombersContract(bombers).generateBombers(countryId);
        CountryParametersContract(payable(countryParameters)).generateCountryParameters(
            countryId,
            ruler,
            nationName,
            capitalCity,
            nationSlogan
        );
        FightersContract(fighters).generateFighters(countryId);
        ForcesContract(forces).generateForces(countryId);
        MissilesContract(missiles).generateMissiles(countryId);
        ImprovementsContract1(improvements1).generateImprovements(countryId);
        ImprovementsContract2(improvements2).generateImprovements(countryId);
        ImprovementsContract3(improvements3).generateImprovements(countryId);
        ImprovementsContract4(improvements4).generateImprovements(countryId);
        InfrastructureContract(infrastructure).generateInfrastructure(
            countryId
        );
        MilitaryContract(military).generateMilitary(countryId);
        NavalActionsContract(navalActions).generateNavalActions(countryId);
        NavyContract(navy).generateNavy(countryId);
        NavyContract2(navy2).generateNavy2(countryId);
        ResourcesContract(payable(resources)).generateResources(countryId);
        BonusResourcesContract(bonusResources).generateBonusResources(
            countryId
        );
        SenateContract(senate).generateVoter(countryId);
        TreasuryContract(treasury).generateTreasury(countryId);
        WondersContract1(wonders1).generateWonders1(countryId);
        WondersContract2(wonders2).generateWonders2(countryId);
        WondersContract3(wonders3).generateWonders3(countryId);
        WondersContract4(wonders4).generateWonders4(countryId);
        emit NationCreated(nationName, ruler, countryId, msg.sender);
        countryId++;
    }

    ///@dev this function will return the current country Id that gets incremented every time a county is minted
    ///@return uint256 will be number of countries minted
    function getCountryCount() public view returns (uint256) {
        return countryId;
    }

    function checkOwnership(
        uint256 nationId,
        address caller
    ) public view returns (bool) {
        address owner = ownerOf(nationId);
        if (owner == caller) {
            return true;
        } else {
            return false;
        }
    }

    function tokensOfOwner(
        address owner
    ) public view returns (uint256[] memory) {
        return ownerCountryIds[owner];
    }

    function deleteNation(uint256 nationId) public nonReentrant {
        require(_isApprovedOrOwner(msg.sender, nationId), "Not authorized");

        address owner = ownerOf(nationId);

        uint256[] storage ownedIds = ownerCountryIds[owner];
        for (uint256 i = 0; i < ownedIds.length; i++) {
            if (ownedIds[i] == nationId) {
                ownedIds[i] = ownedIds[ownedIds.length - 1];
                ownedIds.pop();
                break;
            }
        }

        ownerCountryCount[owner]--;
        delete idToOwner[nationId];

        _burn(nationId);

        emit Transfer(owner, address(0), nationId);
    }

    ///@dev this function will transfer the ownership of a nation to another address
    ///@param nationId this is the id of the nation that will be transferred
    ///@param newOwner this is the address of the new owner of the nation
    ///@notice this function will only transfer the ownership of the nation to another address
    function transferNation(uint256 nationId, address newOwner) public {
        require(newOwner != address(0), "Cannot transfer to zero address");
        require(newOwner != msg.sender, "Cannot transfer to yourself");
        require(_isApprovedOrOwner(msg.sender, nationId), "Not authorized");

        address previousOwner = ownerOf(nationId);

        _transfer(previousOwner, newOwner, nationId);

        ownerCountryCount[previousOwner]--;
        ownerCountryCount[newOwner]++;

        uint256[] storage ownedIds = ownerCountryIds[previousOwner];
        for (uint256 i = 0; i < ownedIds.length; i++) {
            if (ownedIds[i] == nationId) {
                ownedIds[i] = ownedIds[ownedIds.length - 1];
                ownedIds.pop();
                break;
            }
        }

        ownerCountryIds[newOwner].push(nationId);

        emit Transfer(previousOwner, newOwner, nationId);
    }

    ///to my darling, i made this for you because without you this game does not exist
    ///thank you for making this dream real
    function loveLetter() public pure returns (string memory) {
        return
            "To my darling wife, I made this for you. Without you this game does not exist. Thank you for being by my side for every step and making this dream real. I hope playing this will enrich your life because you enrich mine. My love for you is immutable.";
    }
}
","keccak256":"0x770b6387e09a95cae7f564469bacfdb54afb1f14e05d8adaf636076eb49787c0","license":"BUSL-1.1"},"contracts/CountryParameters.sol":{"content":"//SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

import "./CountryMinter.sol";
import "./Senate.sol";
import "./KeeperFile.sol";
import "./Wonders.sol";
import "./Treasury.sol";
import {VRFConsumerBaseV2Plus} from "@chainlink/contracts/src/v0.8/vrf/dev/VRFConsumerBaseV2Plus.sol";
import {VRFV2PlusClient} from "@chainlink/contracts/src/v0.8/vrf/dev/libraries/VRFV2PlusClient.sol";
import {IVRFCoordinatorV2Plus} from "@chainlink/contracts/src/v0.8/vrf/dev/interfaces/IVRFCoordinatorV2Plus.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

///@title CountryParametersContract
///@author OxSnosh
///@dev this contract will inferit from Chainlink VRF and OpenZeppelin Ownable
contract CountryParametersContract is
    VRFConsumerBaseV2Plus,
    ReentrancyGuard
{
    address public spyAddress;
    address public senateAddress;
    uint256[] private s_randomWords;
    address public countryMinter;
    address public keeper;
    address public nuke;
    address public groundBattle;
    address public wonders1;
    address public treasury;

    //chainlink variables
    // VRFConsumerBaseV2Plus public i_vrfCoordinator;
    uint256 private s_subscriptionId;
    bytes32 private s_gasLane;
    uint32 private s_callbackGasLimit;
    uint16 private s_confirmations = 3;
    uint32 private s_numWords = 2;
    bool private s_useNative = true;

    CountryMinter mint;
    SenateContract senate;
    KeeperContract keep;
    WondersContract1 won1;
    TreasuryContract tres;

    struct CountryParameters {
        uint256 id;
        string rulerName;
        string nationName;
        string capitalCity;
        string nationSlogan;
    }

    struct CountrySettings {
        uint256 dayCreated;
        uint256 nationTeam;
        uint256 governmentType;
        uint256 dayGovernmentChanged;
        uint256 nationalReligion;
        uint256 dayReligionChanged;
        uint256 dayOfAnarchy;
    }

    event RulerNameChanged(
        uint256 indexed countryId,
        string indexed newRulerName
    );

    event NationNameChanged(
        uint256 indexed countryId,
        string indexed newNationName
    );

    event CapitalCityChanged(
        uint256 indexed countryId,
        string indexed newCapitalCity
    );

    event NationSloganChanged(
        uint256 indexed countryId,
        string indexed newNationSlogan
    );

    event AllianceChanged(
        uint256 indexed countryId,
        string indexed newAlliance
    );

    event TeamChanged(uint256 indexed countryId, uint256 indexed newTeam);

    event GovernmentChanged(
        uint256 indexed countryId,
        uint256 indexed newGovernment
    );

    event ReligionChanged(
        uint256 indexed countryId,
        uint256 indexed newReligion
    );

    event AnarchyInflicted(uint256 indexed countryId);

    mapping(uint256 => CountryParameters) public idToCountryParameters;
    mapping(uint256 => CountrySettings) public idToCountrySettings;
    mapping(uint256 => uint256) s_requestIdToRequestIndex;
    mapping(uint256 => uint256[]) public s_requestIndexToRandomWords;
    mapping(uint256 => uint256) private idToReligionPreference;
    mapping(uint256 => uint256) private idToGovernmentPreference;

    modifier onlyNukeAndGroundBattle() {
        require(
            msg.sender == nuke || msg.sender == groundBattle,
            "function only callable from nuke or battle contract"
        );
        _;
    }

    ///@dev the consructor will inherit parameters required to initialize the chainlinh VRF functionality
    constructor(
        address vrfCoordinatorV2,
        uint256 subscriptionId,
        bytes32 gasLane,
        uint32 callbackGasLimit
    ) VRFConsumerBaseV2Plus(vrfCoordinatorV2) {
        s_vrfCoordinator = IVRFCoordinatorV2Plus(vrfCoordinatorV2);
        s_gasLane = gasLane;
        s_subscriptionId = subscriptionId;
        s_callbackGasLimit = callbackGasLimit;
    }

    function updateVRFCoordinator(
        address vrfCoordinatorV2
    ) public onlyOwner {
        s_vrfCoordinator = IVRFCoordinatorV2Plus(vrfCoordinatorV2);
    }

    function setVRFConfig(
        bytes32 _keyHash,
        uint64  _subId,
        uint16  _minConf,
        uint32  _gasLimit,
        uint32  _numWords,
        bool    _useNative
    ) external onlyOwner {
        s_gasLane                   = _keyHash;
        s_subscriptionId            = _subId;
        s_confirmations             = _minConf;
        s_callbackGasLimit          = _gasLimit;
        s_numWords                  = _numWords;
        s_useNative                 = _useNative;
        emit VrfConfigUpdated(_keyHash, _subId, _minConf, _gasLimit, _numWords, _useNative);
    }

    event VrfConfigUpdated(
        bytes32 keyHash,
        uint64 subId,
        uint16 minConf,
        uint32 gasLimit,
        uint32 numWords,
        bool useNative
    );

    ///@dev this function is only callable by the contract owner
    ///@dev this function will be called immediately after contract deployment in order to set contract pointers
    function settings(
        address _spy,
        address _countryMinter,
        address _senate,
        address _keeper,
        address _nuke,
        address _groundBattle,
        address _wonders1,
        address _treasury
    ) public onlyOwner {
        spyAddress = _spy;
        countryMinter = _countryMinter;
        mint = CountryMinter(_countryMinter);
        senateAddress = _senate;
        senate = SenateContract(_senate);
        keeper = _keeper;
        keep = KeeperContract(_keeper);
        nuke = _nuke;
        groundBattle = _groundBattle;
        wonders1 = _wonders1;
        won1 = WondersContract1(_wonders1);
        treasury = _treasury;
        tres = TreasuryContract(_treasury);
    }

    modifier onlySpyContract() {
        require(
            msg.sender == spyAddress,
            "function only callable from spy contract"
        );
        _;
    }

    modifier onlyCountryMinter() {
        require(
            msg.sender == countryMinter,
            "function only callable from the country minter contract"
        );
        _;
    }

    ///@dev this is a public function but only callable from the counry minter contract
    ///@notice this function will get called only when a nation is minted
    ///@param id this will be the nations ID that is passed in from the country minter contact
    ///@param rulerName name passed in from country minter contract when a nation is minted
    ///@param nationName passed in from the country minter contract when a nation is minted
    ///@param capitalCity passed in from the country minter contract when a nation is minted
    ///@param nationSlogan passed in from the country minter contract when a nation is minted
    function generateCountryParameters(
        uint256 id,
        string memory rulerName,
        string memory nationName,
        string memory capitalCity,
        string memory nationSlogan
    ) public onlyCountryMinter {
        require(idToCountryParameters[id].id == 0, "Already initialized");
        CountryParameters memory newCountryParameters = CountryParameters(
            id,
            rulerName,
            nationName,
            capitalCity,
            nationSlogan
        );
        uint256 day = keep.getGameDay();
        CountrySettings memory newCountrySettings = CountrySettings(
            day,
            0,
            0,
            0,
            0,
            0,
            0
        );
        idToCountryParameters[id] = newCountryParameters;
        idToCountrySettings[id] = newCountrySettings;
        fulfillRequest(id);
    }

    mapping(uint256 => bool) public pendingRequests;
    mapping(uint256 => uint256) public pendingRequestTimestamp;
    mapping(uint256 => uint256) private _activeReqId;
    uint256 public constant RETRY_TIMEOUT = 10 minutes;

    event VRFRequestSent(uint256 indexed id, uint256 indexed requestId);

    function fulfillRequest(uint256 id) internal {
        require(!pendingRequests[id], "Randomness already requested");

        uint256 requestId = s_vrfCoordinator.requestRandomWords(
            VRFV2PlusClient.RandomWordsRequest({
                keyHash: s_gasLane,
                subId: s_subscriptionId,
                requestConfirmations: s_confirmations,
                callbackGasLimit: s_callbackGasLimit,
                numWords: s_numWords,
                extraArgs: VRFV2PlusClient._argsToBytes(
                    VRFV2PlusClient.ExtraArgsV1({ nativePayment: s_useNative })
                )
            })
        );

        s_requestIdToRequestIndex[requestId] = id;
        _activeReqId[id] = requestId;                
        pendingRequests[id] = true;
        pendingRequestTimestamp[id] = block.timestamp;

        emit VRFRequestSent(id, requestId);
    }

    function retryFulfillRequest(uint256 id) public onlyOwner {
        require(pendingRequests[id], "No pending request");
        require(
            block.timestamp > pendingRequestTimestamp[id] + RETRY_TIMEOUT,
            "Retry not allowed yet"
        );

        uint256 requestId = s_vrfCoordinator.requestRandomWords(
            VRFV2PlusClient.RandomWordsRequest({
                keyHash: s_gasLane,
                subId: s_subscriptionId,
                requestConfirmations: s_confirmations,
                callbackGasLimit: s_callbackGasLimit,
                numWords: s_numWords,
                extraArgs: VRFV2PlusClient._argsToBytes(
                    VRFV2PlusClient.ExtraArgsV1({ nativePayment: s_useNative })
                )
            })
        );

        s_requestIdToRequestIndex[requestId] = id;
        _activeReqId[id] = requestId;                
        pendingRequestTimestamp[id] = block.timestamp;

        emit VRFRequestSent(id, requestId);
    }

    /// @dev Chainlink VRF callback. Ignores stale/duplicate callbacks created by retries.
    /// @param requestId  The VRF request id.
    /// @param randomWords  Random words returned by VRF (used to set religion/government).
    function fulfillRandomWords(
        uint256 requestId,
        uint256[] calldata randomWords
    ) internal override {
        uint256 id = s_requestIdToRequestIndex[requestId];

        if (!pendingRequests[id]) return;
        if (_activeReqId[id] != requestId) return;

        s_requestIndexToRandomWords[id] = randomWords;
        s_randomWords = randomWords;

        uint256 religionPreference = (randomWords[0] % 14) + 1;
        uint256 governmentPreference = (randomWords[1] % 9) + 1;

        idToReligionPreference[id] = religionPreference;
        idToGovernmentPreference[id] = governmentPreference;

        pendingRequests[id] = false;
        delete pendingRequestTimestamp[id];
        delete _activeReqId[id];
    }

    ///@dev this is public function that will allow a nation ruler to reset a nations name
    ///@notice use this function to reset a nations name
    ///@notice this function is only callable by the nation owner
    ///@param newNationName is the updated name for the nation ruler
    ///@param id is the nation ID for the update
    function setNationName(
        string memory newNationName,
        uint256 id
    ) public nonReentrant {
        bool isOwner = mint.checkOwnership(id, msg.sender);
        require(isOwner, "!nation owner");
        require(bytes(newNationName).length <= 64, "Nation Name too long");
        tres.spendBalance(id, 20000000 * (10 ** 18));
        idToCountryParameters[id].nationName = newNationName;
        emit NationNameChanged(id, newNationName);
    }

    ///@dev this is public function that will allow a nation ruler to reset a nations capital city name
    ///@notice use this function to reset a nations capital city name
    ///@notice this function is only callable by the nation owner
    ///@param newCapitalCity is the updated name for the nation ruler
    ///@param id is the nation ID for the update
    function setCapitalCity(string memory newCapitalCity, uint256 id) public {
        bool isOwner = mint.checkOwnership(id, msg.sender);
        require(isOwner, "!nation owner");
        require(bytes(newCapitalCity).length <= 64, "Capital Name too long");
        idToCountryParameters[id].capitalCity = newCapitalCity;
        emit CapitalCityChanged(id, newCapitalCity);
    }

    ///@dev this is public function that will allow a nation ruler to reset a nations slogan
    ///@notice use this function to reset a nations slogan
    ///@notice this function is only callable by the nation owner
    ///@param newNationSlogan is the updated name for the nation ruler
    ///@param id is the nation ID for the update
    function setNationSlogan(string memory newNationSlogan, uint256 id) public {
        bool isOwner = mint.checkOwnership(id, msg.sender);
        require(isOwner, "!nation owner");
        require(bytes(newNationSlogan).length <= 128, "Slogan too long");
        idToCountryParameters[id].nationSlogan = newNationSlogan;
        emit NationSloganChanged(id, newNationSlogan);
    }

    ///@dev this is public function that will allow a nation ruler to set a team membership for the nation
    ///@notice use this function to set a team membership for the nation
    ///@notice this function is only callable by the nation owner
    ///@notice there are only 9 teams in the game, each team has senators that can sanction nations on that team from trading and send sending aid to eachother
    ///@param newTeam is the updated name for the nation ruler
    ///@param id is the nation ID for the update
    function setTeam(uint256 id, uint256 newTeam) public {
        bool isOwner = mint.checkOwnership(id, msg.sender);
        require(isOwner, "!nation owner");
        require(newTeam <= 8, "invalid team selection");
        bool isSenator = senate.isSenator(id);
        require(isSenator == false, "cannot chenge teams as a senator");
        senate.updateTeam(id, newTeam);
        idToCountrySettings[id].nationTeam = newTeam;
        emit TeamChanged(id, newTeam);
    }

    ///@dev this is public function that will allow a nation ruler to chenge their government type
    ///@notice use this function to reset a nations government type
    ///@notice this function is only callable by the nation owner
    ///@notice there are 10 government types each with different advantages
    ///@param newType is the updated type of government
    /** @notice for newType:
     * 0 = Anarchy
     * 1 = Capitalism
     * 2 = Communist
     * 3 = Democracy
     * 4 = Dictatorship
     * 5 = Federal Government
     * 6 = Monarchy
     * 7 = Republic
     * 8 = Revolutionary
     * 9 = Totalitarian
     * 10 = Transitional
     ***/
    ///@param id is the nation ID for the update
    function setGovernment(uint256 id, uint256 newType) public {
        bool isOwner = mint.checkOwnership(id, msg.sender);
        require(isOwner, "!nation owner");
        uint256 gameDay = keep.getGameDay();
        uint256 anarchyDay = idToCountrySettings[id].dayOfAnarchy;
        bool falloutShelter = won1.getFalloutShelterSystem(id);
        if (falloutShelter) {
            require(
                (gameDay - anarchyDay) >= 4,
                "nation in anarchy, must wait 4 days"
            );
        } else {
            require(
                (gameDay - anarchyDay) >= 5,
                "nation in anarchy, must wait 5 days"
            );
        }
        uint256 dayOfChange = idToCountrySettings[id].dayGovernmentChanged;
        require(
            (gameDay - dayOfChange) >= 3,
            "need to wait 3 days before changing"
        );
        require(newType <= 10, "invalid type");
        require(newType > 0, "invalid type");
        idToCountrySettings[id].governmentType = newType;
        idToCountrySettings[id].dayGovernmentChanged = gameDay;
        emit GovernmentChanged(id, newType);
    }

    ///@dev this is a public function but it is only callable from the spy contract
    ///@notice this is the function that the spy contract calls when a successful spy attack updates your desired government
    ///@param id is the nation id of the updated desired government
    ///@param newType is the updated government type
    function updateDesiredGovernment(
        uint256 id,
        uint256 newType
    ) public onlySpyContract {
        idToGovernmentPreference[id] = newType;
    }

    ///@dev this is public function that will allow a nation ruler to chenge their religion type
    ///@notice use this function to reset a nations religion type
    ///@notice this function is only callable by the nation owner
    ///@notice there are 14 religion types
    ///@param newType is the updated name for the nation ruler
    ///@param id is the nation ID for the update
    function setReligion(uint256 id, uint256 newType) public {
        bool isOwner = mint.checkOwnership(id, msg.sender);
        require(isOwner, "!nation owner");
        uint256 gameDay = keep.getGameDay();
        uint256 dayOfChange = idToCountrySettings[id].dayReligionChanged;
        require(
            (gameDay - dayOfChange) >= 3,
            "need to wait 3 days before changing"
        );
        require(newType > 0, "invalid type");
        require(newType <= 14, "invalid type");
        idToCountrySettings[id].nationalReligion = newType;
        idToCountrySettings[id].dayReligionChanged = gameDay;
        emit ReligionChanged(id, newType);
    }

    ///@dev this is a public function but it is only callable from the spy contract
    ///@notice this is the function that the spy contract calls when a successful spy attack updates your desired religion
    ///@param id is the nation id of the updated desired religion
    ///@param newType is the updated religion type
    function updateDesiredReligion(
        uint256 id,
        uint256 newType
    ) public onlySpyContract {
        idToReligionPreference[id] = newType;
    }

    function inflictAnarchy(uint256 id) public onlyNukeAndGroundBattle {
        uint256 gameDay = keep.getGameDay();
        idToCountrySettings[id].governmentType = 0;
        idToCountrySettings[id].dayOfAnarchy = gameDay;
        emit AnarchyInflicted(id);
        emit GovernmentChanged(id, 0);
    }

    ///@dev this is a view funtion that will return the ruler name for a country
    ///@param countryId this is the ID for the nation being queried
    function getRulerName(
        uint256 countryId
    ) public view returns (string memory) {
        string memory ruler = idToCountryParameters[countryId].rulerName;
        return ruler;
    }

    ///@dev this is a view funtion that will return the nation name for a country
    ///@param countryId this is the ID for the nation being queried
    function getNationName(
        uint256 countryId
    ) public view returns (string memory) {
        string memory nationName = idToCountryParameters[countryId].nationName;
        return nationName;
    }

    ///@dev this is a view funtion that will return the capital city for a country
    ///@param countryId this is the ID for the nation being queried
    function getCapital(uint256 countryId) public view returns (string memory) {
        string memory capital = idToCountryParameters[countryId].capitalCity;
        return capital;
    }

    ///@dev this is a view funtion that will return the slogan for a country
    ///@param countryId this is the ID for the nation being queried
    function getSlogan(uint256 countryId) public view returns (string memory) {
        string memory slogan = idToCountryParameters[countryId].nationSlogan;
        return slogan;
    }

    ///@dev this is a view funtion that will return the team for a country
    ///@param countryId this is the ID for the nation being queried
    function getTeam(uint256 countryId) public view returns (uint256) {
        return idToCountrySettings[countryId].nationTeam;
    }

    ///@dev this is a view funtion that will return the goverment type for a country
    ///@param countryId this is the ID for the nation being queried
    function getGovernmentType(
        uint256 countryId
    ) public view returns (uint256) {
        return idToCountrySettings[countryId].governmentType;
    }

    ///@dev this is a view funtion that will return the religion type for a country
    ///@param countryId this is the ID for the nation being queried
    function getReligionType(uint256 countryId) public view returns (uint256) {
        return idToCountrySettings[countryId].nationalReligion;
    }

    ///@dev this is a view funtion that will return the time a nation was minted
    ///@param countryId this is the ID for the nation being queried
    function getDayCreated(uint256 countryId) public view returns (uint256) {
        return idToCountrySettings[countryId].dayCreated;
    }

    function setDesiredReligionAndGovernmentFromOwner(
        uint256 id,
        uint256 religion,
        uint256 government
    ) public onlyOwner {
        idToReligionPreference[id] = religion;
        idToGovernmentPreference[id] = government;
    }

    ///@dev this is a view funtion that will return the government preference for a country
    ///@param id this is the ID for the nation being queried
    function getGovernmentPreference(
        uint256 id
    ) public view returns (uint256 preference) {
        return idToGovernmentPreference[id];
    }

    ///@dev this is a view funtion that will return the religion preference for a country
    ///@param id this is the ID for the nation being queried
    function getReligionPreference(
        uint256 id
    ) public view returns (uint256 preference) {
        return idToReligionPreference[id];
    }

    ///@dev this is a view funtion that will return the days since a religion and government change for a nation
    ///@param id this is the ID for the nation being queried
    ///@return uint256 will return an array with [0] as the days since governemtn change and [1] as days since religion change
    function getDaysSince(uint256 id) public view returns (uint256, uint256) {
        uint256 gameDay = keep.getGameDay();
        uint256 dayOfGovChange = idToCountrySettings[id].dayGovernmentChanged;
        uint256 daysSinceGovChange = gameDay - dayOfGovChange;
        uint256 dayReligionChanged = idToCountrySettings[id].dayReligionChanged;
        uint256 daysSinceReligionChange = gameDay - dayReligionChanged;
        return (daysSinceGovChange, daysSinceReligionChange);
    }

    // Allows the contract to accept plain ETH transfers
    receive() external payable {}
}

/// @title AllianceManager
/// @author OxSnosh
/// @notice Manages alliances in the game with capped size and efficient member tracking.
contract AllianceManager is Ownable {
    uint256 public constant MAX_ALLIANCE_MEMBERS = 1000;
    uint256 public constant MAX_JOIN_REQUESTS = 1000;

    uint256 public allianceCounter;
    address public countryMinter;
    CountryMinter mint;

    struct Alliance {
        uint256 id;
        string name;
        uint256 founderNationId;
        mapping(uint256 => bool) admins;
        mapping(uint256 => bool) isMember;
        mapping(uint256 => uint256) memberIndex;
        mapping(uint256 => string) nationToPlatoon;
        uint256[] members;
        uint256[] joinRequests;
        mapping(uint256 => bool) isJoinRequested;
    }

    mapping(uint256 => Alliance) public alliances;
    mapping(uint256 => uint256) public nationToAlliance;
    mapping(uint256 => bool) public allianceExists;

    event AllianceCreated(
        uint256 indexed allianceId,
        string name,
        uint256 founderNationId
    );
    event AllianceAdminAdded(
        uint256 indexed allianceId,
        uint256 indexed adminNationId
    );
    event AllianceAdminRemoved(
        uint256 indexed allianceId,
        uint256 indexed adminNationId
    );
    event NationRequestedToJoin(
        uint256 indexed allianceId,
        uint256 indexed nationId
    );
    event NationApprovedToJoin(
        uint256 indexed allianceId,
        uint256 indexed nationId
    );
    event NationRemovedFromAlliance(
        uint256 indexed allianceId,
        uint256 indexed nationId
    );
    event NationAssignedToPlatoon(
        uint256 indexed allianceId,
        uint256 indexed nationId,
        string platoonId
    );
    event NationJoinRequestRejected(
        uint256 indexed allianceId,
        uint256 indexed nationId
    );

    function settings(address _countryMinter) public onlyOwner {
        countryMinter = _countryMinter;
        mint = CountryMinter(_countryMinter);
    }

    modifier onlyAllianceFounderOrAdmin(uint256 allianceId, uint256 nationId) {
        Alliance storage alliance = alliances[allianceId];
        require(
            nationId == alliance.founderNationId || alliance.admins[nationId],
            "Not authorized"
        );
        _;
    }

    function createAlliance(
        string memory name,
        uint256 founderNationId
    ) external {
        require(
            mint.checkOwnership(founderNationId, msg.sender),
            "Not the owner of the nation"
        );
        require(bytes(name).length <= 64, "Alliance name too long");
        require(
            nationToAlliance[founderNationId] == 0,
            "Nation already in an alliance"
        );

        allianceCounter++;
        uint256 id = allianceCounter;

        Alliance storage newAlliance = alliances[id];
        newAlliance.id = id;
        newAlliance.name = name;
        newAlliance.founderNationId = founderNationId;
        newAlliance.admins[founderNationId] = true;
        newAlliance.isMember[founderNationId] = true;
        newAlliance.memberIndex[founderNationId] = 0;
        newAlliance.members.push(founderNationId);

        nationToAlliance[founderNationId] = id;
        allianceExists[id] = true;

        emit AllianceCreated(id, name, founderNationId);
    }

    function addAdmin(
        uint256 allianceId,
        uint256 adminNationId,
        uint256 callerNationId
    ) external onlyAllianceFounderOrAdmin(allianceId, callerNationId) {
        require(
            mint.checkOwnership(callerNationId, msg.sender),
            "Not the owner of the nation"
        );
        require(
            alliances[allianceId].isMember[adminNationId],
            "Nominee must be a member of the alliance"
        );
        alliances[allianceId].admins[adminNationId] = true;
        emit AllianceAdminAdded(allianceId, adminNationId);
    }

    function removeAdmin(
        uint256 allianceId,
        uint256 adminNationId,
        uint256 callerNationId
    ) external onlyAllianceFounderOrAdmin(allianceId, callerNationId) {
        require(
            mint.checkOwnership(callerNationId, msg.sender),
            "Not the owner of the nation"
        );
        require(
            adminNationId != alliances[allianceId].founderNationId,
            "Cannot remove founder"
        );
        alliances[allianceId].admins[adminNationId] = false;
        emit AllianceAdminRemoved(allianceId, adminNationId);
    }

    function requestToJoinAlliance(
        uint256 allianceId,
        uint256 nationId
    ) external {
        require(
            mint.checkOwnership(nationId, msg.sender),
            "Not the owner of the nation"
        );
        Alliance storage alliance = alliances[allianceId];
        require(nationToAlliance[nationId] == 0, "Already in an alliance");
        require(
            alliance.joinRequests.length < MAX_JOIN_REQUESTS,
            "Join request limit reached"
        );
        require(!alliance.isJoinRequested[nationId], "Already requested");

        alliance.joinRequests.push(nationId);
        alliance.isJoinRequested[nationId] = true;

        emit NationRequestedToJoin(allianceId, nationId);
    }

    function approveNationJoin(
        uint256 allianceId,
        uint256 nationId,
        uint256 callerNationId
    ) external onlyAllianceFounderOrAdmin(allianceId, callerNationId) {
        require(
            mint.checkOwnership(callerNationId, msg.sender),
            "Not the owner of the nation"
        );

        Alliance storage alliance = alliances[allianceId];
        require(
            nationToAlliance[nationId] == 0,
            "Nation already in an alliance"
        );
        require(alliance.isJoinRequested[nationId], "No join request");
        require(
            alliance.members.length < MAX_ALLIANCE_MEMBERS,
            "Alliance full"
        );

        uint256[] storage requests = alliance.joinRequests;
        for (uint256 i = 0; i < requests.length; i++) {
            if (requests[i] == nationId) {
                requests[i] = requests[requests.length - 1];
                requests.pop();
                break;
            }
        }
        delete alliance.isJoinRequested[nationId];

        alliance.memberIndex[nationId] = alliance.members.length;
        alliance.members.push(nationId);
        alliance.isMember[nationId] = true;
        nationToAlliance[nationId] = allianceId;

        emit NationApprovedToJoin(allianceId, nationId);
    }

    function rejectJoinRequest(
        uint256 allianceId,
        uint256 nationId,
        uint256 callerNationId
    ) external onlyAllianceFounderOrAdmin(allianceId, callerNationId) {
        require(
            mint.checkOwnership(callerNationId, msg.sender),
            "Not the owner of the nation"
        );

        Alliance storage alliance = alliances[allianceId];
        require(
            alliance.isJoinRequested[nationId],
            "Nation did not request to join"
        );

        uint256[] storage requests = alliance.joinRequests;
        for (uint256 i = 0; i < requests.length; i++) {
            if (requests[i] == nationId) {
                requests[i] = requests[requests.length - 1];
                requests.pop();
                break;
            }
        }
        delete alliance.isJoinRequested[nationId];

        emit NationJoinRequestRejected(allianceId, nationId);
    }

    function removeNationFromAlliance(
        uint256 allianceId,
        uint256 nationId,
        uint256 callerNationId
    ) external onlyAllianceFounderOrAdmin(allianceId, callerNationId) {
        require(
            mint.checkOwnership(callerNationId, msg.sender),
            "Not the owner of the nation"
        );
        Alliance storage alliance = alliances[allianceId];
        require(
            nationToAlliance[nationId] == allianceId,
            "Nation not in this alliance"
        );

        uint256 index = alliance.memberIndex[nationId];
        uint256 lastMemberId = alliance.members[alliance.members.length - 1];
        alliance.members[index] = lastMemberId;
        alliance.memberIndex[lastMemberId] = index;
        alliance.members.pop();

        delete nationToAlliance[nationId];
        delete alliance.isMember[nationId];
        delete alliance.admins[nationId];
        delete alliance.nationToPlatoon[nationId];
        delete alliance.memberIndex[nationId];

        emit NationRemovedFromAlliance(allianceId, nationId);
    }

    function leaveAlliance(uint256 nationId) external {
        require(
            mint.checkOwnership(nationId, msg.sender),
            "Not the owner of the nation"
        );
        uint256 allianceId = nationToAlliance[nationId];
        require(allianceId != 0, "Nation not in any alliance");
        Alliance storage alliance = alliances[allianceId];
        require(alliance.isMember[nationId], "Not a member");

        if (alliance.admins[nationId]) {
            bool hasOtherAdmin = false;
            for (uint256 i = 0; i < alliance.members.length; i++) {
                uint256 memberId = alliance.members[i];
                if (memberId != nationId && alliance.admins[memberId]) {
                    hasOtherAdmin = true;
                    break;
                }
            }
            require(hasOtherAdmin, "No other admin in alliance");
        }

        require(
            alliance.founderNationId != nationId,
            "Transfer founder role before leaving"
        );

        uint256 index = alliance.memberIndex[nationId];
        uint256 lastMemberId = alliance.members[alliance.members.length - 1];
        alliance.members[index] = lastMemberId;
        alliance.memberIndex[lastMemberId] = index;
        alliance.members.pop();

        delete alliance.admins[nationId];
        delete alliance.isMember[nationId];
        delete alliance.memberIndex[nationId];
        delete alliance.nationToPlatoon[nationId];
        delete nationToAlliance[nationId];

        emit NationRemovedFromAlliance(allianceId, nationId);
    }

    function assignNationToPlatoon(
        uint256 allianceId,
        uint256 nationId,
        string memory platoonId,
        uint256 callerNationId
    ) external onlyAllianceFounderOrAdmin(allianceId, callerNationId) {
        require(
            mint.checkOwnership(callerNationId, msg.sender),
            "Not the owner of the nation"
        );
        require(
            nationToAlliance[nationId] == allianceId,
            "Nation not in this alliance"
        );

        alliances[allianceId].nationToPlatoon[nationId] = platoonId;
        emit NationAssignedToPlatoon(allianceId, nationId, platoonId);
    }

    function transferFounder(
        uint256 allianceId,
        uint256 newFounderNationId,
        uint256 callerNationId
    ) external {
        require(
            mint.checkOwnership(callerNationId, msg.sender),
            "Not the owner of the nation"
        );
        Alliance storage alliance = alliances[allianceId];
        require(
            alliance.founderNationId == callerNationId,
            "Only founder can transfer ownership"
        );
        require(
            alliance.isMember[newFounderNationId],
            "New founder must be a member"
        );

        alliance.founderNationId = newFounderNationId;
    }

    function deleteAlliance(
        uint256 allianceId,
        uint256 callerNationId
    ) external {
        require(
            mint.checkOwnership(callerNationId, msg.sender),
            "Not the owner of the nation"
        );
        Alliance storage alliance = alliances[allianceId];
        require(
            alliance.founderNationId == callerNationId,
            "Only founder can delete alliance"
        );
        require(
            alliance.members.length == 1 &&
                alliance.members[0] == callerNationId,
            "Must be sole member"
        );

        delete nationToAlliance[callerNationId];
        delete alliance.admins[callerNationId];
        delete alliance.isMember[callerNationId];
        delete alliance.nationToPlatoon[callerNationId];

        delete alliances[allianceId];
        delete allianceExists[allianceId];
    }

    function getAllianceMembers(
        uint256 allianceId
    ) external view returns (uint256[] memory) {
        return alliances[allianceId].members;
    }

    function getJoinRequests(
        uint256 allianceId
    ) external view returns (uint256[] memory) {
        return alliances[allianceId].joinRequests;
    }

    function getNationAlliance(
        uint256 nationId
    ) external view returns (uint256) {
        return nationToAlliance[nationId];
    }

    function getNationAllianceAndPlatoon(
        uint256 nationId
    ) external view returns (uint256, string memory, string memory) {
        uint256 alliance = nationToAlliance[nationId];
        string memory platoon = alliance > 0
            ? alliances[alliance].nationToPlatoon[nationId]
            : "";
        string memory allianceName = alliance > 0
            ? alliances[alliance].name
            : "";
        return (alliance, platoon, allianceName);
    }

    function isNationAllianceAdmin(
        uint256 allianceId,
        uint256 nationId
    ) external view returns (bool) {
        return alliances[allianceId].admins[nationId];
    }

    function isMemberOfAlliance(
        uint256 allianceId,
        uint256 nationId
    ) public view returns (bool) {
        return alliances[allianceId].isMember[nationId];
    }
}
","keccak256":"0xb7e0cec5f2453ffd6b284bbfdf603b28adfb6c8deb6267dc679616ba90737f20","license":"BUSL-1.1"},"contracts/Crime.sol":{"content":"//SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

import "./Infrastructure.sol";
import "./Improvements.sol";
import "./CountryParameters.sol";
import "./Wonders.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "hardhat/console.sol";

///@title CrimeContract
///@author OxSnosh
///@notice this contract will calculate the number of criminals in a nations population
contract CrimeContract is Ownable {
    address public infrastructure;
    address public improvements1;
    address public improvements2;
    address public improvements3;
    address public improvements4;
    address public parameters;
    address public wonders2;

    InfrastructureContract inf;
    ImprovementsContract1 imp1;
    ImprovementsContract2 imp2;
    ImprovementsContract3 imp3;
    ImprovementsContract4 imp4;
    CountryParametersContract cp;
    WondersContract2 won2;

    ///@dev this function is only callable by the contract owner
    ///@dev this function will be called immediately after contract deployment in order to set contract pointers
    function settings(
        address _infrastructure,
        address _improvements1,
        address _improvements2,
        address _improvements3,
        address _improvements4,
        address _parameters,
        address _wonders2
    ) public onlyOwner {
        infrastructure = _infrastructure;
        inf = InfrastructureContract(_infrastructure);
        improvements1 = _improvements1;
        imp1 = ImprovementsContract1(_improvements1);
        improvements2 = _improvements2;
        imp2 = ImprovementsContract2(_improvements2);
        improvements3 = _improvements3;
        imp3 = ImprovementsContract3(_improvements3);
        parameters = _parameters;
        improvements4 = _improvements4;
        imp4 = ImprovementsContract4(_improvements4);
        cp = CountryParametersContract(payable(_parameters));
        wonders2 = _wonders2;
        won2 = WondersContract2(_wonders2);
    }

    ///@dev this is a public view function that will calculate the number of criminals in a nations population
    ///@notice this will calulate the number of criminals in a nations population
    ///@notice criminals will reduce the amount of your tax paying citizens
    ///@notice you will also lose population happiness as your criminal population increases
    ///@notice jails, labor camps, border walls and prisons will reduce the number of criminals in your nation
    ///@param id this is the nation ID for the nation being queried
    ///@return uint256 this function will return the number of criminals in your population
    function getCriminalCount(
        uint256 id
    ) public view returns (uint256, uint256, uint256) {
        uint256 totalPopulation = inf.getTotalPopulationCount(id);
        uint256 crimeIndex = getCrimeIndex(id);
        uint256 criminalPercentage = (crimeIndex + 1);
        uint256 baseCriminalCount = ((totalPopulation * criminalPercentage) /
            100);
        uint256 rehabs = imp3.getRehabilitationFacilityCount(id);
        uint256 rehabilitatedCitizens = (rehabs * 1000);
        if (baseCriminalCount <= rehabilitatedCitizens) {
            rehabilitatedCitizens = baseCriminalCount;
            baseCriminalCount = 0;
        } else {
            baseCriminalCount = baseCriminalCount - rehabilitatedCitizens;
        }
        (uint256 criminalCount, uint256 incarcerated) = incarcerateCriminals(baseCriminalCount, id);
        return (criminalCount, rehabilitatedCitizens, incarcerated);
    }

    function incarcerateCriminals(
        uint256 baseCriminalCount,
        uint256 countryId
    ) public view returns (uint256, uint256) {
        uint256 jailCount = imp2.getJailCount(countryId);
        uint256 laborCamps = imp2.getLaborCampCount(countryId);
        uint256 prisons = imp3.getPrisonCount(countryId);
        uint256 incarceratedCriminals = 0;
        uint256 roomForIncarceration = ((jailCount * 500) +
            (laborCamps * 200) +
            (prisons * 5000));
        if (baseCriminalCount <= roomForIncarceration) {
            incarceratedCriminals = baseCriminalCount;
            baseCriminalCount = 0;
        } else {
            baseCriminalCount = baseCriminalCount - roomForIncarceration;
            incarceratedCriminals = roomForIncarceration;
        }
        uint256 criminalCountPercentageModifier = 100;
        uint256 borderWalls = imp1.getBorderWallCount(countryId);
        if (borderWalls > 0) {
            criminalCountPercentageModifier -= borderWalls;
        }
        uint256 criminalCount = ((baseCriminalCount *
            criminalCountPercentageModifier) / 100);
        return (criminalCount, incarceratedCriminals);
    }

    ///@dev this function will take your nation's crime prevention score and return a crime index
    ///@notice the higher your crime prevention score the lower your crime index
    ///@notice the higher your crime index the more criminals you will have in your population
    ///@param id this is the nation ID of the country being queried
    ///@return uint256 this is crime index of the nation
    ///@notice the percentage of your population that is criminals = crime index +1
    function getCrimeIndex(uint256 id) public view returns (uint256) {
        uint256 cps = getCrimePreventionScore(id);
        uint256 crimeIndex;
        if (cps < 200) {
            crimeIndex = 6;
        } else if (cps < 250) {
            crimeIndex = 5;
        } else if (cps < 300) {
            crimeIndex = 4;
        } else if (cps < 350) {
            crimeIndex = 3;
        } else if (cps < 400) {
            crimeIndex = 2;
        } else if (cps < 500) {
            crimeIndex = 1;
        } else {
            crimeIndex = 0;
        }
        return crimeIndex;
    }

    ///@dev this is a public view function that calculates a nations crime prevention score
    ///@notice this function calculates crime prevention score
    ///@notice the higher the CPS the lower the number of criminals in your population
    ///@param id is the nation ID of the nation being queried
    ///@return uint256 is the crime prevention score being returned by the function
    function getCrimePreventionScore(uint256 id) public view returns (uint256) {
        uint256 litPoints = getLiteracyPoints(id);
        uint256 improvementPoints = getImprovementPoints(id);
        uint256 taxRatePoints = getTaxRateCrimeMultiplier(id);
        uint256 governmentPoints = getPointsFromGovernmentType(id);
        uint256 getPointsFromInfrastructure = getPointsFromInfrastruture(id);
        uint256 populationPoints = getPointsFromPopulation(id);
        uint256 cps = (litPoints +
            improvementPoints +
            taxRatePoints +
            governmentPoints +
            getPointsFromInfrastructure +
            populationPoints);
        return cps;
    }

    ///@dev this is a public view function that returns the literace rate of the nation
    ///@notice this function will return a nations literacy rate
    ///@param id is the nation ID of the country being queried
    ///@return uint256 is the literacy rate of the nation
    ///@notice literacy is increased by the amount of technology of a nation as well as the amount of schools and universities
    ///@notice increased literacy will increase crime prevention score
    function getLiteracy(uint256 id) public view returns (uint256) {
        uint256 tech = inf.getTechnologyCount(id);
        uint256 litBeforeModifiers;
        if (tech <= 50) {
            litBeforeModifiers = 20;
        } else {
            uint256 addedLiteracy = ((tech - 50) / 3);
            litBeforeModifiers = (20 + addedLiteracy);
        }
        uint256 schoolPoints = imp3.getSchoolCount(id);
        uint256 universities = imp3.getUniversityCount(id);
        uint256 universityPoints = (universities * 3);
        bool greatUniversity = won2.getGreatUniversity(id);
        if (greatUniversity == true) {
            universityPoints += 10;
        }
        uint256 literacy = (litBeforeModifiers +
            schoolPoints +
            universityPoints);
        if (literacy > 100) {
            literacy = 100;
        }
        return literacy;
    }

    ///@dev this function is a public view function that will calculate the amount of points that a nations literace rate will add to the crime prevention score
    ///@notice literacy will increse the crime prevention score of a nation
    ///@notice crime prevention score points added will be 80% of literacy (max of 80 points added)
    ///@param id is the nation ID of the nation being queried
    ///@return uint256 is the amount of points being added to the crime prevention score
    function getLiteracyPoints(uint256 id) public view returns (uint256) {
        uint256 literacyPercentage = getLiteracy(id);
        uint256 litPoints = ((literacyPercentage * 80) / 100);
        return litPoints;
    }

    ///@dev this is a publci view function that will calculate the amount of crime prevention score points from a nations improvements and tax rate
    ///@notice schools, universities, polive headquarters, casinos and red light districts all affect a nations crime prevention score
    ///@notice a nations tax rate will change the magnitude of these improvements affect on crime prevention score
    ///@notice the higher a tax rate the lower the crime prevention score will be and the more criminals a population will have
    ///@param id this is the nation ID of the nation being queried
    ///@return uint256 is the number of points added to crime prevention score from imrpovements and tax rate
    function getImprovementPoints(uint256 id) public view returns (uint256) {
        uint256 schools = imp3.getSchoolCount(id);
        uint256 universities = imp3.getUniversityCount(id);
        uint256 policeHqs = imp4.getPoliceHeadquartersCount(id);
        uint256 casinoCount = imp1.getCasinoCount(id);
        uint256 redLightDistricts = imp3.getRedLightDistrictCount(id);
        uint256 schoolPoints = (schools * 3);
        uint256 universityPoints = (universities * 10);
        uint256 policeHqPoints = (policeHqs * 2);
        uint256 casinoPoints = (casinoCount * 2);
        uint256 redLightDistrictPoints = (redLightDistricts * 2);
        uint256 improvementPoints = (8 +
            schoolPoints +
            universityPoints +
            policeHqPoints -
            casinoPoints -
            redLightDistrictPoints);
        return improvementPoints;
    }

    ///@dev this a public view function that will return the multiplier used to adjust the affect that a nations tax rate will have on crime prevention score
    ///@notice the higher a nations tax rate the lower the multiplier will be and the lower the crime prevention score will be
    ///@param id is the nation ID of the nation being queried
    ///@return uint256 is the munliplier used to adjust the points added to crime prevention score from taxes and improvements
    function getTaxRateCrimeMultiplier(
        uint256 id
    ) public view returns (uint256) {
        uint256 taxRate = inf.getTaxRate(id);
        uint256 taxRateCrimeMultiplier;
        if (taxRate <= 15) {
            taxRateCrimeMultiplier = 35;
        } else if (taxRate == 16) {
            taxRateCrimeMultiplier = 34;
        } else if (taxRate == 17) {
            taxRateCrimeMultiplier = 33;
        } else if (taxRate == 18) {
            taxRateCrimeMultiplier = 32;
        } else if (taxRate == 19) {
            taxRateCrimeMultiplier = 31;
        } else if (taxRate == 20) {
            taxRateCrimeMultiplier = 30;
        } else if (taxRate == 21) {
            taxRateCrimeMultiplier = 29;
        } else if (taxRate == 22) {
            taxRateCrimeMultiplier = 28;
        } else if (taxRate == 23) {
            taxRateCrimeMultiplier = 27;
        } else if (taxRate == 24) {
            taxRateCrimeMultiplier = 26;
        } else if (taxRate == 25) {
            taxRateCrimeMultiplier = 25;
        } else if (taxRate == 26) {
            taxRateCrimeMultiplier = 24;
        } else if (taxRate == 27) {
            taxRateCrimeMultiplier = 23;
        } else if (taxRate == 28) {
            taxRateCrimeMultiplier = 22;
        } else if (taxRate == 29) {
            taxRateCrimeMultiplier = 21;
        } else if (taxRate == 30) {
            taxRateCrimeMultiplier = 20;
        }
        uint256 taxMultiplier = (taxRateCrimeMultiplier);
        return taxMultiplier;
    }

    ///@dev this is a public view function that will add points to crime prevention score based on government type
    ///@notice different governmet types will affect a nations crime prevenetion score differently
    ///@param id is the nation ID of the nation being queried
    ///@return uint256 is the points added to crime prevention score from governemtn type
    function getPointsFromGovernmentType(
        uint256 id
    ) public view returns (uint256) {
        uint256 governmentPoints;
        uint256 gov = cp.getGovernmentType(id);
        if (gov == 0) {
            /** Anarchy */
            governmentPoints = 50;
        } else if (gov == 1) {
            /** Capitalist */
            governmentPoints = 110;
        } else if (gov == 2) {
            /** Communist */
            governmentPoints = 150;
        } else if (gov == 3) {
            /** Democracy */
            governmentPoints = 120;
        } else if (gov == 4) {
            /** Dictatorship */
            governmentPoints = 175;
        } else if (gov == 5) {
            /** Federal Goverment */
            governmentPoints = 160;
        } else if (gov == 6) {
            /** Monarchy */
            governmentPoints = 140;
        } else if (gov == 7) {
            /** Republic */
            governmentPoints = 165;
        } else if (gov == 8) {
            /** Revolutuionary */
            governmentPoints = 150;
        } else if (gov == 9) {
            /** Totalitarian State */
            governmentPoints = 190;
        } else {
            /** Transitional */
            governmentPoints = 100;
        }
        return governmentPoints;
    }

    ///@dev this is a public view function that will return the crime prevention score points from infrastructure
    ///@notice more infrastructure will increase crime prevention score reducing criminals
    ///@param id is the nation ID for the countrtry being queried
    ///@return uint256 is the points added to crime prevention score from infrastructure
    function getPointsFromInfrastruture(
        uint256 id
    ) public view returns (uint256) {
        uint256 infra = inf.getInfrastructureCount(id);
        uint256 infraPoints = (infra / 400);
        return infraPoints;
    }

    ///@dev this is a public view function that will add points to crime prevention score based on population
    ///@notice increased population will reduce crime prevention score
    ///@param id is the nation id of the nation being queries
    ///@return uint256 is the amount of points being added to crime prevention score
    function getPointsFromPopulation(uint256 id) public view returns (uint256) {
        uint256 population = inf.getTotalPopulationCount(id);
        uint256 populationPointsDeduction = (population / 250);
        if (populationPointsDeduction >= 350) {
            populationPointsDeduction = 350;
        }
        uint256 populationPoints = (350 - populationPointsDeduction);
        return populationPoints;
    }
}
","keccak256":"0x971fde64b4b718cc4022f35ee2c43c420990aa06c2f1f6302154b0302c8c66e6","license":"BUSL-1.1"},"contracts/Environment.sol":{"content":"//SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

import "./CountryMinter.sol";
import "./Resources.sol";
import "./Infrastructure.sol";
import "./Improvements.sol";
import "./Wonders.sol";
import "./Forces.sol";
import "./Taxes.sol";
import "./Nuke.sol";
import "./CountryParameters.sol";
import "./Missiles.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

///@title EnvironmentContract
///@author OxSnosh
///@notice this contract will calculate the environment score for a nation
///@dev this contact inherits from OpenZeppelin ownable 
contract EnvironmentContract is Ownable {
    address public countryMinter;
    address public resources;
    address public infrastructure;
    address public improvements1;
    address public improvements3;
    address public improvements4;
    address public wonders3;
    address public wonders4;
    address public forces;
    address public parameters;
    address public additionalTaxes;
    address public missiles;
    address public nukes;
    address public bonusResources;

    CountryMinter mint;
    ResourcesContract res;
    InfrastructureContract inf;
    ImprovementsContract1 imp1;
    ImprovementsContract3 imp3;
    ImprovementsContract4 imp4;
    WondersContract3 won3;
    WondersContract4 won4;
    ForcesContract force;
    CountryParametersContract param;
    AdditionalTaxesContract addTax;
    MissilesContract mis;
    NukeContract nuke;
    BonusResourcesContract bonus;

    ///@dev this function is only callable by the contract owner
    ///@dev this function will be called immediately after contract deployment in order to set contract pointers 
    function settings (
        address _countryMinter,
        address _resources,
        address _infrastructure,
        address _wonders3,
        address _wonders4,
        address _forces,
        address _parameters,
        address _additionalTaxes,
        address _missiles,
        address _nukes
    ) public onlyOwner {
        countryMinter = _countryMinter;
        mint = CountryMinter(_countryMinter);
        resources = _resources;
        res = ResourcesContract(payable(_resources));
        infrastructure = _infrastructure;
        inf = InfrastructureContract(_infrastructure);
        wonders3 = _wonders3;
        won3 = WondersContract3(_wonders3);
        wonders4 = _wonders4;
        won4 = WondersContract4(_wonders4);
        forces = _forces;
        force = ForcesContract(_forces);
        parameters = _parameters;
        param = CountryParametersContract(payable(_parameters));
        additionalTaxes = _additionalTaxes;
        addTax = AdditionalTaxesContract(_additionalTaxes);
        missiles = _missiles;
        mis = MissilesContract(_missiles);
        nukes = _nukes;
        nuke = NukeContract(_nukes);
    }

    ///@dev this function is only callable by the contract owner
    ///@dev this function will be called immediately after contract deployment in order to set contract pointers
    function settings2 (
        address _improvements1,
        address _improvements3,
        address _improvements4,
        address _bonusResources
    ) public onlyOwner {
        improvements1 = _improvements1;
        imp1 = ImprovementsContract1(_improvements1);
        improvements3 = _improvements3;
        imp3 = ImprovementsContract3(_improvements3);
        improvements4 = _improvements4;
        imp4 = ImprovementsContract4(_improvements4);
        bonusResources = _bonusResources;
        bonus = BonusResourcesContract(_bonusResources);
    }

    ///@dev this is a public view function that will return the environment score for a nation
    ///@notice a higher environment score will decrease a nations happiness and population
    ///@param id is the nation ID of the nation being queried
    ///@return uint256 is the environement score for the nation (environment scores are 0 - 10)
    function getEnvironmentScore(uint256 id) public view returns (uint256) {
        uint256 environmentScore;
        int256 grossScore = getGrossEnvironmentScore(id);
        uint256 globalRadiation = nuke.getGlobalRadiation();
        if (globalRadiation > 5) {
            globalRadiation = 5;
        }
        uint256 radiationContainmentChambers = imp3
            .getRadiationContainmentChamberCount(id);
        if (radiationContainmentChambers >= globalRadiation) {
            globalRadiation = 0;
        } else {
            globalRadiation -= radiationContainmentChambers;
        }
        bool radiationCleanup = bonus.viewRadiationCleanup(id);
        if (radiationCleanup) {
            globalRadiation = (globalRadiation / 2);
        }
        if (grossScore <= 0) {
            environmentScore = 0;
        } else if (grossScore <= 10) {
            environmentScore = 1;
        } else if (grossScore <= 20) {
            environmentScore = 2;
        } else if (grossScore <= 30) {
            environmentScore = 3;
        } else if (grossScore <= 40) {
            environmentScore = 4;
        } else if (grossScore <= 50) {
            environmentScore = 5;
        } else if (grossScore <= 60) {
            environmentScore = 6;
        } else if (grossScore <= 70) {
            environmentScore = 7;
        } else if (grossScore <= 80) {
            environmentScore = 8;
        } else if (grossScore <= 90) {
            environmentScore = 9;
        } else if (grossScore > 90) {
            environmentScore = 10;
        }
        if ((globalRadiation + environmentScore) > 10) {
            environmentScore = 10;
        } else {
            environmentScore += globalRadiation;
        }
        return environmentScore;
    }

    ///@dev this is a public view function that will generate the gross environment score
    ///@notice the gross environment score generated here will be converted into the environment score
    ///@notice every 10 points of gross score is equal to one point of environement
    ///@param id this is the nation ID being queried
    ///@return int256 this is the gross environment score
    function getGrossEnvironmentScore(uint256 id) public view returns (int256) {
        int256 scoreFromResources = getEnvironmentScoreFromResources(id);
        int256 scoreFromImprovementsAndWonders = getEnvironmentScoreFromImprovementsAndWonders(
                id
            );
        int256 scoreFromTech = getEnvironmentScoreFromTech(id);
        int256 scoreFromMilitaryRatio = getEnvironmentScoreFromMilitaryDensity(
            id
        );
        int256 scoreFromInfrastructure = getEnvironmentScoreFromInfrastructure(
            id
        );
        int256 scoreFromNukes = getScoreFromNukes(id);
        int256 scoreFromGovernment = getScoreFromGovernment(id);
        int256 grossEnvironmentScore = scoreFromResources +
            scoreFromImprovementsAndWonders +
            scoreFromTech +
            scoreFromMilitaryRatio +
            scoreFromInfrastructure +
            scoreFromNukes +
            scoreFromGovernment;
        return grossEnvironmentScore;
    }

    ///@dev this is a public view function that will generate environment points from resources
    ///@notice coal, oil, iron, uranium, water and radiation cleanup resources will all affect environemnt
    ///@param id is the nation ID of the nation being queried
    ///@return int256 is gross environment points from resources
    function getEnvironmentScoreFromResources(uint256 id)
        public
        view
        returns (int256)
    {
        int256 pointsFromResources;
        (,, bool isCoal,,,,,,,,) = res.getResources1(id);
        (bool isOil,,,,,, bool isUranium, bool isWater,,) = res.getResources2(id);
        bool isRadiationCleanup = bonus.viewRadiationCleanup(id);
        bool nationalEnvironmentalOffice = won3.getNationalEnvironmentOffice(id);
        if (isCoal && !nationalEnvironmentalOffice) {
            pointsFromResources += 10;
        }
        if (isOil && !nationalEnvironmentalOffice) {
            pointsFromResources += 10;
        }
        if (isUranium && !nationalEnvironmentalOffice) {
            pointsFromResources += 10;
        }
        if (isWater) {
            pointsFromResources -= 10;
        }
        if (isRadiationCleanup) {
            pointsFromResources -= 10;
        }
        return pointsFromResources;
    }

    ///@dev this is a public view function that will generate gross environment score from improvements and wonders
    ///@notice this function will return gross environment points from improvements and wonders
    ///@notice border walls, munitions factories and red light districts affect a nations environment score
    ///@param id this is the nation ID of the nation being queried
    ///@return int256 is the gross environment points from improvements and wonders
    function getEnvironmentScoreFromImprovementsAndWonders(uint256 id)
        public
        view
        returns (int256)
    {
        uint256 borderWallCount = imp1.getBorderWallCount(id);
        uint256 munitionsFactories = imp4.getMunitionsFactoryCount(id);
        uint256 redLightDistricts = imp3.getRedLightDistrictCount(id);
        bool isNationalEnvironmentOffice = won3.getNationalEnvironmentOffice(
            id
        );
        bool isWeaponsResearchCenter = won4.getWeaponsResearchCenter(id);
        int256 pointsFromWondersAndImprovements;
        if (borderWallCount == 0) {
            pointsFromWondersAndImprovements += 0;
        } else if (borderWallCount == 1) {
            pointsFromWondersAndImprovements -= 10;
        } else if (borderWallCount == 2) {
            pointsFromWondersAndImprovements -= 20;
        } else if (borderWallCount == 3) {
            pointsFromWondersAndImprovements -= 30;
        } else if (borderWallCount == 4) {
            pointsFromWondersAndImprovements -= 40;
        } else if (borderWallCount == 5) {
            pointsFromWondersAndImprovements -= 50;
        }
        if (munitionsFactories == 0) {
            pointsFromWondersAndImprovements += 0;
        } else if (munitionsFactories == 1) {
            pointsFromWondersAndImprovements += 3;
        } else if (munitionsFactories == 2) {
            pointsFromWondersAndImprovements += 6;
        } else if (munitionsFactories == 3) {
            pointsFromWondersAndImprovements += 9;
        } else if (munitionsFactories == 4) {
            pointsFromWondersAndImprovements += 12;
        } else if (munitionsFactories == 5) {
            pointsFromWondersAndImprovements += 15;
        }
        if (redLightDistricts == 0) {
            pointsFromWondersAndImprovements += 0;
        } else if (redLightDistricts == 1) {
            pointsFromWondersAndImprovements += 5;
        } else if (redLightDistricts == 2) {
            pointsFromWondersAndImprovements += 10;
        }
        if (isNationalEnvironmentOffice) {
            pointsFromWondersAndImprovements -= 10;
        }
        if (isWeaponsResearchCenter) {
            pointsFromWondersAndImprovements += 10;
        }
        return pointsFromWondersAndImprovements;
    }

    ///@dev this is a public view function that will generate gross environment score from a nations technology level
    ///@notice this function will return gross environment points from a nations technology level
    ///@notice a nations environment score will be penalized if the tech level is less than 6
    ///@param id this is the nation ID of the nation being queried
    ///@return int256 is the gross environment points from a nations technology level
    function getEnvironmentScoreFromTech(uint256 id)
        public
        view
        returns (int256)
    {
        uint256 techCount = inf.getTechnologyCount(id);
        int256 pointsFromTech;
        if (techCount >= 6) {
            pointsFromTech = -10;
        }
        return pointsFromTech;
    }

    ///@dev this is a public view function that will generate gross environment score from a nations soldier to population ratio
    ///@notice this function will return gross environment points from a nations soldier to population ratio
    ///@notice a soldier to population ratio greater than 60% will result in an environmental penalty
    ///@param id this is the nation ID of the nation being queried
    ///@return int256 is the gross environment points from a nations soldier to population ratio   
    function getEnvironmentScoreFromMilitaryDensity(uint256 id)
        public
        view
        returns (int256)
    {
        int256 pointsFromMilitaryRatio;
        ( , bool environmentPenalty, ) = addTax.soldierToPopulationRatio(id);
        if (environmentPenalty) {
            pointsFromMilitaryRatio += 10;
        }
        return pointsFromMilitaryRatio;
    }

    ///@dev this is a public view function that will generate gross environment score from a nations infrastructure to land ratio
    ///@notice this function will return gross environment points from a nations infrastructure to land ratio
    ///@notice a infrastructure to land ratio greater than 2:1 will result in an environmental penalty
    ///@param id this is the nation ID of the nation being queried
    ///@return int256 is the gross environment points from a nations infrastructure to land ratio  
    function getEnvironmentScoreFromInfrastructure(uint256 id)
        public
        view
        returns (int256)
    {
        int256 pointsFromInfrastructure;
        uint256 area = inf.getAreaOfInfluence(id);
        uint256 infra = inf.getInfrastructureCount(id);
        if ((infra / 2) >= area) {
            pointsFromInfrastructure += 10;
        }
        return pointsFromInfrastructure;
    }

    ///@dev this is a public view function that will generate gross environment score from a nations nuke count
    ///@notice this function will return gross environment points from a nations nuke count
    ///@notice a nations environment score will go up 1 point every ten nukes owned
    ///@param id this is the nation ID of the nation being queried
    ///@return int256 is the gross environment points from a nations nuke count
    function getScoreFromNukes(uint256 id) public view returns (int256) {
        int256 pointsFromNukes;
        uint256 nukeCount = mis.getNukeCount(id);
        if (nukeCount > 0) {
            pointsFromNukes = (int256(nukeCount));
        }
        (,,,,,,,, bool isLead,,) = res.getResources1(id);
        if (isLead) {
            pointsFromNukes = (pointsFromNukes / 2);
        }
        return pointsFromNukes;
    }

    ///@dev this is a public view function that will generate gross environment score from a nations government type
    ///@notice this function will return gross environment points from a nations government type
    ///@notice a nations environment score will go up 10 points for anarchy, communist, dictatorship, and transitional gov types
    ///@notice a nations environment score will go down 10 points for capitalist, democracy, and republic gov types
    ///@param id this is the nation ID of the nation being queried
    ///@return int256 is the gross environment points from a nations government
    function getScoreFromGovernment(uint256 id) public view returns (int256) {
        int256 pointsFromGovernmentType = 0;
        uint256 governmentType = param.getGovernmentType(id);
        if (
            //anarchy
            governmentType == 0 ||
            //communist            
            governmentType == 2 ||
            //dictatorship
            governmentType == 4 ||
            //transitional
            governmentType == 10
        ) {
            pointsFromGovernmentType += 10;
        } else if (
            //capitalist
            governmentType == 1 ||
            //democracy            
            governmentType == 3 ||
            //republic
            governmentType == 7
        ) {
            pointsFromGovernmentType -= 10;
        }
        return pointsFromGovernmentType;
    }
}
","keccak256":"0xca39ffc1d9582653387180662aa33842c49b5fce5f1524e8238445ad4903f787","license":"BUSL-1.1"},"contracts/Fighters.sol":{"content":"//SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

import "./Treasury.sol";
import "./Infrastructure.sol";
import "./CountryMinter.sol";
import "./Bombers.sol";
import "./Resources.sol";
import "./Improvements.sol";
import "./Wonders.sol";
import "./Navy.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "hardhat/console.sol";

///@title FightersContract
///@author OxSnosh
///@notice this contract will store data for the figher aircraft owned by a nation
contract FightersContract is Ownable {
    address public countryMinter;
    address public fightersMarket1;
    address public fightersMarket2;
    address public bombers;
    address public treasuryAddress;
    address public infrastructure;
    address public war;
    address public resources;
    address public improvements1;
    address public airBattle;
    address public wonders1;
    address public losses;
    address public navy;

    CountryMinter mint;
    ResourcesContract res;
    ImprovementsContract1 imp1;
    WondersContract1 won1;
    NavyContract nav;
    BombersContract bomb;

    struct Fighters {
        bool initialized;
        uint256 yak9Count;
        uint256 p51MustangCount;
        uint256 f86SabreCount;
        uint256 mig15Count;
        uint256 f100SuperSabreCount;
        uint256 f35LightningCount;
        uint256 f15EagleCount;
        uint256 su30MkiCount;
        uint256 f22RaptorCount;
    }

    event Yak9Purchased(uint256 indexed id, uint256 indexed amount);
    event P51MustangPurchased(uint256 indexed id, uint256 indexed amount);
    event F86SabrePurchased(uint256 indexed id, uint256 indexed amount);
    event Mig15Purchased(uint256 indexed id, uint256 indexed amount);
    event F100SuperSabrePurchased(uint256 indexed id, uint256 indexed amount);
    event F35LightningPurchased(uint256 indexed id, uint256 indexed amount);
    event F15EaglePurchased(uint256 indexed id, uint256 indexed amount);
    event Su30MkiPurchased(uint256 indexed id, uint256 indexed amount);
    event F22RaptorPurchased(uint256 indexed id, uint256 indexed amount);

    event Yak9Scrapped(uint256 indexed id, uint256 indexed amount);
    event P51MustangScrapped(uint256 indexed id, uint256 indexed amount);
    event F86SabreScrapped(uint256 indexed id, uint256 indexed amount);
    event Mig15Scrapped(uint256 indexed id, uint256 indexed amount);
    event F100SuperSabreScrapped(uint256 indexed id, uint256 indexed amount);
    event F35LightningScrapped(uint256 indexed id, uint256 indexed amount);
    event F15EagleScrapped(uint256 indexed id, uint256 indexed amount);
    event Su30MkiScrapped(uint256 indexed id, uint256 indexed amount);
    event F22RaptorScrapped(uint256 indexed id, uint256 indexed amount);

    mapping(uint256 => Fighters) public idToFighters;

    ///@dev this function is only callable by the contract owner
    ///@dev this function will be called immediately after contract deployment in order to set contract pointers
    function settings(
        address _countryMinter,
        address _fightersMarket1,
        address _fightersMarket2,
        address _treasuryAddress,
        address _war,
        address _infrastructure,
        address _resources,
        address _improvements1,
        address _airBattle,
        address _wonders1,
        address _losses
    ) public onlyOwner {
        countryMinter = _countryMinter;
        mint = CountryMinter(_countryMinter);
        resources = _resources;
        res = ResourcesContract(payable(_resources));
        improvements1 = _improvements1;
        imp1 = ImprovementsContract1(_improvements1);
        fightersMarket1 = _fightersMarket1;
        fightersMarket2 = _fightersMarket2;
        treasuryAddress = _treasuryAddress;
        war = _war;
        infrastructure = _infrastructure;
        airBattle = _airBattle;
        wonders1 = _wonders1;
        won1 = WondersContract1(_wonders1);
        losses = _losses;
    }

    ///@dev this function is only callable by the contract owner
    ///@dev this function will be called immediately after contract deployment in order to set contract pointers
    function settings2(address _navy, address _bombers) public onlyOwner {
        navy = _navy;
        nav = NavyContract(_navy);
        bombers = _bombers;
        bomb = BombersContract(_bombers);
    }

    modifier onlyCountryMinter() {
        require(msg.sender == countryMinter, "only countryMinter can call");
        _;
    }

    modifier onlyMarket() {
        require(
            msg.sender == fightersMarket1 || msg.sender == fightersMarket2,
            "this function can only be called by market"
        );
        _;
    }

    modifier onlyLossesContract() {
        require(msg.sender == losses, "only callable from losses contract");
        _;
    }

    ///@dev this function is a public function but only callable from the country minter contact when a country is minted
    ///@notice this function allows a nation to purchase fighter aircraft once a country is minted
    ///@param id this is the nation ID of the nation being minted
    function generateFighters(uint256 id) public onlyCountryMinter {
        require(
            idToFighters[id].initialized == false,
            "fighters already initialized for this nation"
        );
        Fighters memory newFighters = Fighters(true, 0, 0, 0, 0, 0, 0, 0, 0, 0);
        idToFighters[id] = newFighters;
    }

    ///@notice this function will return the amount of defending Yak9's of a nation
    ///@param id is the nation ID of the nation
    ///@return uint256 is the number of defending Yak9 aircraft for the nation
    function getYak9Count(uint256 id) public view returns (uint256) {
        uint256 count = idToFighters[id].yak9Count;
        return count;
    }

    ///@dev this function is only callable from the Fighter Market contracts
    ///@notice this function will increase the number of aircraft when they are purchased in the marketplace
    ///@param id is the nation ID of the nation
    ///@param amount is the amount of aircraft being purchased
    function increaseYak9Count(uint256 id, uint256 amount) public onlyMarket {
        idToFighters[id].yak9Count += amount;
        emit Yak9Purchased(id, amount);
    }

    ///@dev this function is only callable from the losses contract
    ///@notice this function will decrease the amount of defending aircraft lost in a battle
    ///@param id is the nation ID of the nation
    ///@param amount is the amount of aircraft being destroyed
    function decreaseYak9Count(
        uint256 amount,
        uint256 id
    ) public onlyLossesContract {
        uint256 currentAmount = idToFighters[id].yak9Count;
        if (currentAmount >= amount) {
            idToFighters[id].yak9Count -= amount;
        } else {
            idToFighters[id].yak9Count = 0;
        }
    }

    ///@notice this function will allow a nation owner to decommission Yak9's
    ///@param id is the nation ID of the nation
    ///@param amount is the amount of aircraft being destroyed
    function scrapYak9(uint256 amount, uint256 id) public {
        bool isOwner = mint.checkOwnership(id, msg.sender);
        require(isOwner, "!nation ruler");
        uint256 currentAmount = idToFighters[id].yak9Count;
        require(currentAmount >= amount, "cannot delete that many");
        idToFighters[id].yak9Count -= amount;
        emit Yak9Scrapped(id, amount);
    }

    ///@notice this function will return the amount of defending P51 Mustangs's of a nation
    ///@param id is the nation ID of the nation
    ///@return uint256 is the number of defending P51 Mustang aircraft for the nation
    function getP51MustangCount(uint256 id) public view returns (uint256) {
        uint256 count = idToFighters[id].p51MustangCount;
        return count;
    }

    ///@dev this function is only callable from the Fighter Market contracts
    ///@notice this function will increase the number of aircraft when they are purchased in the marketplace
    ///@param id is the nation ID of the nation
    ///@param amount is the amount of aircraft being purchased
    function increaseP51MustangCount(
        uint256 id,
        uint256 amount
    ) public onlyMarket {
        idToFighters[id].p51MustangCount += amount;
        emit P51MustangPurchased(id, amount);
    }

    ///@dev this function is only callable from the losses contract
    ///@notice this function will decrease the amount of defending aircraft lost in a battle
    ///@param id is the nation ID of the nation
    ///@param amount is the amount of aircraft being destroyed
    function decreaseP51MustangCount(
        uint256 amount,
        uint256 id
    ) public onlyLossesContract {
        uint256 currentAmount = idToFighters[id].p51MustangCount;
        if (currentAmount >= amount) {
            idToFighters[id].p51MustangCount -= amount;
        } else {
            idToFighters[id].p51MustangCount = 0;
        }
    }

    ///@notice this function will allow a nation owner to decommission P51 Mustangs's
    ///@param id is the nation ID of the nation
    ///@param amount is the amount of aircraft being destroyed
    function scrapP51Mustang(uint256 amount, uint256 id) public {
        bool isOwner = mint.checkOwnership(id, msg.sender);
        require(isOwner, "!nation ruler");
        uint256 currentAmount = idToFighters[id].p51MustangCount;
        require(currentAmount >= amount, "cannot delete that many");
        idToFighters[id].p51MustangCount -= amount;
        emit P51MustangScrapped(id, amount);
    }

    ///@notice this function will return the amount of defending F86 Sabre's of a nation
    ///@param id is the nation ID of the nation
    ///@return uint256 is the number of defending F86 Sabre aircraft for the nation
    function getF86SabreCount(uint256 id) public view returns (uint256) {
        uint256 count = idToFighters[id].f86SabreCount;
        return count;
    }

    ///@dev this function is only callable from the Fighter Market contracts
    ///@notice this function will increase the number of aircraft when they are purchased in the marketplace
    ///@param id is the nation ID of the nation
    ///@param amount is the amount of aircraft being purchased
    function increaseF86SabreCount(
        uint256 id,
        uint256 amount
    ) public onlyMarket {
        idToFighters[id].f86SabreCount += amount;
        emit F86SabrePurchased(id, amount);
    }

    ///@dev this function is only callable from the losses contract
    ///@notice this function will decrease the amount of defending aircraft lost in a battle
    ///@param id is the nation ID of the nation
    ///@param amount is the amount of aircraft being destroyed
    function decreaseF86SabreCount(
        uint256 amount,
        uint256 id
    ) public onlyLossesContract {
        uint256 currentAmount = idToFighters[id].f86SabreCount;
        if (currentAmount >= amount) {
