    ///@dev this is a public function only callable by a nation owner
    ///@dev this function will allow a nation owner to set their nations tax rate
    ///@notice this function will allow a nation owner to set their nations tax rate
    ///@notice a tax rate can be between 15% and 28%
    ///@notice a tax rate can be 30% with a social security wonder
    ///@param id is the nation id of the nation changing its tax rate
    ///@param newTaxRate is the new tax rate for a nation
    function setTaxRate(uint256 id, uint256 newTaxRate) public {
        bool owner = mint.checkOwnership(id, msg.sender);
        require(owner, "!nation owner");
        require(
            idToInfrastructure[id].collectionNeededToChangeRate == false,
            "need to collect taxes before changing tax rate"
        );
        uint256 maximumTaxRate = 28;
        bool socialSecurity = won4.getSocialSecuritySystem(id);
        if (socialSecurity) {
            maximumTaxRate = 30;
        }
        require(newTaxRate <= maximumTaxRate, "cannot tax above maximum rate");
        require(newTaxRate >= 15, "cannot tax below 15%");
        idToInfrastructure[id].taxRate = newTaxRate;
    }

    ///@dev this is a public function only callable from the spy contract
    ///@dev this function will reset a nations tax rate after a succesful spy attack
    ///@notice this function will reset a nations tax rate after a succesful spy attack
    ///@param id this is the nation ID for the nation being attacked and getting its tax rate changed
    ///@param newTaxRate is the new tax rate for the nation
    function setTaxRateFromSpyContract(
        uint256 id,
        uint256 newTaxRate
    ) public onlySpyContract {
        idToInfrastructure[id].taxRate = newTaxRate;
        idToInfrastructure[id].collectionNeededToChangeRate = true;
    }

    ///@dev this is a public function only callable from the taxes contract
    ///@dev this function will toggle the collection needed to change tax rate to true
    ///@notice this function will toggle the collection needed to change tax rate to true
    ///@notice when a nation is blockaded it will need to either break the blockade or collect taxes at a reduced rate to be able to change tax rate
    ///@param id is the nation ID of the nation toggleing the collection needed parameter
    function toggleCollectionNeededToChangeRate(
        uint256 id
    ) public onlyTaxesContract {
        idToInfrastructure[id].collectionNeededToChangeRate = false;
    }

    ///@dev this is a public view function that will return true if a nation needs to collect taxes in order to change its tax rate
    ///@notice this function will return true if a nation needs to collect taxes in order to change its tax rate
    ///@param id is the nation ID of the nation being queried
    ///@return bool is the boolean value whether a nation needs to collect taxes in order to change its tax rate
    function checkIfCollectionNeededToChangeRate(
        uint256 id
    ) public view returns (bool) {
        bool collectionNeeded = idToInfrastructure[id]
            .collectionNeededToChangeRate;
        return collectionNeeded;
    }
}
","keccak256":"0x45c59853ae907eac83ba3e967a47625c1cab660ebc506af8d7aacbc19a2cd960","license":"BUSL-1.1"},"contracts/KeeperFile.sol":{"content":"//SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

import "@openzeppelin/contracts/access/Ownable.sol";

///@title KeeperContract
///@author OxSnosh
///@dev this contract will allow gelato automation to maintain the game clock that increments every 12 hours
contract KeeperContract is Ownable {
    uint256 public gameDay;
    uint public interval;
    uint public lastTimeStamp;

    event GameDayIncremented(uint256 indexed gameDay);


    constructor() {
        interval = 12 hours;
        lastTimeStamp = block.timestamp;
        gameDay = 0;
    }

    function checkUpkeep() external view returns (bool upkeepNeeded) {
        upkeepNeeded = (block.timestamp - lastTimeStamp) >= interval;
        return (upkeepNeeded);
    }

    function performUpkeep() external {
        require((block.timestamp - lastTimeStamp) >= interval, "Upkeep not needed");
        lastTimeStamp = lastTimeStamp + interval;
        gameDay++;
        emit GameDayIncremented(gameDay);
    }

    function incrementGameDay() public onlyOwner {
        gameDay++;
        lastTimeStamp = block.timestamp;
        emit GameDayIncremented(gameDay);
    }

    function updateInterval(uint _interval) public onlyOwner {
        interval = _interval;
    }

    function getGameDay() public view returns (uint256) {
        return gameDay;
    }
}
","keccak256":"0x140d457a63490620dae49d159d574c3b3e79df12bdf4fba03d597a3011de4bb6","license":"BUSL-1.1"},"contracts/Military.sol":{"content":"//SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

import "./SpyOperations.sol";
import "./CountryMinter.sol";
import "./KeeperFile.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

///@title MilitaryContract
///@author OxSnosh
///@dev this contract inherits from openzeppelin's ownable contract
///@notice this contract will allow a nation owner to control their defcon level, threat level and war/peace preference
contract MilitaryContract is Ownable {
    address public spyAddress;
    address public countryMinter;
    address public keeper;

    SpyOperationsContract spy;
    CountryMinter mint;
    KeeperContract keep;

    struct Military {
        bool initialized;
        uint256 defconLevel;
        uint256 threatLevel;
        bool warPeacePreference;
        uint256 dayPeaceToggled;
    }

    event DefconLevelUpdated(uint256 indexed id, uint256 indexed newLevel);

    event ThreatLevelUpdated(uint256 indexed id, uint256 indexed newLevel);

    event WarPeacePreferenceToggled(
        uint256 indexed id,
        bool indexed warPeacePreference
    );

    mapping(uint256 => Military) public idToMilitary;

    modifier onlySpyContract() {
        require(msg.sender == spyAddress, "only callable from spy contract");
        _;
    }

    modifier onlyCountryMinter() {
        require(msg.sender == countryMinter, "only callable from country minter contract");
        _;
    }

    ///@dev this function is only callable by the contract owner
    ///@dev this function will be called immediately after contract deployment in order to set contract pointers
    function settings (address _spyAddress, address _countryMinter, address _keeper) public onlyOwner {
        spyAddress = _spyAddress;
        spy = SpyOperationsContract(_spyAddress);
        countryMinter = _countryMinter;
        mint = CountryMinter(_countryMinter);
        keeper = _keeper;
        keep = KeeperContract(_keeper);
    }

    ///@dev this function is a public function only callable from the country minter contract
    ///@notice this function will allow allow a nation owner to reset their defcon and threat level and toggle their war peace preference
    function generateMilitary(uint256 id) public onlyCountryMinter {
        require(idToMilitary[id].initialized == false, "Military already initialized");
        Military memory newMilitary = Military(true, 5, 1, false, 0);
        idToMilitary[id] = newMilitary;
    }

    ///@dev this is a public function only callable by the nation owner
    ///@dev this function will allow a nation owner to update their defcon level
    ///@notice this function will allow a nation owner to update their defcon level
    ///@param newDefcon is the new defcon which must be an integer between 1 and 5
    ///@param id is the nation id of the nation updating their defcon
    function updateDefconLevel(uint256 newDefcon, uint256 id) public {
        bool isOwner = mint.checkOwnership(id, msg.sender);
        require (isOwner, "!nation owner");
        require(
            newDefcon == 1 ||
                newDefcon == 2 ||
                newDefcon == 3 ||
                newDefcon == 4 ||
                newDefcon == 5,
            "New DEFCON level is not an integer between 1 and 5"
        );
        idToMilitary[id].defconLevel = newDefcon;
        emit DefconLevelUpdated(id, newDefcon);
    }

    ///@dev this function will only be callable from the Spy contract
    ///@dev this function will allow a successful spy operation to update the defcon level
    ///@notice this function will allow a successful spy attack to update the defcon level
    ///@param id is the nation id that was attacked and getting their defcon reset
    ///@param newLevel is the new defcon level being set during the attack
    function setDefconLevelFromSpyContract(uint256 id, uint256 newLevel) public onlySpyContract {
        require(
            newLevel == 1 ||
                newLevel == 2 ||
                newLevel == 3 ||
                newLevel == 4 ||
                newLevel == 5,
            "New DEFCON level is not an integer between 1 and 5"
        );
        idToMilitary[id].defconLevel = newLevel;
        emit DefconLevelUpdated(id, newLevel);
    }

    ///@dev this is a public function only callable by the nation owner
    ///@dev this function allows a nation owner to update the threat level of a nation
    ///@notice this function allows a nation owner to update the threat level of a nation
    ///@param newThreatLevel is the new threat level being updated
    ///@param id is the nation id of the nation updating the threat level
    function updateThreatLevel(uint256 newThreatLevel, uint256 id) public {
        bool isOwner = mint.checkOwnership(id, msg.sender);
        require (isOwner, "!nation owner");
        require(
            newThreatLevel == 1 ||
                newThreatLevel == 2 ||
                newThreatLevel == 3 ||
                newThreatLevel == 4 ||
                newThreatLevel == 5,
            "Not a valid threat level"
        );
        idToMilitary[id].threatLevel = newThreatLevel;
        emit ThreatLevelUpdated(id, newThreatLevel);
    }

    ///@dev this function will only be callable from the Spy contract
    ///@dev this function will allow a successful spy operation to update the threat level
    ///@notice this function will allow a successful spy attack to update the threat level
    ///@param id is the nation id that was attacked and getting their threat level reset
    ///@param newLevel is the new threat level being set during the attack
    function setThreatLevelFromSpyContract(uint256 id, uint256 newLevel)
        public
        onlySpyContract
    {
        require(
            newLevel == 1 ||
                newLevel == 2 ||
                newLevel == 3 ||
                newLevel == 4 ||
                newLevel == 5,
            "New threat level is not an integer between 1 and 5"
        );
        idToMilitary[id].threatLevel = newLevel;
        emit ThreatLevelUpdated(id, newLevel);
    }

    ///@dev this function is a public function only callable from the nation owner
    ///@dev this function will allow a nation to toggle their preference for peace or war
    ///@notice this function will allow a nation to toggle their preference for peace or war
    ///@param id is the nation id of the nation toggling their preference
    function toggleWarPeacePreference(uint256 id) public {
        bool isOwner = mint.checkOwnership(id, msg.sender);
        require (isOwner, "!nation owner");
        uint256 gameDay = keep.getGameDay();
        if (idToMilitary[id].warPeacePreference == true) {
            require(
                (gameDay - idToMilitary[id].dayPeaceToggled) >= 7,
                "Must wait 7 days to switch to peace mode"
            );
            idToMilitary[id].warPeacePreference = false;
            idToMilitary[id].dayPeaceToggled = gameDay;
        } else {
            idToMilitary[id].warPeacePreference = true;
            idToMilitary[id].dayPeaceToggled = gameDay;
        }
        emit WarPeacePreferenceToggled(id, idToMilitary[id].warPeacePreference);
    }

    ///@dev this is a public view function that will return a nations defcon level
    ///@param id is the nation id of the nation being queried
    ///@return uint256 is the defcon level for a given nation
    function getDefconLevel(uint256 id) public view returns (uint256) {
        uint256 defcon = idToMilitary[id].defconLevel;
        return defcon;
    }

    ///@dev this is a public view function that will return a nations threat level
    ///@param id is the nation id of the nation being queried
    ///@return uint256 is the threat level for a given nation
    function getThreatLevel(uint256 id) public view returns (uint256) {
        uint256 threatLevel = idToMilitary[id].threatLevel;
        return threatLevel;
    }

    ///@dev this is a public view function that will return a nations preference for war
    ///@param id is the nation id of the nation being queried
    ///@return bool is true if war is possible
    function getWarPeacePreference(uint256 id) public view returns (bool, uint256) {
        bool war = idToMilitary[id].warPeacePreference;
        uint256 daysPeaceToggled = idToMilitary[id].dayPeaceToggled;
        uint256 gameDay = keep.getGameDay();
        uint256 daysRemaining;
        if (war == true) {
            daysRemaining = 7 - (gameDay - daysPeaceToggled);
        } else {
            daysRemaining = 0;
        }
        return (war, daysRemaining);
    }

    function getDaysInPeaceMode(uint256 id) public view returns (uint256) {
        uint256 daysPeaceToggled = idToMilitary[id].dayPeaceToggled;
        uint256 gameDay = keep.getGameDay();
        uint256 daysInPeaceMode = (gameDay - daysPeaceToggled);
        return daysInPeaceMode;
    }
}
","keccak256":"0x6ed3bc0adaf78564e8f4d88574f21271ab544a2a5a7ebc9fd863a571ec67a65b","license":"BUSL-1.1"},"contracts/Missiles.sol":{"content":"//SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

import "./Treasury.sol";
import "./Infrastructure.sol";
import "./Resources.sol";
import "./Wonders.sol";
import "./Improvements.sol";
import "./CountryMinter.sol";
import "./War.sol";
import "./NationStrength.sol";
import "./GroundBattle.sol";
import "./KeeperFile.sol";
import "./CountryParameters.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

///@title MissilesContract
///@author OxSnosh
///@dev this contract will allow a nation to purchase cruise missiles and nukes
///@dev this contract inherits from the openzeppelin ownable contract
contract MissilesContract is Ownable, ReentrancyGuard {
    uint256 public cruiseMissileCost = 20000 * (10 ** 18);
    uint256 public defaultNukeCost = 500000 * (10 ** 18);
    uint256 public nukeCost;
    address public countryMinter;
    address public treasury;
    address public spyAddress;
    address public resources;
    address public improvements1;
    address public wonders1;
    address public wonders2;
    address public wonders4;
    address public nukeAddress;
    address public airBattle;
    address public countryinter;
    address public strength;
    address public keeper;
    address public infrastructure;

    CountryMinter mint;
    InfrastructureContract inf;
    ResourcesContract res;
    WondersContract1 won1;
    WondersContract2 won2;
    WondersContract4 won4;
    ImprovementsContract1 imp1;
    ImprovementsContract2 imp2;
    WarContract war;
    TreasuryContract tsy;
    NationStrengthContract stren;
    KeeperContract keep;

    struct Missiles {
        bool initialized;
        uint256 cruiseMissiles;
        uint256 nuclearWeapons;
    }

    mapping(uint256 => mapping(uint256 => uint256))
        public idToNukesPurchasedToday;

    event CruiseMissilesPurchased(
        uint256 indexed id,
        uint256 indexed amountPurchased
    );

    event NukePurchased(uint256 indexed id);

    event CruiseMissilesDestroyedByAirAssault(
        uint256 indexed id,
        uint256 amountDestroyed
    );

    ///@dev this function is only callable by the contract owner
    ///@dev this function will be called immediately after contract deployment in order to set contract pointers
    function settings(
        address _treasury,
        address _spyAddress,
        address _nukeAddress,
        address _airBattle,
        address _wonders2,
        address _strength,
        address _infrastructure
    ) public onlyOwner {
        treasury = _treasury;
        tsy = TreasuryContract(_treasury);
        spyAddress = _spyAddress;
        nukeAddress = _nukeAddress;
        airBattle = _airBattle;
        wonders2 = _wonders2;
        won2 = WondersContract2(_wonders2);
        strength = _strength;
        stren = NationStrengthContract(_strength);
        infrastructure = _infrastructure;
        inf = InfrastructureContract(_infrastructure);
    }

    ///@dev this function is only callable by the contract owner
    ///@dev this function will be called immediately after contract deployment in order to set contract pointers
    function settings2(
        address _resources,
        address _improvements1,
        address _wonders1,
        address _wonders4,
        address _countryMinter,
        address _keeper
    ) public onlyOwner {
        resources = _resources;
        res = ResourcesContract(payable(_resources));
        wonders1 = _wonders1;
        won1 = WondersContract1(_wonders1);
        wonders4 = _wonders4;
        won4 = WondersContract4(_wonders4);
        improvements1 = _improvements1;
        imp1 = ImprovementsContract1(_improvements1);
        countryMinter = _countryMinter;
        mint = CountryMinter(_countryMinter);
        keeper = _keeper;
        keep = KeeperContract(_keeper);
    }

    mapping(uint256 => Missiles) public idToMissiles;

    modifier onlyCountryMinter() {
        require(
            msg.sender == countryMinter,
            "only callable from country minter contract"
        );
        _;
    }

    function generateMissiles(uint256 id) public onlyCountryMinter {
        require(
            idToMissiles[id].initialized == false,
            "missiles already initialized"
        );
        Missiles memory newMissiles = Missiles(true, 0, 0);
        idToMissiles[id] = newMissiles;
    }

    modifier onlySpyContract() {
        require(msg.sender == spyAddress, "only callable from spy contract");
        _;
    }

    modifier onlyNukeContract() {
        require(msg.sender == nukeAddress, "only callable from nuke contract");
        _;
    }

    modifier onlyAirBattle() {
        require(
            msg.sender == airBattle,
            "only callable from air battle contract"
        );
        _;
    }

    ///@dev this function is a public function that will allow a nation owner to purchase cruise missiles
    ///@notice this function allows a nation owner to purchase cruise missiles
    ///@param amount is the amount of cruise missiles being purchased
    ///@param id is the nation id of the nation buying cruise missiles
    function buyCruiseMissiles(uint256 amount, uint256 id) public nonReentrant{
        bool isOwner = mint.checkOwnership(id, msg.sender);
        require(isOwner, "!nation owner");
        uint256 techAmount = inf.getTechnologyCount(id);
        require(techAmount >= 15, "requires 15 tech");
        uint256 costPerMissile = getCruiseMissileCost(id);
        uint256 cost = (costPerMissile * amount);
        tsy.spendBalance(id, cost);
        idToMissiles[id].cruiseMissiles += amount;
        emit CruiseMissilesPurchased(id, amount);
    }

    ///@dev this function is only callable by the contract owner
    function updateCruiseMissileCost(uint256 newPrice) public onlyOwner {
        cruiseMissileCost = newPrice;
    }

    ///@dev this is a public view function that will return the cost per missiile of cruise missiles for a nation
    ///@notice this function will return the cost per cruise missile for a given nation
    ///@param id is the nation id of the nation purchasing missiles
    ///@return cost is the cost per missile of cruise missiles for that nation
    function getCruiseMissileCost(
        uint256 id
    ) public view returns (uint256 cost) {
        uint256 basePurchasePrice = cruiseMissileCost;
        uint256 factoryCount = imp1.getFactoryCount(id);
        uint256 costModifier = 100;
        if (factoryCount > 0) {
            costModifier -= (factoryCount * 5);
        }
        (,,,,,,,, bool lead,,) = res.getResources1(id);
        if (lead) {
            costModifier -= 20;
        }
        uint256 costPerMissile = ((basePurchasePrice * costModifier) / 100);
        return costPerMissile;
    }

    ///@dev this is a public view function that will return the number of cruise missile a nation owns
    ///@notice this function will return the number of cruise missiles a given nation owns
    ///@param id is the nation ID of the nation being queried
    ///@return uint256 is the number of cruise missiles a given nation owns
    function getCruiseMissileCount(uint256 id) public view returns (uint256) {
        uint256 count = idToMissiles[id].cruiseMissiles;
        return count;
    }

    ///@dev this is a public function that will decrease the number of cruise missiles only callable from the spy contract
    ///@notice this function will decrease the number of cruise missiles lost during a spy attack
    ///@param amount this is the number of missiles being destroyed
    ///@param id this is the nation id of the nation being attacked
    function decreaseCruiseMissileCount(
        uint256 amount,
        uint256 id
    ) public onlySpyContract {
        uint256 missiles = idToMissiles[id].cruiseMissiles;
        if (amount >= missiles) {
            idToMissiles[id].cruiseMissiles = 0;
        } else {
            idToMissiles[id].cruiseMissiles -= amount;
        }
    }

    ///@dev this is a public function that will decrease the number of cruise missiles only callable from the nuke contract
    ///@notice this function will decrease the number of cruise missiles lost during a nuke attack
    ///@notice a succesful nuke attack will destroy 35% of your nations cruise missiles
    ///@notice a fallout shelter system will reduce the number of missiles lost during a nuke attack to 25%
    ///@param id this is the nation id of the nation being attacked
    function decreaseCruiseMissileCountFromNukeContract(
        uint256 id
    ) public onlyNukeContract {
        uint256 cruiseMissiles = idToMissiles[id].cruiseMissiles;
        uint256 percentage = 35;
        bool falloutShelter = won1.getFalloutShelterSystem(id);
        if (falloutShelter) {
            percentage = 25;
        }
        uint256 cruiseMissilesToDecrease = ((cruiseMissiles * percentage) /
            100);
        if (cruiseMissilesToDecrease >= cruiseMissiles) {
            idToMissiles[id].cruiseMissiles = 0;
        } else {
            idToMissiles[id].cruiseMissiles -= cruiseMissilesToDecrease;
        }
    }

    ///@dev this is a public function only callable from the air battle contact
    ///@notice this function will decrease the number of cruise missiles lost during a bombing mission
    ///@param id this is the nation id of the nation losing missiles from being attacked by bombers
    ///@param amountToDecrease this is the number of cruise missiles beind destroyed by the bombing mission
    function decreaseCruiseMissileCountFromAirBattleContract(
        uint256 id,
        uint256 amountToDecrease
    ) public onlyAirBattle returns (bool) {
        uint256 cruiseMissiles = idToMissiles[id].cruiseMissiles;
        if (amountToDecrease >= cruiseMissiles) {
            idToMissiles[id].cruiseMissiles = 0;
            amountToDecrease = cruiseMissiles;
        } else {
            idToMissiles[id].cruiseMissiles -= amountToDecrease;
        }
        emit CruiseMissilesDestroyedByAirAssault(id, amountToDecrease);
        return true;
    }

    ///@dev this is a public function that will allow a nation owner to purchase nukes
    ///@notice this function allows a nation owner to purchase nukes
    ///@notice requirements to purchase nukes are 75 technology, 1000 infrastructure and access to uranium
    ///@notice a nation must also have a nation strength of 150,000 or a manhattan project to purchase nukes
    ///@notice a nation owner can only purchase one nuke per day (2 with a weapons research center)
    ///@param id is the nation id of the nation purchasing nukes
    function buyNukes(uint256 id) public nonReentrant {
        bool isOwner = mint.checkOwnership(id, msg.sender);
        require(isOwner, "!nation owner");
        uint256 techAmount = inf.getTechnologyCount(id);
        require(techAmount >= 75, "requires 75 tech");
        uint256 infrastructureAmount = inf.getInfrastructureCount(id);
        require(infrastructureAmount >= 1000, "insufficient infrastructure");
        (,,,,,, bool isUranium,,,) = res.getResources2(id);
        require(isUranium, "no uranium");
        uint256 nationStrength = stren.getNationStrength(id);
        bool manhattanProject = won2.getManhattanProject(id);
        require(
            nationStrength > 150000 || manhattanProject,
            "need 150,000 strength or manhattan project"
        );
        uint256 day = keep.getGameDay();
        uint256 nukesPurchasedToday = idToNukesPurchasedToday[id][day];
        uint256 maxNukesPerDay = 1;
        bool weaponsResearchCenter = won4.getWeaponsResearchCenter(id);
        if (weaponsResearchCenter) {
            maxNukesPerDay = 2;
        }
        require(
            nukesPurchasedToday < maxNukesPerDay,
            "already purchased nuke today"
        );
        uint256 cost = getNukeCost(id);
        require(tsy.spendBalance(id, cost), "failed to spend balance on nuke");
        idToNukesPurchasedToday[id][day] += 1;
        idToMissiles[id].nuclearWeapons += 1;
        emit NukePurchased(id);
    }

    ///@dev this is a public function that will return the cost per nuke for a nation
    ///@notice this function will return the cost per nuke for a nation
    ///@notice nukes cost 500,000 + (50,000 * current nuke count)
    ///@param id is the nation ID of the nation being queried
    ///@return uint256 is the cost per nuke for a given nation
    function getNukeCost(uint256 id) public view returns (uint256) {
        uint256 nukeCount = idToMissiles[id].nuclearWeapons;
        uint256 cost = (defaultNukeCost + (nukeCount * 50000));
        uint256 mod = 100;
        (,,,,,,,, bool lead,,) = res.getResources1(id);
        if (lead) {
            mod = 80;
        }
        cost = ((cost * mod) / 100);
        return cost;
    }

    function updateDefaultNukeCost(uint256 newCost) public onlyOwner {
        defaultNukeCost = newCost;
    }

    function getDefaultNukeCost() public view returns (uint256) {
        return defaultNukeCost;
    }

    function getNukesPurchasedToday(uint256 id) public view returns (uint256) {
        uint256 day = keep.getGameDay();
        return idToNukesPurchasedToday[id][day];
    }

    ///@dev this is a public view function that will retrun a nations current nuke count
    ///@notice this function retrurns a nations current nuke count
    ///@param id this is the nation ID for the nation being queried
    ///@return uint256 this is the current nuke count for a given nation
    function getNukeCount(uint256 id) public view returns (uint256) {
        uint256 count = idToMissiles[id].nuclearWeapons;
        return count;
    }

    ///@dev this is a public function that will decrease the nuke count for a nation by 1 when a nuke is launched from the nuke contract
    ///@dev this function is only callable from the nuke contract
    ///@notice this function will decrease a nations nuke count by 1 when a nuke is launched
    ///@param id is the nation id of the nation launching the nuke that will have its nuke count decreased by 1
    function decreaseNukeCountFromNukeContract(
        uint256 id
    ) public onlyNukeContract {
        require(
            idToMissiles[id].nuclearWeapons > 0,
            "no nukes to launch"
        );
        idToMissiles[id].nuclearWeapons -= 1;
    }

    ///@dev this is a public function that will decrease the nuke count for a nation when a successful spy attack is executed
    ///@dev this function is only callable from the spy contract
    ///@notice this function will decrease a nations nuke count if they are successfully attacked by a spy
    ///@param id is the nation id of the nation that was attacked and is losing a nuke
    function decreaseNukeCountFromSpyContract(
        uint256 id
    ) public onlySpyContract {
        bool silo = won2.getHiddenNuclearMissileSilo(id);
        uint256 nukeCount = getNukeCount(id);
        uint256 requiredNukeAmount = 1;
        if (silo) {
            requiredNukeAmount = 6;
        }
        require(nukeCount >= requiredNukeAmount, "no nukes to destroy");
        idToMissiles[id].nuclearWeapons -= 1;
    }
}","keccak256":"0x185cdf02e42b6f6cb1f3287629916201aa260154c2196de2838df494a6642366","license":"BUSL-1.1"},"contracts/NationStrength.sol":{"content":"//SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

import "./Infrastructure.sol";
import "./Forces.sol";
import "./Fighters.sol";
import "./Bombers.sol";
import "./Navy.sol";
import "./Missiles.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "hardhat/console.sol";

///@title NationStrengthContract
///@author OxSnosh
///@dev this contract inherits from openzeppelin's ownable contract
///@notice this contract will calculate a given nation's strength
contract NationStrengthContract is Ownable {
    address public infrastructure;
    address public forces;
    address public fighters;
    address public bombers;
    address public navy;
    address public missiles;
    address public navy2;

    InfrastructureContract inf;
    ForcesContract frc;
    FightersContract fight;
    BombersContract bomb;
    NavyContract nav;
    MissilesContract mis;
    NavyContract2 nav2;

    ///@dev this function is only callable by the contract owner
    ///@dev this function will be called immediately after contract deployment in order to set contract pointers
    function settings (
        address _infrastructure,
        address _forces,
        address _fighters,
        address _bombers,
        address _navy,
        address _missiles,
        address _navy2
    ) public onlyOwner {
        infrastructure = _infrastructure;
        inf = InfrastructureContract(_infrastructure);
        forces = _forces;
        frc = ForcesContract(_forces);
        fighters = _fighters;
        fight = FightersContract(_fighters);
        bombers = _bombers;
        bomb = BombersContract(_bombers);
        navy = _navy;
        nav = NavyContract(_navy);
        missiles = _missiles;
        mis = MissilesContract(_missiles);
        navy2 = _navy2;
        nav2 = NavyContract2(_navy2);
    }


    ///@dev this function is only callable by the contract owner
    function updateInfrastructureContract(address newAddress) public onlyOwner {
        infrastructure = newAddress;
        inf = InfrastructureContract(newAddress);
    }

    ///@dev this function is only callable by the contract owner
    function updateForcesContract(address newAddress) public onlyOwner {
        forces = newAddress;
        frc = ForcesContract(newAddress);
    }

    ///@dev this function is only callable by the contract owner
    function updateFightersContract(address newAddress) public onlyOwner {
        fighters = newAddress;
        fight = FightersContract(newAddress);
    }

    ///@dev this function is only callable by the contract owner
    function updateBombersContract(address newAddress) public onlyOwner {
        bombers = newAddress;
        bomb = BombersContract(newAddress);
    }

    ///@dev this function is only callable by the contract owner
    function updateNavyContract(address newAddress) public onlyOwner {
        navy = newAddress;
        nav = NavyContract(newAddress);
    }

    ///@dev this is a public view function that will return a nation's strength
    ///@notice this function will return a given nations strength
    ///@notice strength is calculated in the following way
    /**
     * land purchased * 1.5 +
     * infra * 3 +
     * tech * 5 +
     * soldiers * .02 +
     * tanks deployed * .15 +
     * tanks defending * .20 +
     * aircraft rankings * 5 +
     * navy rankings * 10
     * cruise missiles * 10 +
     * nukes ** 2 * 10 +
    */
    ///@param id is the nation id of the nation being queried
    ///@return uint256 is the nations strength
    function getNationStrength(uint256 id) public view returns (uint256) {
        uint256 nationStrengthFromCommodities = getNationStrengthFromCommodities(
                id
            );
        uint256 nationStrengthFromMilitary = getNationStrengthFromMilitary(id);
        uint256 nationStrength = nationStrengthFromCommodities +
            nationStrengthFromMilitary;
        return nationStrength;
    }

    function getNationStrengthFromCommodities(uint256 id)
        public
        view
        returns (uint256)
    {
        uint256 landCount = inf.getLandCount(id);
        uint256 techCount = inf.getTechnologyCount(id);
        uint256 infrastructureCount = inf.getInfrastructureCount(id);
        uint256 strengthFromCommodities = (((landCount * 150) +
            (techCount * 500) +
            (infrastructureCount * 300)) / 100);
        return strengthFromCommodities;
    }

    function getNationStrengthFromMilitary(uint256 id)
        internal
        view
        returns (uint256)
    {
        uint256 soldierCount = frc.getSoldierCount(id);
        uint256 soldierStrength = ((soldierCount * 2) / 100);
        uint256 deployedTankCount = frc.getDeployedTankCount(id);
        uint256 defendingTankCount = frc.getDefendingTankCount(id);
        uint256 tankStrength = (((deployedTankCount * 15) +
            (defendingTankCount * 20)) / 100);
        uint256 additionalStrength = getAdditionalStrengthFromMiliary(id);
        uint256 strengthFromMilitary = (soldierStrength +
            tankStrength +
            additionalStrength);
        return strengthFromMilitary;
    }

    function getAdditionalStrengthFromMiliary(uint256 id) public view returns (uint256) {
        uint256 cruiseMissileCount = mis.getCruiseMissileCount(id);
        uint256 cruiseMissileStrength = ((cruiseMissileCount * 10));
        uint256 nukeCount = mis.getNukeCount(id);
        uint256 nukeStrengthBase = (nukeCount**2);
        uint256 nukeStrength = (nukeStrengthBase * 10);
        uint256 aircraftStrength = getStrengthFromAirForce(id);
        uint256 navyStrength = getStrengthFromNavy(id);
        uint256 additionalStrengthFromMilitary = (cruiseMissileStrength +
            nukeStrength +
            aircraftStrength +
            navyStrength);
        return additionalStrengthFromMilitary;
    }

    function getStrengthFromAirForce(uint256 id)
        internal
        view
        returns (uint256)
    {
        uint256 defendingFighterStrength = getStrengthFromFighters(id);
        uint256 defendingBomberStrength = getStrengthFromBombers(id);
        uint256 strengthFromAirForce = (defendingFighterStrength +
            defendingBomberStrength);
        return strengthFromAirForce;
    }

    function getStrengthFromFighters(uint256 id)
        internal
        view
        returns (uint256)
    {
        uint256 yak9Count = fight.getYak9Count(id);
        uint256 p51MustangCount = fight.getP51MustangCount(id);
        uint256 f86SabreCount = fight.getF86SabreCount(id);
        uint256 mig15Count = fight.getMig15Count(id);
        uint256 f100SuperSabreCount = fight.getF100SuperSabreCount(id);
        uint256 additionalFighterStrength = getAdditionalStrengthFromFighters(
                id
            );
        uint256 strengthFromFighters = (((yak9Count * 1) +
            (p51MustangCount * 2) +
            (f86SabreCount * 3) +
            (mig15Count * 4) +
            (f100SuperSabreCount * 5) +
            additionalFighterStrength) * 5);
        return strengthFromFighters;
    }

    function getAdditionalStrengthFromFighters(uint256 id)
        public
        view
        returns (uint256)
    {
        uint256 f35LightningCount = fight.getF35LightningCount(id);
        uint256 f15EagleCount = fight.getF15EagleCount(id);
        uint256 su30MkiCount = fight.getSu30MkiCount(id);
        uint256 f22RaptorCount = fight.getF22RaptorCount(id);
        uint256 additionalFighterStrength = ((f35LightningCount * 6) +
            (f15EagleCount * 7) +
            (su30MkiCount * 8) +
            (f22RaptorCount * 9));
        return additionalFighterStrength;
    }

    function getStrengthFromBombers(uint256 id)
        internal
        view
        returns (uint256)
    {
        uint256 ah1CobraCount = bomb.getAh1CobraCount(id);
        uint256 ah64ApacheCount = bomb.getAh64ApacheCount(id);
        uint256 bristolBlenheimCount = bomb.getBristolBlenheimCount(
            id
        );
        uint256 b52MitchellCount = bomb.getB52MitchellCount(id);
        uint256 b17gFlyingFortressCount = bomb
            .getB17gFlyingFortressCount(id);
        uint256 additionalStrengthFromBombers = getAdditionalStrengthFromBombers(
                id
            );
        uint256 strengthFromBombers = (((ah1CobraCount * 1) +
            (ah64ApacheCount * 2) +
            (bristolBlenheimCount * 3) +
            (b52MitchellCount * 4) +
            (b17gFlyingFortressCount * 5) +
            additionalStrengthFromBombers) * 5);
        return strengthFromBombers;
    }

    function getAdditionalStrengthFromBombers(uint256 id)
        internal
        view
        returns (uint256)
    {
        uint256 b52StratofortressCount = bomb
            .getB52StratofortressCount(id);
        uint256 b2SpiritCount = bomb.getB2SpiritCount(id);
        uint256 b1bLancerCount = bomb.getB1bLancerCount(id);
        uint256 tupolevTu160Count = bomb.getTupolevTu160Count(id);
        uint256 additionalStrengthFromBombers = ((b52StratofortressCount * 6) +
            (b2SpiritCount * 7) +
            (b1bLancerCount * 8) +
            (tupolevTu160Count * 9));
        return additionalStrengthFromBombers;
    }

    function getStrengthFromNavy(uint256 id) internal view returns (uint256) {
        uint256 corvetteCount = nav.getCorvetteCount(id);
        uint256 landingShipCount = nav.getLandingShipCount(id);
        uint256 battleshipCount = nav.getBattleshipCount(id);
        uint256 cruiserCount = nav.getCruiserCount(id);
        uint256 additionalNavyStrength = getAdditionalNavyStrength(id);
        uint256 strengthFromNavy = (((corvetteCount * 1) +
            (landingShipCount * 3) +
            (battleshipCount * 5) +
            (cruiserCount * 6) +
            additionalNavyStrength) * 10);
        return strengthFromNavy;
    }

    function getAdditionalNavyStrength(uint256 id)
        internal
        view
        returns (uint256)
    {
        uint256 frigateCount = nav2.getFrigateCount(id);
        uint256 destroyerCount = nav2.getDestroyerCount(id);
        uint256 submarineCount = nav2.getSubmarineCount(id);
        uint256 aircraftCarrierCount = nav2.getAircraftCarrierCount(id);
        uint256 additionalNavyStrength = ((frigateCount * 8) +
            (destroyerCount * 11) +
            (submarineCount * 12) +
            (aircraftCarrierCount * 15));
        return additionalNavyStrength;
    }
}
