import "@openzeppelin/contracts/access/Ownable.sol";
import "@gelatonetwork/relay-context/contracts/GelatoRelayContextERC2771.sol";
import {VRFConsumerBaseV2Plus} from "@chainlink/contracts/src/v0.8/vrf/dev/VRFConsumerBaseV2Plus.sol";
import {VRFV2PlusClient} from "@chainlink/contracts/src/v0.8/vrf/dev/libraries/VRFV2PlusClient.sol";

/* --------------------------------------------------------------------------
 * Contract
 * -------------------------------------------------------------------------- */

contract SpyOperationsContract is
    GelatoRelayContextERC2771,
    VRFConsumerBaseV2Plus
{
    struct DaySpies {
        uint8 outgoing;
        uint8 incoming;
    }

    struct PendingAttack {
        uint256 attackerId;
        uint256 defenderId;
        uint8 attackType;
        bool exists;
    }

    struct AttackMeta {
        uint256 attackerId;
        uint256 defenderId;
        uint8 attackType;
        bool success;
        bool revealed; // True if failure OR later reveal
        bool resolved;
    }

    /* ------------------------------- Constants -------------------------------- */

    uint8 public constant MAX_OUTGOING = 6;
    uint8 public constant MAX_INCOMING = 2;
    uint8 public constant MIN_ATTACK_TYPE = 1;
    uint8 public constant MAX_ATTACK_TYPE = 12;
    uint256 public constant MASK_SENTINEL = 0; // Masked attacker id in public success event

    /* --------------------------------- Storage -------------------------------- */

    // Incremental attack id
    uint256 public attackId;

    // day => nationId => counts
    mapping(uint256 => mapping(uint256 => DaySpies)) private _daySpyCount;

    // VRF request -> attackId
    mapping(uint256 => uint256) public vrfRequestToAttackId;

    // attackId -> pending (while waiting for VRF)
    mapping(uint256 => PendingAttack) public pending;

    // attackId -> final meta
    mapping(uint256 => AttackMeta) public attacks;

    /* -------------------------- External Contract Refs ------------------------ */

    IInfrastructure private inf;
    IForces private force;
    IMilitary private mil;
    INationStrength private strength;
    ITreasury private tsy;
    ICountryParameters private params;
    IMissiles private mis;
    IWonders1 private won1;
    IWonders2 private won2;
    ICountryMinter private mint;
    IKeeper private keep;
    ISpies private spy;

    /* ----------------------------- VRF Configuration --------------------------- */

    bytes32 private vrfKeyHash;
    uint256 private vrfSubId;
    uint16 private vrfMinConfirmations = 3;
    uint32 private vrfCallbackGasLimit;
    uint32 private vrfNumWords = 1;
    bool private useNativePayment = true;

    /* ---------------------------------- Events -------------------------------- */

    event SpyAttackCommitted(
        uint256 indexed attackId,
        uint256 indexed defenderId,
        uint8 attackType
    );
    event SpyAttackResolvedPublic(
        uint256 indexed attackId,
        uint256 maskedAttackerId,
        uint256 defenderId,
        bool success,
        uint256 attackType
    );
    event SpyAttackRevealed(uint256 indexed attackId, uint256 attackerId);
    event VrfRequested(uint256 indexed attackId, uint256 indexed requestId);

    event VrfConfigUpdated(
        bytes32 keyHash,
        uint64 subId,
        uint16 minConf,
        uint32 gasLimit,
        uint32 numWords,
        bool nativePayment
    );

    /* -------------------------------- Constructor ------------------------------ */

    constructor(
        address _vrfCoordinator,
        uint256 _subId,
        bytes32 _keyHash,
        uint32 _vrfCallbackGasLimit
    ) GelatoRelayContextERC2771() VRFConsumerBaseV2Plus(_vrfCoordinator) {
        vrfKeyHash = _keyHash;
        vrfSubId = _subId;
        vrfCallbackGasLimit = _vrfCallbackGasLimit;
    }

    /* ---------------------------- Owner Admin Setters ------------------------- */

    function settings(
        address _infrastructure,
        address _forces,
        address _military,
        address _nationStrength,
        address _wonders1,
        address _wonders2,
        address _treasury,
        address _parameters,
        address _missiles,
        address _countryMinter
    ) public onlyOwner {
        inf = IInfrastructure(_infrastructure);
        force = IForces(_forces);
        mil = IMilitary(_military);
        strength = INationStrength(_nationStrength);
        won1 = IWonders1(_wonders1);
        won2 = IWonders2(_wonders2);
        tsy = ITreasury(_treasury);
        params = ICountryParameters(payable(_parameters));
        mis = IMissiles(_missiles);
        mint = ICountryMinter(_countryMinter);
    }

    function settings2(address _keeper, address _spies) public onlyOwner {
        keep = IKeeper(_keeper);
        spy = ISpies(_spies);
    }

    function setVRFConfig(
        bytes32 _keyHash,
        uint64 _subId,
        uint16 _minConf,
        uint32 _gasLimit,
        uint32 _numWords,
        bool _useNative
    ) external onlyOwner {
        vrfKeyHash = _keyHash;
        vrfSubId = _subId;
        vrfMinConfirmations = _minConf;
        vrfCallbackGasLimit = _gasLimit;
        vrfNumWords = _numWords;
        useNativePayment = _useNative;
        emit VrfConfigUpdated(
            _keyHash,
            _subId,
            _minConf,
            _gasLimit,
            _numWords,
            _useNative
        );
    }

    /* ------------------------------ Public Getters ---------------------------- */

    function attackedAlready(uint256 defenderId) external view returns (bool) {
        uint256 day = keep.getGameDay();
        DaySpies storage def = _daySpyCount[day][defenderId];
        return def.incoming >= MAX_INCOMING;
    }

    function getAttackPublic(
        uint256 id
    )
        external
        view
        returns (
            uint256 attackerIdOrMask,
            uint256 defenderId,
            uint8 attackType,
            bool success,
            bool revealed,
            bool resolved
        )
    {
        AttackMeta storage m = attacks[id];
        require(m.resolved, "NOT_RESOLVED");
        uint256 shown = (m.success && !m.revealed)
            ? MASK_SENTINEL
            : m.attackerId;
        return (
            shown,
            m.defenderId,
            m.attackType,
            m.success,
            m.revealed,
            m.resolved
        );
    }

    event VrfRetried(
        uint256 indexed attackId,
        uint256 indexed oldRequestId,
        uint256 indexed newRequestId
    );
    event VrfRequestCleared(
        uint256 indexed attackId,
        uint256 indexed requestId
    );
    event VrfStaleResponseIgnored(
        uint256 indexed attackId,
        uint256 indexed staleRequestId
    );

    error RequestPending(uint256 attackId, uint256 retryAfter);
    error UnknownRequest(uint256 requestId);

    mapping(uint256 => bool) public pendingRequests; // attackId → in-flight?
    mapping(uint256 => uint256) public pendingRequestTimestamp; // attackId → block.timestamp
    mapping(uint256 => uint256) public attackIdToRequestId; // attackId → latest requestId     // requestId → attackId

    uint256 public constant RETRY_TIMEOUT = 5 minutes;

    /* helper: test if retry window elapsed */
    function canRetry(uint256 _attackId) public view returns (bool) {
        if (!pendingRequests[_attackId]) return true;
        return
            block.timestamp >=
            pendingRequestTimestamp[_attackId] + RETRY_TIMEOUT;
    }

    /* external retry entry -- make open or gated as you prefer */
    function retrySpyAttackVRF(uint256 _attackId) external onlyOwner {

        require(
            pendingRequests[_attackId] && canRetry(_attackId),
            "Retry not yet available"
        );
        _issueVRFRequest(_attackId);
    }

    function initiateSpyAttack(
        uint256 attackerId,
        uint256 defenderId,
        uint8 attackType
    ) external onlyGelatoRelayERC2771 returns (uint256 newAttackId) {
        require(
            attackType >= MIN_ATTACK_TYPE && attackType <= MAX_ATTACK_TYPE,
            "attackType"
        );
        require(attackerId != defenderId, "SELF");
        address user = _getMsgSender();
        require(mint.checkOwnership(attackerId, user), "!owner");

        _registerSpyOp(attackerId, defenderId);
        require(_internalCheckSpyOperation(defenderId, attackType), "!allowed");

        uint256 defenderStrength = strength.getNationStrength(defenderId);
        uint256 cost = _calculateSpyOpCost(attackType, defenderStrength);
        tsy.spendBalance(attackerId, cost);

        pending[attackId] = PendingAttack({
            attackerId: attackerId,
            defenderId: defenderId,
            attackType: attackType,
            exists: true
        });

        _issueVRFRequest(attackId);

        emit SpyAttackCommitted(attackId, defenderId, attackType);
        newAttackId = attackId;
        attackId++;
    }

    function _issueVRFRequest(uint256 _attackId) internal {
        if (
            pendingRequests[_attackId] &&
            block.timestamp < pendingRequestTimestamp[_attackId] + RETRY_TIMEOUT
        ) {
            revert RequestPending(
                _attackId,
                pendingRequestTimestamp[_attackId] + RETRY_TIMEOUT
            );
        }

        uint256 oldReqId = attackIdToRequestId[_attackId];
        uint256 newReqId = _requestRandomness(); // below

        vrfRequestToAttackId[newReqId] = _attackId;
        attackIdToRequestId[_attackId] = newReqId;
        pendingRequests[_attackId] = true;
        pendingRequestTimestamp[_attackId] = block.timestamp;

        if (oldReqId != 0 && oldReqId != newReqId) {
            emit VrfRetried(_attackId, oldReqId, newReqId);
        } else {
            emit VrfRequested(_attackId, newReqId);
        }
    }

    /* -------------------------- VRF Request Helper ---------------------------- */

    function _requestRandomness() internal returns (uint256 reqId) {
        VRFV2PlusClient.RandomWordsRequest memory req = VRFV2PlusClient
            .RandomWordsRequest({
                keyHash: vrfKeyHash,
                subId: vrfSubId,
                requestConfirmations: vrfMinConfirmations,
                callbackGasLimit: vrfCallbackGasLimit,
                numWords: vrfNumWords,
                extraArgs: VRFV2PlusClient._argsToBytes(
                    VRFV2PlusClient.ExtraArgsV1({
                        nativePayment: useNativePayment
                    })
                )
            });

        reqId = s_vrfCoordinator.requestRandomWords(req);
    }

    /* -------------------------- VRF Fulfillment (v2.5) ------------------------ */

    /**
     * @dev VRFConsumerBaseV2Plus internal override.
     * NOTE: randomWords length = vrfNumWords.
     */
    function fulfillRandomWords(
        uint256 requestId,
        uint256[] calldata randomWords
    ) internal override {
        uint256 _attackId = vrfRequestToAttackId[requestId];

        /* reject totally unknown requests */
        if (
            vrfRequestToAttackId[requestId] == 0 &&
            requestId != attackIdToRequestId[0]
        ) revert UnknownRequest(requestId);

        /* ignore stale fulfillments */
        if (
            attackIdToRequestId[_attackId] != requestId ||
            !pendingRequests[_attackId]
        ) {
            emit VrfStaleResponseIgnored(_attackId, requestId);
            return;
        }

        uint256 id = vrfRequestToAttackId[requestId];
        require(pending[id].exists, "no pending");

        PendingAttack memory p = pending[id];
        delete pending[id];
        delete vrfRequestToAttackId[requestId];

        // Compute success
        uint256 atkScore = getAttackerSuccessScore(p.attackerId);
        uint256 defScore = getDefenseSuccessScore(p.defenderId);
        uint256 total = atkScore + defScore;
        bool success = false;
        if (total > 0) {
            success = (randomWords[0] % total) < atkScore;
        }

        attacks[id] = AttackMeta({
            attackerId: p.attackerId,
            defenderId: p.defenderId,
            attackType: p.attackType,
            success: success,
            revealed: !success,
            resolved: true
        });

        if (success) {
            uint256 effectRand = uint256(
                keccak256(
                    abi.encode(randomWords[0], id, p.attackerId, p.defenderId)
                )
            );
            _applySpyEffects(
                p.attackerId,
                p.defenderId,
                p.attackType,
                effectRand
            );
            emit SpyAttackResolvedPublic(
                id,
                MASK_SENTINEL,
                p.defenderId,
                true,
                p.attackType
            );
        } else {
            emit SpyAttackResolvedPublic(
                id,
                p.attackerId,
                p.defenderId,
                false,
                p.attackType
            );
        }

        delete pendingRequests[_attackId];
        delete pendingRequestTimestamp[_attackId];
        delete attackIdToRequestId[_attackId];
        delete vrfRequestToAttackId[requestId];

        emit VrfRequestCleared(_attackId, requestId);
    }

    /* ------------------------------ Reveal Success ---------------------------- */

    function revealAttack(uint256 id) external {
        AttackMeta storage m = attacks[id];
        require(m.resolved, "!resolved");
        require(m.success, "only success");
        require(!m.revealed, "revealed");
        require(
            mint.checkOwnership(m.attackerId, _getMsgSender()) ||
                msg.sender == owner(),
            "!auth"
        );
        m.revealed = true;
        emit SpyAttackRevealed(id, m.attackerId);
    }

    /* ------------------------- Daily Cap Registration ------------------------- */

    function _registerSpyOp(uint256 attackerId, uint256 defenderId) internal {
        uint256 day = keep.getGameDay();

        DaySpies storage atk = _daySpyCount[day][attackerId];
        require(atk.outgoing < MAX_OUTGOING, "atk cap");
        atk.outgoing += 1;

        DaySpies storage def = _daySpyCount[day][defenderId];
        require(def.incoming < MAX_INCOMING, "def cap");
        def.incoming += 1;
    }

    /* ---------------------- Operation Allowance (internal) -------------------- */

    function _internalCheckSpyOperation(
        uint256 defenderId,
        uint8 attackType
    ) internal view returns (bool) {
        uint256 infra = inf.getInfrastructureCount(defenderId);
        uint256 tech = inf.getTechnologyCount(defenderId);
        uint256 land = inf.getLandCount(defenderId);

        if (attackType == 4) require(land >= 15, "land<15");
        if (attackType == 10) require(tech >= 15, "tech<15");
        if (attackType == 11) require(infra >= 15, "infra<15");
        if (attackType == 12) {
            uint256 nukeCount = mis.getNukeCount(defenderId);
            bool silo = won2.getHiddenNuclearMissileSilo(defenderId);
            if (silo) require(nukeCount >= 6, "nukes<6");
            else require(nukeCount >= 1, "nukes<1");
        }
        return true;
    }

    /* --------------------------- Success Score Logic -------------------------- */

    function getAttackerSuccessScore(
        uint256 countryId
    ) public view returns (uint256) {
        uint256 spyCount = spy.getSpyCount(countryId);
        uint256 techAmount = inf.getTechnologyCount(countryId);
        uint256 score = spyCount + (techAmount / 15);
        if (won1.getCentralIntelligenceAgency(countryId)) {
            score = (score * 110) / 100;
        }
        if (_accommodativeGov(countryId)) {
            score = (score * 110) / 100;
        }
        return score;
    }

    function getDefenseSuccessScore(
        uint256 countryId
    ) public view returns (uint256) {
        uint256 spyCount = spy.getSpyCount(countryId);
        uint256 techAmount = inf.getTechnologyCount(countryId);
        uint256 landAmount = inf.getLandCount(countryId);
        uint256 threat = mil.getThreatLevel(countryId);

        uint256 gross = spyCount + (techAmount / 20) + (landAmount / 70);
        if (threat == 1) return (gross * 75) / 100;
        if (threat == 2) return (gross * 90) / 100;
        if (threat == 3) return gross;
        if (threat == 4) return (gross * 110) / 100;
        if (threat == 4) return (gross * 125) / 100;
        return (gross * 125) / 100;
    }

    function _accommodativeGov(uint256 id) internal view returns (bool) {
        uint256 g = params.getGovernmentType(id);
        return (g == 2 || g == 7 || g == 10);
    }

    /* ------------------------------ Cost Function ----------------------------- */

    function _calculateSpyOpCost(
        uint8 attackType,
        uint256 defenderStrength
    ) internal pure returns (uint256) {
        if (attackType == 1) return 100_000 + defenderStrength;
        if (attackType == 2) return 100_000 + defenderStrength * 2;
        if (attackType == 3) return 100_000 + defenderStrength * 3;
        if (attackType == 4) return 100_000 + defenderStrength * 3;
        if (attackType == 5) return 100_000 + defenderStrength * 3;
        if (attackType == 6) return 150_000 + defenderStrength;
        if (attackType == 7) return 150_000 + defenderStrength * 5;
        if (attackType == 8) return 250_000 + defenderStrength * 2;
        if (attackType == 9) return 300_000 + defenderStrength * 2;
        if (attackType == 10) return 100_000 + defenderStrength * 20;
        if (attackType == 11) return 500_000 + defenderStrength * 5;
        if (attackType == 12) return 500_000 + defenderStrength * 15;
        revert("attackType");
    }

    /* ------------------------------- Effect Engine ---------------------------- */

    function _applySpyEffects(
        uint256 /*attackerId*/,
        uint256 defenderId,
        uint8 attackType,
        uint256 seed
    ) internal {
        if (attackType == 1) {
            _destroyCruiseMissiles(defenderId, seed);
        } else if (attackType == 2) {
            _destroyDefendingTanks(defenderId, seed);
        } else if (attackType == 3) {
            _captureLand(defenderId, seed);
        } else if (attackType == 4) {
            _changeDesiredGovernment(defenderId, seed);
        } else if (attackType == 5) {
            _changeDesiredReligion(defenderId, seed);
        } else if (attackType == 6) {
            mil.setThreatLevelFromSpyContract(defenderId, 1);
        } else if (attackType == 7) {
            mil.setDefconLevelFromSpyContract(defenderId, 5);
        } else if (attackType == 8) {
            _destroySpies(defenderId, seed);
        } else if (attackType == 9) {
            _captureTechnology(defenderId, seed);
        } else if (attackType == 10) {
            _sabotageTaxes(defenderId, seed);
        } else if (attackType == 11) {
            _captureInfrastructure(defenderId, seed);
        } else if (attackType == 12) {
            mis.decreaseNukeCountFromSpyContract(defenderId);
        }
    }

    function _randSlice(
        uint256 seed,
        bytes32 tag,
        uint256 modBase
    ) private pure returns (uint256) {
        return uint256(keccak256(abi.encode(seed, tag))) % modBase;
    }

    function _destroyCruiseMissiles(uint256 defenderId, uint256 seed) internal {
        uint256 amount = (_randSlice(seed, "CRUISE", 5) + 1); // 1..5
        mis.decreaseCruiseMissileCount(amount, defenderId);
    }

    function _destroyDefendingTanks(uint256 defenderId, uint256 seed) internal {
        uint256 pct = (_randSlice(seed, "TANKS", 5) + 5); // 5..9%
        uint256 defending = force.getDefendingTankCount(defenderId);
        uint256 delta = (defending * pct) / 100;
        if (delta > 0) force.decreaseDefendingTankCount(delta, defenderId);
    }

    function _captureLand(uint256 defenderId, uint256 seed) internal {
        uint256 amt = (_randSlice(seed, "LAND", 10) + 5); // 5..14
        inf.decreaseLandCountFromSpyContract(defenderId, amt);
    }

    function _changeDesiredGovernment(
        uint256 defenderId,
        uint256 seed
    ) internal {
        uint256 current = params.getGovernmentPreference(defenderId);
        uint256 newPref = (_randSlice(seed, "GOV", 10) + 1); // 1..10
        if (newPref == current) {
            newPref = (current == 1) ? 2 : current - 1;
        }
        params.updateDesiredGovernment(defenderId, newPref);
    }

    function _changeDesiredReligion(uint256 defenderId, uint256 seed) internal {
        uint256 current = params.getReligionPreference(defenderId);
        uint256 newPref = (_randSlice(seed, "REL", 14) + 1); // 1..14
        if (newPref == current) {
            newPref = (current == 1) ? 2 : current - 1;
        }
        params.updateDesiredReligion(defenderId, newPref);
    }

    function _destroySpies(uint256 defenderId, uint256 seed) internal {
        uint256 spyCount = spy.getSpyCount(defenderId);
        uint256 toDestroy = (_randSlice(seed, "SPY", 20) + 1); // 1..20
        if (toDestroy > spyCount) toDestroy = spyCount;
        if (toDestroy > 0) spy.decreaseDefenderSpyCount(toDestroy, defenderId);
    }

    function _captureTechnology(uint256 defenderId, uint256 seed) internal {
        uint256 amt = (_randSlice(seed, "TECH", 10) + 5); // 5..14
        inf.decreaseTechCountFromSpyContract(defenderId, amt);
    }

    function _sabotageTaxes(uint256 defenderId, uint256 seed) internal {
        uint256 newRate = (_randSlice(seed, "TAX", 4) + 20); // 20..23
        inf.setTaxRateFromSpyContract(defenderId, newRate);
    }

    function _captureInfrastructure(uint256 defenderId, uint256 seed) internal {
        uint256 amt = (_randSlice(seed, "INFRA", 10) + 5); // 5..14
        inf.decreaseInfrastructureCountFromSpyContract(defenderId, amt);
    }
}
","keccak256":"0xf6e627338ab983299a02d979f530653f54f919d146e91e15dcc2b306528cccef","license":"BUSL-1.1"},"contracts/Taxes.sol":{"content":"//SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

import "./Infrastructure.sol";
import "./Treasury.sol";
import "./Improvements.sol";
import "./Wonders.sol";
import "./Resources.sol";
import "./CountryParameters.sol";
import "./Forces.sol";
import "./Military.sol";
import "./Crime.sol";
import "./CountryMinter.sol";
import "./KeeperFile.sol";
import "./Environment.sol";
import "./NavyBattle.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "hardhat/console.sol";

///@title TaxesContract
///@author OxSnosh
///@dev this contract inherits from the open zeppelin ownable contract
///@notice this contract will allow a nation owner to collect taxes from their citizens
contract TaxesContract is Ownable, ReentrancyGuard {
    address public countryMinter;
    address public infrastructure;
    address public treasury;
    address public improvements1;
    address public improvements2;
    address public improvements3;
    address public improvements4;
    address public parameters;
    address public wonders1;
    address public wonders2;
    address public wonders3;
    address public wonders4;
    address public resources;
    address public forces;
    address public military;
    address public crime;
    address public additionalTaxes;
    address public bonusResources;
    address public keeper;
    address public environment;
    address public blockade;

    uint16 public taxModifierBP = 10000;

    InfrastructureContract inf;
    TreasuryContract tsy;
    ImprovementsContract1 imp1;
    ImprovementsContract2 imp2;
    ImprovementsContract3 imp3;
    ImprovementsContract4 imp4;
    CountryParametersContract params;
    WondersContract1 won1;
    WondersContract2 won2;
    WondersContract3 won3;
    WondersContract4 won4;
    ResourcesContract res;
    ForcesContract frc;
    MilitaryContract mil;
    CrimeContract crm;
    AdditionalTaxesContract addTax;
    CountryMinter mint;
    BonusResourcesContract bonus;
    KeeperContract keep;
    EnvironmentContract env;
    NavalBlockadeContract blk;

    event TaxesCollected(uint256 indexed id, uint256 indexed amount);
    event TaxModifierUpdated(uint16 newValue);

    ///@dev this function is only callable by the contract owner
    ///@dev this function will be called immediately after contract deployment in order to set contract pointers
    function settings1(
        address _countryMinter,
        address _infrastructure,
        address _treasury,
        address _improvements1,
        address _improvements2,
        address _improvements3,
        address _improvements4,
        address _additionalTaxes,
        address _bonusResources,
        address _keeper,
        address _environment
    ) public onlyOwner {
        countryMinter = _countryMinter;
        mint = CountryMinter(_countryMinter);
        infrastructure = _infrastructure;
        inf = InfrastructureContract(_infrastructure);
        treasury = _treasury;
        tsy = TreasuryContract(_treasury);
        improvements1 = _improvements1;
        imp1 = ImprovementsContract1(_improvements1);
        improvements2 = _improvements2;
        imp2 = ImprovementsContract2(_improvements2);
        improvements3 = _improvements3;
        imp3 = ImprovementsContract3(_improvements3);
        improvements4 = _improvements4;
        imp4 = ImprovementsContract4(_improvements4);
        additionalTaxes = _additionalTaxes;
        addTax = AdditionalTaxesContract(_additionalTaxes);
        bonusResources = _bonusResources;
        bonus = BonusResourcesContract(_bonusResources);
        keeper = _keeper;
        keep = KeeperContract(_keeper);
        environment = _environment;
        env = EnvironmentContract(_environment);
    }

    ///@dev this function is only callable by the contract owner
    ///@dev this function will be called immediately after contract deployment in order to set contract pointers
    function settings2(
        address _parameters,
        address _wonders1,
        address _wonders2,
        address _wonders3,
        address _wonders4,
        address _resources,
        address _forces,
        address _military,
        address _crime,
        address _blockade
    ) public onlyOwner {
        parameters = _parameters;
        params = CountryParametersContract(payable(_parameters));
        wonders1 = _wonders1;
        won1 = WondersContract1(_wonders1);
        wonders2 = _wonders2;
        won2 = WondersContract2(_wonders2);
        wonders3 = _wonders3;
        won3 = WondersContract3(_wonders3);
        wonders4 = _wonders4;
        won4 = WondersContract4(_wonders4);
        resources = _resources;
        res = ResourcesContract(payable(_resources));
        forces = _forces;
        frc = ForcesContract(_forces);
        military = _military;
        mil = MilitaryContract(_military);
        crime = _crime;
        crm = CrimeContract(_crime);
        blockade = _blockade;
        blk = NavalBlockadeContract(_blockade);
    }

    function setTaxModifierBP(uint16 newBP) external onlyOwner {
       taxModifierBP = newBP;
       emit TaxModifierUpdated(newBP);
   }

    ///@dev this is a public function callable only by the nation owner collecting taxes
    ///@notice this function will allow a nation owner to collect taxes from their citizens
    ///@param id this is the nation id of the nation collecting taxes
    function collectTaxes(uint256 id) public nonReentrant {
        bool isOwner = mint.checkOwnership(id, msg.sender);
        require(isOwner, "!nation owner");
        (bool war, ) = mil.getWarPeacePreference(id);
        require(war, "must be ready for war to collct taxes");
        (, uint256 taxesCollectible) = getTaxesCollectible(id);
        inf.toggleCollectionNeededToChangeRate(id);
        tsy.increaseBalanceOnTaxCollection(id, taxesCollectible);
        blk.removeAllBlockadesAgainst(id);
        emit TaxesCollected(id, taxesCollectible);
    }

    ///@dev this is a public view function that will return a nations taxes that are collectible
    ///@notice this function will return a nations taxes collectible
    ///@param id is the nation id of the nation being queried
    ///@return dailyTaxesCollectiblePerCitizen is the tax portion of each citizens income per day
    ///@return taxesCollectible is the amount of taxes that are collectible (daily taxes per citizen * days since last collection * citizen count)
    function getTaxesCollectible(
        uint256 id
    ) public view returns (uint256, uint256) {
        uint256 dailyIncomePerCitizen = getDailyIncome(id);
        uint256 daysSinceLastTaxCollection = tsy.getDaysSinceLastTaxCollection(
            id
        );
        (uint256 citizenCount, ) = inf.getTaxablePopulationCount(id);
        uint256 taxRate = inf.getTaxRate(id);
        uint256 dailyTaxesCollectiblePerCitizen = (dailyIncomePerCitizen *
            taxRate);
        uint256 taxesCollectible = ((dailyTaxesCollectiblePerCitizen *
            daysSinceLastTaxCollection) * citizenCount) * (10 ** 18);
        uint256 mod = 100;
        uint256 percentageReductionForBlockades = blk
            .getBlockadePercentageReduction(id);
        if (percentageReductionForBlockades > 100) {
            percentageReductionForBlockades = 100;
        }
        mod = mod - percentageReductionForBlockades;
        taxesCollectible = ((taxesCollectible * mod) / 100);
        taxesCollectible = (taxesCollectible * taxModifierBP) / 10000;
        return (dailyTaxesCollectiblePerCitizen, taxesCollectible);
    }

    ///@dev this is a public view function that will return the daily gross income per citizen for a given nation
    ///@notice this function will return the gross income per citizen for a given nation
    ///@param id is the nation id of the nation being queried
    ///@return uint256 is the daily income of each citizen for a nation
    function getDailyIncome(uint256 id) public view returns (uint256) {
        uint256 happiness = getHappiness(id);
        //increasers
        uint256 banks = imp1.getBankCount(id);
        uint256 ministries = imp2.getForeignMinistryCount(id);
        uint256 harbors = imp2.getHarborCount(id);
        uint256 schools = imp3.getSchoolCount(id);
        uint256 universityPoints = addTax.getUniversityPoints(id);
        //detractors
        uint256 casinos = imp1.getCasinoCount(id);
        uint256 guerillaCamp = imp2.getGuerillaCampCount(id);
        uint256 multipliers = (100 +
            (banks * 7) +
            (ministries * 5) +
            (harbors * 1) +
            (schools * 5) +
            universityPoints -
            (guerillaCamp * 8) -
            (casinos * 1));
        uint256 baseDailyIncomePerCitizen = (((35 + (2 * happiness)) *
            multipliers) / 100);
        uint256 incomeAdjustments = addTax.getIncomeAdjustments(id);
        uint256 dailyIncomePerCitizen = baseDailyIncomePerCitizen +
            incomeAdjustments;
        return dailyIncomePerCitizen;
    }

    ///@dev this is a publci view function that will return a nations happiness
    ///@notice this function will return a nations happiness
    ///@notice the higher a nations happiness the more money its citizens will make
    ///@param id is the nation id of the nation being queried
    ///@return happiness is the happiness for the queried nation
    function getHappiness(uint256 id) public view returns (uint256) {
        uint256 happinessAdditions = getHappinessPointsToAdd(id);
        uint256 happinessSubtractions = getHappinessPointsToSubtract(id);
        uint256 happiness = 0;
        if (happinessSubtractions >= happinessAdditions) {
            happiness = 0;
        } else {
            happiness = (happinessAdditions - happinessSubtractions);
        }
        return happiness;
    }

    function getHappinessPointsToAdd(uint256 id) public view returns (uint256) {
        uint256 compatabilityPoints = checkCompatability(id);
        uint256 densityPoints = getDensityPoints(id);
        uint256 pointsFromResources = getPointsFromResources(id);
        uint256 pointsFromImprovements = getPointsFromImprovements(id);
        uint256 wonderPoints = getHappinessFromWonders(id);
        uint256 casualtyPoints = getCasualtyPoints(id);
        uint256 additionalHappinessPoints = getAdditionalHappinessPointsToAdd(
            id
        );
        uint256 happinessPointsToAdd = (compatabilityPoints +
            densityPoints +
            pointsFromResources +
            pointsFromImprovements +
            wonderPoints +
            casualtyPoints +
            additionalHappinessPoints);
        return happinessPointsToAdd;
    }

    function getAdditionalHappinessPointsToAdd(
        uint256 id
    ) internal view returns (uint256) {
        uint256 technologyPoints = getTechnologyPoints(id);
        uint256 pointsFromAge = getPointsFromNationAge(id);
        uint256 pointsFromTrades = addTax.getPointsFromTrades(id);
        uint256 pointsFromDefcon = addTax.getPointsFromDefcon(id);
        uint256 pointsFromGovt = addTax.getPointsFromGovernment(id);
        uint256 additonalHappinessPointsToAdd = (technologyPoints +
            pointsFromAge +
            pointsFromTrades +
            pointsFromDefcon +
            pointsFromGovt);
        return additonalHappinessPointsToAdd;
    }

    function getHappinessPointsToSubtract(
        uint256 id
    ) public view returns (uint256) {
        uint256 taxRatePoints = getTaxRatePoints(id);
        uint256 pointsFromStability = addTax.getPointsFromMilitary(id);
        uint256 pointsFromCrime = getPointsFromCriminals(id);
        uint256 pointsFromImprovements = addTax
            .getPointsToSubtractFromImprovements(id);
        uint256 pointsFromIntelAgencies = addTax.getPointsFromIntelAgencies(id);
        uint256 environmentPoints = env.getEnvironmentScore(id);
        uint256 happinessPointsToSubtract = (taxRatePoints +
            pointsFromCrime +
            pointsFromImprovements +
            pointsFromStability +
            pointsFromIntelAgencies +
            environmentPoints);
        return happinessPointsToSubtract;
    }

    function checkCompatability(
        uint256 id
    ) public view returns (uint256 compatability) {
        uint256 religion = params.getReligionType(id);
        uint256 govType = params.getGovernmentType(id);
        uint256 preferredReligion = params.getReligionPreference(id);
        uint256 preferredGovernment = params.getGovernmentPreference(id);
        (bool monument, bool temple, , , ) = wonderChecks1(id);
        uint256 religionPoints;
        uint256 governmentPoints;
        if (religion == preferredReligion || temple) {
            religionPoints = 1;
        }
        if (govType == preferredGovernment || monument) {
            governmentPoints = 1;
        }
        uint256 compatabilityPoints = (religionPoints + governmentPoints);
        return compatabilityPoints;
    }

    function checkPopulationDensity(uint256 id) public view returns (uint256) {
        uint256 landArea = inf.getAreaOfInfluence(id);
        uint256 population = inf.getTotalPopulationCount(id);
        uint256 populationDensity = (population / landArea);
        return populationDensity;
    }

    function getDensityPoints(uint256 id) public view returns (uint256) {
        uint256 densityPoints = 0;
        uint256 density = checkPopulationDensity(id);
        uint256 maxDensity = 70;
        (,,,,,,, bool water,,) = res.getResources2(id);
        // bool water = res.viewWater(id);
        if (water) {
            maxDensity = 120;
        }
        if (density <= maxDensity) {
            densityPoints = 5;
        }
        return densityPoints;
    }

    function getPointsFromResources(uint256 id) public view returns (uint256) {
        uint256 pointsFromResources = 0;
        (,,,,, bool gems,,,,,) = res.getResources1(id);
        (bool oil,,, bool silver, bool spices, bool sugar,, bool water,, bool wine) = res.getResources2(id);
        // bool gems = res.viewGems(id);
        if (gems) {
            pointsFromResources += 3;
        }
        // bool oil = res.viewOil(id);
        if (oil) {
            pointsFromResources += 2;
        }
        // bool silver = res.viewSilver(id);
        if (silver) {
            pointsFromResources += 2;
        }
        // bool spices = res.viewSpices(id);
        if (spices) {
            pointsFromResources += 2;
        }
        // bool sugar = res.viewSugar(id);
        if (sugar) {
            pointsFromResources += 1;
        }
        // bool water = res.viewWater(id);
        if (water) {
            pointsFromResources += 3;
        }
        // bool wine = res.viewWine(id);
        if (wine) {
            pointsFromResources += 3;
        }
        bool beer = bonus.viewBeer(id);
        if (beer) {
            pointsFromResources += 2;
        }
        bool fastFood = bonus.viewFastFood(id);
        if (fastFood) {
            pointsFromResources += 2;
        }
        bool fineJewelry = bonus.viewFineJewelry(id);
        if (fineJewelry) {
            pointsFromResources += 3;
        }
        uint256 additionalPoints = getAdditionalPointsFromResources(id);
        pointsFromResources += additionalPoints;
        return pointsFromResources;
    }

    function getAdditionalPointsFromResources(
        uint256 id
    ) public view returns (uint256) {
        uint256 additionalPointsFromResources;
        bool automobiles = bonus.viewAutomobiles(id);
        if (automobiles) {
            additionalPointsFromResources += 3;
        }
        bool microchips = bonus.viewMicrochips(id);
        if (microchips) {
            additionalPointsFromResources += 2;
        }
        return additionalPointsFromResources;
    }

    function getPointsFromImprovements(
        uint256 id
    ) public view returns (uint256) {
        uint256 pointsFromImprovements;
        uint256 borderWalls = imp1.getBorderWallCount(id);
        if (borderWalls > 0) {
            pointsFromImprovements += (2 * borderWalls);
        }
        uint256 casinos = imp1.getCasinoCount(id);
        if (casinos > 0) {
            pointsFromImprovements += (2 * casinos);
        }
        uint256 churchCount = imp1.getChurchCount(id);
        if (churchCount > 0) {
            pointsFromImprovements += churchCount;
        }
        uint256 policeHeadquarters = imp4.getPoliceHeadquartersCount(id);
        if (policeHeadquarters > 0) {
            pointsFromImprovements += (2 * policeHeadquarters);
        }
        uint256 redLightDistricts = imp3.getRedLightDistrictCount(id);
        if (redLightDistricts > 0) {
            pointsFromImprovements += (redLightDistricts);
        }
        uint256 stadiums = imp3.getStadiumCount(id);
        if (stadiums > 0) {
            pointsFromImprovements += (3 * stadiums);
        }
        uint256 taxRate = inf.getTaxRate(id);
        uint256 intelAgencies = imp2.getIntelAgencyCount(id);
        if (taxRate >= 23) {
            pointsFromImprovements += intelAgencies;
        }
        return pointsFromImprovements;
    }

    function getHappinessFromWonders(
        uint256 id
    ) public view returns (uint256 wonderPts) {
        (
            bool monument,
            bool temple,
            bool greatUniversity,
            bool internet,
            bool movieIndustry
        ) = wonderChecks1(id);
        (
            bool warMemorial,
            bool scientificDevCenter,
            bool spaceProgram,
            bool universalHealthcare
        ) = wonderChecks2(id);
        uint256 wonderPoints = 0;
        if (monument) {
            wonderPoints += 4;
        }
        if (temple) {
            wonderPoints += 5;
        }
        uint256 tech = inf.getTechnologyCount(id);
        uint256 techDivided = (tech / 1000);
        if (greatUniversity && !scientificDevCenter) {
            uint256 points;
            if (techDivided == 0) {
                points = 0;
            } else if (techDivided == 1) {
                points = 1;
            } else if (techDivided == 2) {
                points = 2;
            } else {
                points = 3;
            }
            wonderPoints += points;
        } else if (greatUniversity && scientificDevCenter) {
            uint256 points;
            if (techDivided == 0) {
                points = 0;
            } else if (techDivided == 1) {
                points = 1;
            } else if (techDivided == 2) {
                points = 2;
            } else if (techDivided == 3) {
                points = 3;
            } else if (techDivided == 4) {
                points = 4;
            } else {
                points = 5;
            }
            wonderPoints += points;
        }
        if (internet) {
            wonderPoints += 5;
        }
        if (movieIndustry) {
            wonderPoints += 3;
        }
        if (warMemorial) {
            wonderPoints += 4;
        }
        if (spaceProgram) {
            wonderPoints += 3;
        }
        if (universalHealthcare) {
            wonderPoints += 2;
        }
        return wonderPoints;
    }

    function wonderChecks1(
        uint256 id
    ) internal view returns (bool, bool, bool, bool, bool) {
        bool isMonument = won2.getGreatMonument(id);
        bool isTemple = won2.getGreatTemple(id);
        bool isUniversity = won2.getGreatUniversity(id);
        bool isInternet = won2.getInternet(id);
        bool isMovieIndustry = won3.getMovieIndustry(id);

        return (
            isMonument,
            isTemple,
            isUniversity,
            isInternet,
            isMovieIndustry
        );
    }

    function wonderChecks2(
        uint256 id
    ) internal view returns (bool, bool, bool, bool) {
        bool isWarMemorial = won3.getNationalWarMemorial(id);
        bool isScientificDevCenter = won3.getScientificDevelopmentCenter(id);
        bool isSpaceProgram = won4.getSpaceProgram(id);
        bool isUniversalHealthcare = won4.getUniversalHealthcare(id);
        return (
            isWarMemorial,
            isScientificDevCenter,
            isSpaceProgram,
            isUniversalHealthcare
        );
    }

    function getCasualtyPoints(uint256 id) public view returns (uint256) {
        bool nationalCemetary = won3.getNationalCemetary(id);
        (uint256 casualties, ) = frc.getCasualties(id);
        uint256 casualtyPoints = 0;
        if (nationalCemetary) {
            if (casualties < 10000000) {
                casualtyPoints = 1;
            } else if (casualties < 15000000) {
                casualtyPoints = 2;
            } else if (casualties < 20000000) {
                casualtyPoints = 3;
            } else if (casualties < 25000000) {
                casualtyPoints = 4;
            } else if (casualties >= 25000000) {
                casualtyPoints = 5;
            }
        }
        return casualtyPoints;
    }

    function getTechnologyPoints(uint256 id) public view returns (uint256) {
        uint256 pointsFromTechnology;
        uint256 tech = inf.getTechnologyCount(id);
        if (tech == 0) {
            pointsFromTechnology = 0;
        } else if (tech == 1) {
            pointsFromTechnology = 1;
        } else if (tech <= 3) {
            pointsFromTechnology = 2;
        } else if (tech <= 6) {
            pointsFromTechnology = 3;
        } else if (tech <= 10) {
            pointsFromTechnology = 4;
        } else if (tech <= 15) {
            pointsFromTechnology = 5;
        } else if (tech <= 50) {
            pointsFromTechnology = 6;
        } else if (tech <= 100) {
            pointsFromTechnology = 7;
        } else if (tech <= 150) {
            pointsFromTechnology = 8;
        } else if (tech <= 200) {
            pointsFromTechnology = 9;
        } else {
            pointsFromTechnology = 10;
        }
        return pointsFromTechnology;
    }

    function getPointsFromNationAge(uint256 id) public view returns (uint256) {
        uint256 nationCreated = params.getDayCreated(id);
        uint256 gameDay = keep.getGameDay();
        uint256 agePoints = 0;
        if ((gameDay - nationCreated) < 90) {
            agePoints = 0;
        } else if ((gameDay - nationCreated) < 180) {
            agePoints = 2;
        } else {
            agePoints = 4;
        }
        return agePoints;
    }

    function getTaxRatePoints(uint256 id) public view returns (uint256) {
        uint256 subtractTaxPoints;
        uint256 taxRate = inf.getTaxRate(id);
        if (taxRate <= 16) {
            subtractTaxPoints = 0;
        } else if (taxRate <= 20) {
            subtractTaxPoints = 1;
        } else if (taxRate <= 23) {
            subtractTaxPoints = 3;
        } else if (taxRate <= 25) {
            subtractTaxPoints = 5;
        } else if (taxRate <= 30) {
            subtractTaxPoints = 7;
        }
        return subtractTaxPoints;
    }

    function getPointsFromCriminals(uint256 id) public view returns (uint256) {
        (uint256 unincarceratedCriminals, , ) = crm.getCriminalCount(id);
        uint256 pointsFromCrime;
        if (unincarceratedCriminals < 200) {
            pointsFromCrime = 0;
        } else if (unincarceratedCriminals < 2000) {
            pointsFromCrime = 1;
        } else if (unincarceratedCriminals < 4000) {
            pointsFromCrime = 2;
        } else if (unincarceratedCriminals < 6000) {
            pointsFromCrime = 3;
        } else if (unincarceratedCriminals < 8000) {
            pointsFromCrime = 4;
        } else {
            pointsFromCrime = 5;
        }
        return pointsFromCrime;
    }
}

///@title AdditionalTaxesContract
///@author OxSnosh
///@dev tis contract inherits from openzeppelin's ownable contract
///@notice this contract will have additional formulas that will allow a nation to collect taxes from its citizens
contract AdditionalTaxesContract is Ownable {
    address public infrastructure;
    address public improvements2;
    address public improvements3;
    address public parameters;
    address public wonders1;
    address public wonders2;
    address public wonders3;
    address public wonders4;
    address public resources;
    address public military;
    address public bonusResources;
    address public forces;

    InfrastructureContract inf;
    ImprovementsContract2 imp2;
    ImprovementsContract3 imp3;
    CountryParametersContract params;
    WondersContract1 won1;
    WondersContract2 won2;
    WondersContract3 won3;
    WondersContract4 won4;
    ResourcesContract res;
    MilitaryContract mil;
    BonusResourcesContract bonus;
    ForcesContract frc;

    ///@dev this function is only callable by the contract owner
    ///@dev this function will be called immediately after contract deployment in order to set contract pointers
    function settings(
        address _parameters,
        address _wonders1,
        address _wonders2,
        address _wonders3,
        address _wonders4,
        address _resources,
        address _military,
        address _infrastructure,
        address _bonusResources
    ) public onlyOwner {
        parameters = _parameters;
        params = CountryParametersContract(payable(_parameters));
        wonders1 = _wonders1;
        won1 = WondersContract1(_wonders1);
        wonders2 = _wonders2;
        won2 = WondersContract2(_wonders2);
        wonders3 = _wonders3;
        won3 = WondersContract3(_wonders3);
        wonders4 = _wonders4;
        won4 = WondersContract4(_wonders4);
        resources = _resources;
        res = ResourcesContract(payable(_resources));
        military = _military;
        mil = MilitaryContract(_military);
        infrastructure = _infrastructure;
        inf = InfrastructureContract(_infrastructure);
        bonusResources = _bonusResources;
        bonus = BonusResourcesContract(_bonusResources);
    }

    ///@dev this function is only callable by the contract owner
    ///@dev this function will be called immediately after contract deployment in order to set contract pointers
    function settings2(
        address _improvements2,
        address _improvements3,
        address _forces
    ) public onlyOwner {
        improvements2 = _improvements2;
        imp2 = ImprovementsContract2(_improvements2);
        improvements3 = _improvements3;
        imp3 = ImprovementsContract3(_improvements3);
        forces = _forces;
        frc = ForcesContract(_forces);
    }

    function getIncomeAdjustments(uint256 id) public view returns (uint256) {
        uint256 adjustments = 0;
        (,,,, bool furs, bool gems, bool gold,,,, ) = res.getResources1(id);
        (,,, bool silver,,,,,,) = res.getResources2(id);
        if (furs) {
            adjustments += 4;
        }
        if (gems) {
            adjustments += 2;
        }
        if (gold) {
            adjustments += 3;
        }
        if (silver) {
            adjustments += 2;
        }
        bool scholars = bonus.viewScholars(id);
        if (scholars) {
            adjustments += 3;
        }
        bool agriDevProgram = won1.getAgriculturalDevelopmentProgram(id);
        if (agriDevProgram) {
            adjustments += 2;
        }
        bool miningIndustryConsortium = won2.getMiningIndustryConsortium(id);
        if (miningIndustryConsortium) {
            uint256 points = getResourcePointsForMiningConsortium(id);
            adjustments += (2 * points);
        }
        bool stockMarket = won4.getStockMarket(id);
        if (stockMarket) {
            adjustments += 10;
        }
        uint256 uraniumAndNuclearPowerBonus = getNuclearAndUraniumBonus(id);
        adjustments += uraniumAndNuclearPowerBonus;
        return adjustments;
    }

    function getResourcePointsForMiningConsortium(
        uint256 id
    ) public view returns (uint256) {
        uint256 points = 0;
        (,, bool coal,,,,,, bool lead,,) = res.getResources1(id);
        (bool oil,,,,,, bool uranium,,,) = res.getResources2(id);
        if (coal) {
            points += 1;
        }
        if (lead) {
            points += 1;
        }
        if (oil) {
            points += 1;
        }
        if (uranium) {
            points += 1;
        }
        return points;
    }

    function getNuclearAndUraniumBonus(
        uint256 id
    ) public view returns (uint256) {
        bool nuclearPowerPlant = won3.getNuclearPowerPlant(id);
        (,,,,,, bool uranium,,,) = res.getResources2(id);
        uint256 techAmount = inf.getTechnologyCount(id);
        uint256 adjustment = 0;
        if (nuclearPowerPlant && uranium) {
            adjustment += 3;
            if (techAmount >= 10 && techAmount < 20) {
                adjustment += 1;
            } else if (techAmount >= 20 && techAmount < 30) {
                adjustment += 2;
            } else if (techAmount >= 30 && techAmount < 40) {
                adjustment += 3;
            } else if (techAmount >= 40) {
                adjustment += 4;
            }
        }
        return adjustment;
    }

    function getPointsFromTrades(uint256 id) public view returns (uint256) {
        uint256[] memory partners = res.getTradingPartners(id);
        uint256 pointsFromTeamTrades = 0;
        uint256 callerNationTeam = params.getTeam(id);
        for (uint256 i = 0; i < partners.length; i++) {
            uint256 partnerId = partners[i];
            uint256 partnerTeam = params.getTeam(partnerId);
            if (callerNationTeam == partnerTeam) {
                pointsFromTeamTrades++;
            }
        }
        return pointsFromTeamTrades;
    }

    function getPointsFromDefcon(uint256 id) public view returns (uint256) {
        uint256 defconLevel = mil.getDefconLevel(id);
        return (defconLevel - 1);
    }

    function getPointsToSubtractFromImprovements(
        uint256 id
    ) public view returns (uint256) {
        uint256 pointsToSubtractFromImprovements;
        uint256 laborCamps = imp2.getLaborCampCount(id);
        if (laborCamps > 0) {
            pointsToSubtractFromImprovements += (laborCamps * 1);
        }
        return pointsToSubtractFromImprovements;
    }

    function getUniversityPoints(uint256 id) public view returns (uint256) {
        uint256 universities = imp3.getUniversityCount(id);
        uint256 universityPoints = 0;
        bool scientificDevelopmentCenter = won3.getScientificDevelopmentCenter(
            id
        );
        if (universities > 0 && !scientificDevelopmentCenter) {
            universityPoints = (universities * 8);
        } else if (universities > 0 && scientificDevelopmentCenter) {
            universityPoints = (universities * 10);
        }
        return universityPoints;
    }

    function getPointsFromGovernment(uint256 id) public view returns (uint256) {
        uint256 governmentType = params.getGovernmentType(id);
        uint256 pointsFromGovernmentType = 0;
        if (
            governmentType == 3 ||
            governmentType == 6 ||
            governmentType == 8 ||
            governmentType == 9
        ) {
            pointsFromGovernmentType = 1;
        }
        return pointsFromGovernmentType;
    }

    function getPointsFromMilitary(uint256 id) public view returns (uint256) {
        (uint256 ratio, , ) = soldierToPopulationRatio(id);
        if (ratio > 70) {
            return 10;
        } else if (ratio < 10) {
            return 14;
        } else if (ratio < 20) {
            return 5;
        } else {
            return 0;
        }
    }

    function soldierToPopulationRatio(
        uint256 id
    ) public view returns (uint256, bool, bool) {
        uint256 soldierCount = frc.getSoldierCount(id);
        if (soldierCount == 0) {
            return (0, false, true);
        }
        uint256 populationCount = inf.getTotalPopulationCount(id);
        uint256 soldierPopulationRatio = (
            ((soldierCount * 100) / populationCount)
        );
        bool environmentPenalty = false;
        bool anarchyCheck = false;
        if (soldierPopulationRatio > 60) {
            environmentPenalty = true;
        }
        if (soldierPopulationRatio < 10) {
            anarchyCheck = true;
        }
        return (soldierPopulationRatio, environmentPenalty, anarchyCheck);
    }

    function getPointsFromIntelAgencies(
        uint256 id
    ) public view returns (uint256) {
        uint256 intelAgencies = imp2.getIntelAgencyCount(id);
        uint256 subtractPoints;
        uint256 taxRate = inf.getTaxRate(id);
        if (taxRate <= 20) {
            subtractPoints = 0;
        } else if (intelAgencies >= 1 && taxRate > 20 && taxRate <= 23) {
            subtractPoints = 1;
        } else if (intelAgencies >= 1 && taxRate > 23) {
            subtractPoints = intelAgencies;
        } else {
            subtractPoints = 0;
        }
        return subtractPoints;
    }
}
","keccak256":"0x658595f2bd1e07827f92b6a57bf2305caeffa2f53deb7f539ccb433888221ee4","license":"BUSL-1.1"},"contracts/Treasury.sol":{"content":"//SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

import "./IWarBucks.sol";
import "./WarBucks.sol";
import "./Wonders.sol";
import "./Infrastructure.sol";
import "./Forces.sol";
import "./Navy.sol";
import "./Fighters.sol";
import "./CountryMinter.sol";
import "./GroundBattle.sol";
import "./KeeperFile.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

///@title TreasuryContract
///@author OxSnosh
///@dev this contract allows a nation owner to withdraw game revenues from their nation
///@dev this contract allows a nation owner to deposit game revenues into their nation
///@dev this contract inherits from the openzeppelin ownable contract
///@dev this contract allows the game owner to set the MILF and withdraw game revenues
contract TreasuryContract is Ownable, ReentrancyGuard {
    uint256 public totalGameBalance;
    uint256 public counter;
    address public wonders1;
    address public wonders2;
    address public wonders3;
    address public wonders4;
    address public improvements1;
    address public improvements2;
    address public improvements3;
    address public improvements4;
    address public infrastructure;
    address public navy;
    address public navy2;
    address public fighters;
    address public bombers;
    address public fightersMarket1;
    address public fightersMarket2;
    address public bombersMarket1;
    address public bombersMarket2;
    address public warBucksAddress;
    address public forces;
    address public spies;
    address public missiles;
    address public aid;
    address public taxes;
    address public bills;
    address public spyOperations;
    address public groundBattle;
    address public countryMinter;
    address public landMarket;
    address public techMarket;
    address public infrastructureMarket;
    address public keeper;
    address public parameters;
    uint256 public daysToInactive = 30;
    uint256 public maxDaysOfTaxes = 20;
    uint256 private silt = 100;

    CountryMinter mint;
    GroundBattleContract ground;
    KeeperContract keep;

    struct Treasury {
        bool initialized;
        uint256 dayOfLastBillPaid;
        uint256 dayOfLastTaxCollection;
        bool demonitized;
    }

    mapping(uint256 => Treasury) public idToTreasury;

    event FundsWithdrawn(
        uint256 countryId,
        uint256 amount
    );

    event FundsAdded(
        uint256 countryId,
        uint256 amount
    );

    event OwnerWithdrawMilfRevenues(
        uint256 amount
    );

    event SeedMoneyUpdated(
        uint256 newSeedMoney
    );

    event SpoilsTransferred(
        uint256 attackerId,
        uint256 defenderId,
        uint256 fundsTransferred
    );

    WarBucks public wbx;

    ///@dev this function is only callable by the contract owner
    ///@dev this function will be called immediately after contract deployment in order to set contract pointers
    function settings1(
        address _warBucksAddress,
        address _wonders1,
        address _wonders2,
        address _wonders3,
        address _wonders4,
        address _improvements1,
        address _improvements2,
        address _improvements3,
        address _improvements4,
        address _infrastructure
    ) public onlyOwner {
        warBucksAddress = _warBucksAddress;
        wbx = WarBucks(warBucksAddress);
        wonders1 = _wonders1;
        wonders2 = _wonders2;
        wonders3 = _wonders3;
        wonders4 = _wonders4;
        improvements1 = _improvements1;
        improvements2 = _improvements2;
        improvements3 = _improvements3;
        improvements4 = _improvements4;
        infrastructure = _infrastructure;
    }

    ///@dev this function is only callable by the contract owner
    ///@dev this function will be called immediately after contract deployment in order to set contract pointers
    function settings2(
        address _groundBattle,
        address _countryMinter,
        address _keeper,
        address _forces,
        address _navy,
        address _fighters,
        address _bombers,
        address _aid,
        address _taxes,
        address _bills,
        address _spyOperations
    ) public onlyOwner {
        groundBattle = _groundBattle;
        ground = GroundBattleContract(_groundBattle);
        countryMinter = _countryMinter;
        mint = CountryMinter(_countryMinter);
        keeper = _keeper;
        keep = KeeperContract(_keeper);
        forces = _forces;
        navy = _navy;
        fighters = _fighters;
        bombers = _bombers;
        aid = _aid;
        taxes = _taxes;
        bills = _bills;
        spyOperations = _spyOperations;
    }

    function settings3(
        address _navy2,
        address _missiles,
        address _infrastructureMarket,
        address _landMarket,
        address _techMarket,
        address _fightersMarket1,
        address _fightersMarket2,
        address _bombersMarket1,
        address _bombersMarket2,
        address _parameters,
        address _spies
    ) public onlyOwner {
        navy2 = _navy2;
        missiles = _missiles;
        infrastructureMarket = _infrastructureMarket;
        landMarket = _landMarket;
        techMarket = _techMarket;
        fightersMarket1 = _fightersMarket1;
        fightersMarket2 = _fightersMarket2;
        bombersMarket1 = _bombersMarket1;
        bombersMarket2 = _bombersMarket2;
        parameters = _parameters;
        spies = _spies;
    }

    modifier onlyCountryMinter() {
        require(
            msg.sender == countryMinter,
            "function only callable from country minter contract"
        );
        _;
    }

    ///@dev this function is only callable from the country minter contract
    ///@notice this function will be called when a nation is minted and will allow a nation to undergo treasury operations
    ///@param id is the nation id of the nation being minted
    function generateTreasury(uint256 id) public onlyCountryMinter {
        require(
            idToTreasury[id].initialized == false,
            "treasury already initialized"
        );
        uint256 gameDay = keep.getGameDay();
        Treasury memory newTreasury = Treasury(
            true,
            gameDay,
            gameDay,
            false
        );
        idToTreasury[id] = newTreasury;
        counter++;
    }

    ///@dev this is a public view function that will return a nations in game balance
    ///@notice this function will return a given nations in game balance
    ///@param id is the nation id of the nation being queries
    ///@return uint256 is the balance of war bucks for the nation
    function checkBalance(uint256 id) public view returns (uint256) {
        address owner = mint.idToOwner(id);
        uint256 balance = wbx.balanceOf(owner);       
        return balance;
    }

    ///@dev this funtion is a public view function that will return the number of days it has been since a nation has collected taxes
    ///@notice this funtion will return the number of days it has been since a nation has collected taxes
    ///@param id is the nation id of the nation being queried
    ///@return uint256 is the number of days since a nation has collected taxes
    function getDaysSinceLastTaxCollection(
        uint256 id
    ) public view returns (uint256) {
        uint256 gameDay = keep.getGameDay();
        uint256 dayOfLastTaxCollected = idToTreasury[id].dayOfLastTaxCollection;
        uint256 daysSince = (gameDay - dayOfLastTaxCollected);
        if (daysSince > maxDaysOfTaxes) {
            daysSince = maxDaysOfTaxes;
        }
        return daysSince;
    }

    function getMaxDaysOfTaxes() public view returns (uint256) {
        return maxDaysOfTaxes;
    }

    function setMaxDaysOfTaxes(uint256 newMaxDays) public onlyOwner {
        maxDaysOfTaxes = newMaxDays;
    }

    modifier onlyTaxesContract() {
        require(msg.sender == taxes, "only callable from taxes contract");
        _;
    }

    ///@dev this function is only callable by the taxes contract
    ///@dev this function will increase a nations balance when taxes are collected
    ///@param id this is the nation id of the country collecting taxes
    ///@param amount this is the amount of taxes being collected
    function increaseBalanceOnTaxCollection(
        uint256 id,
        uint256 amount
    ) public onlyTaxesContract {
        address owner = mint.idToOwner(id);
        wbx.mintFromTreasury(owner, amount);
        uint256 day = keep.getGameDay();
        idToTreasury[id].dayOfLastTaxCollection = day;
    }

    ///@dev this funtion is a public view function that will return the number of days it has been since a nation has paid bills
    ///@notice this funtion will return the number of days it has been since a nation has paid bills
    ///@param id is the nation id of the nation being queried
    ///@return uint256 is the number of days since a nation has paid bills
    function getDaysSinceLastBillsPaid(
        uint256 id
    ) public view returns (uint256) {
        uint256 gameDay = keep.getGameDay();
        uint256 dayOfLastBillPaid = idToTreasury[id].dayOfLastBillPaid;
        uint256 daysSince = (gameDay - dayOfLastBillPaid);
        if (daysSince > daysToInactive) {
            daysSince = daysToInactive;
        }
        return daysSince;
    }

    modifier onlyBillsContract() {
        require(msg.sender == bills, "only callable from taxes contract");
        _;
    }

    ///@dev this function is only callable from the bills contract
    ///@dev this function will decrease a nations balance when bils are paid
    ///@param id is the nation id of the nation paying bills
    ///@param amount is the amount of bills being paid
    function decreaseBalanceOnBillsPaid(
        uint256 id,
        uint256 amount
    ) public onlyBillsContract returns (bool) {
        address owner = mint.idToOwner(id);
        wbx.burnFromTreasury(owner, amount);
        uint256 day = keep.getGameDay();
        idToTreasury[id].dayOfLastBillPaid = day;
        return true;
    }

    ///@dev this is a public view function that will return if a nation is inactive
    ///@notice this function will retun if a nation is inactive
    ///@param id is the nation id of the nation being queried
    ///@return bool will be true if the nation is inactive
    function checkInactive(uint256 id) public view returns (bool) {
        uint256 day = keep.getGameDay();
        uint256 dayBillsPaid = idToTreasury[id].dayOfLastBillPaid;
        uint256 elapsed = (day - dayBillsPaid);
        bool inactive = false;
        if (elapsed > daysToInactive) {
            inactive = true;
        }
        return inactive;
    }

    ///@dev this function is only callable from the contract owner
    ///@dev this function will allow the contract owner to set the number of days a nation cannot pay bill until it becomes inactive
    ///@notice this function will allow the contract owner to set the number of days a nation cannot pay bill until it becomes inactive
    function setDaysToInactive(uint256 newDays) public onlyOwner {
        daysToInactive = newDays;
    }

    function getDaysToInactive() public view returns (uint256) {
        return daysToInactive;
    }

    modifier approvedBalanceSpender() {
        require(
            msg.sender == bombers ||
                msg.sender == bombersMarket1 ||
                msg.sender == bombersMarket2 ||
                msg.sender == fightersMarket1 ||
                msg.sender == fightersMarket2 ||
                msg.sender == fighters ||
                msg.sender == forces ||
                msg.sender == missiles ||
                msg.sender == navy ||
                msg.sender == navy2 ||
                msg.sender == improvements1 ||
                msg.sender == improvements2 ||
                msg.sender == improvements3 ||
                msg.sender == improvements4 ||
                msg.sender == wonders1 ||
                msg.sender == wonders2 ||
                msg.sender == wonders3 ||
                msg.sender == wonders4 ||
                msg.sender == infrastructureMarket ||
                msg.sender == techMarket ||
                msg.sender == landMarket ||
                msg.sender == spyOperations ||
                msg.sender == parameters ||
                msg.sender == spies,
            "cannot call spendBalance()"
        );
        _;
    }

    ///@dev this function is public but only callable by contracts within the game where funds are being spent
    ///@dev this function will decrease a nation owner's balance when money is spent within the game
    ///@notice this function will decrease a nation owner's balance when money is spent within the game
    ///@param id is the nation id of the nation spending funds
    ///@param cost is the cost of the expense
    function spendBalance(
        uint256 id,
        uint256 cost
    ) external approvedBalanceSpender returns (bool) {
        uint256 balance = wbx.balanceOf(mint.idToOwner(id));
        require(balance >= cost, "insufficient balance");
        bool demonitized = idToTreasury[id].demonitized;
        require(demonitized == false, "ERROR");
        bool inactive = checkInactive(id);
        require(inactive == false, "ERROR Inactive, pay bills to reactivate");
        wbx.burnFromTreasury(mint.idToOwner(id), cost);
        //TAXES here
        uint256 taxLevied = ((cost * silt) / 100);
        if (taxLevied > 0) {
            IWarBucks(warBucksAddress).mintFromTreasury(
                address(this),
                taxLevied
            );
        }
        return true;
    }

    ///@dev this function will show the balance of warbucks within the contract
    ///@dev when money is spent within the game it can be taxed an deposited within this contract
    function viewSiltRevenues() public view returns (uint256) {
        return (WarBucks(warBucksAddress).balanceOf(address(this)));
    }

    ///@dev when money is spent within the game it can be taxed an deposited within this contract
    ///@dev this function will allow the contract owner to withdraw the warbucks from this contract into the owners wallet
    function withdrawSiltRevenues(uint256 amount) public onlyOwner {
        WarBucks(warBucksAddress).transfer(msg.sender, amount);
        emit OwnerWithdrawMilfRevenues(amount);
    }

    ///@dev this function allows the contract owner to set the tax rate in game purchases are taxed at
    ///@dev the tax rate will be the % of the purchase price that is minted into this contract that can be withdrawn later
    function setSilt(uint256 newPercentage) public onlyOwner {
        silt = newPercentage;
    }

    ///@dev this funtion will reuturn the game tax rate
    ///@return uint256 will be the tax rate at which purchases in the game are taxed at
    function getSilt() public view returns (uint256) {
        return silt;
    }

    function demonetizeNation(uint256 id) public onlyOwner {
        idToTreasury[id].demonitized = true;
    }

    function remonetizeNation(uint256 id) public onlyOwner {
        idToTreasury[id].demonitized = false;
    }

    function isNationDemonetized(uint256 id) public view returns (bool) {
        return idToTreasury[id].demonitized;
    }

    function getTotalGameBalance() public view returns (uint256) {
        return totalGameBalance;
    }
}
","keccak256":"0x74072ed055f0ce8b3e990171ac7b9f0ac619ed5d37cea69bc787790e91067272","license":"BUSL-1.1"},"contracts/War.sol":{"content":"//SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

import "./NationStrength.sol";
import "./Military.sol";
import "./Wonders.sol";
import "./CountryMinter.sol";
import "./Treasury.sol";
import "./KeeperFile.sol";
import "./Forces.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "hardhat/console.sol";

///@title WarContract
///@author OxSnosh
///@notice this contact will allow a naion owner to declare war on another nation
///@notice this contract will also handle peace negotiations
///@dev this contract inherits from openzeppelin's ownable contract
contract WarContract is Ownable {
    uint256 public warId;
    address private nationStrength;
    address private breakBlockade;
    address private navalAttack;
    address private airBattle;
    address private groundBattle;
    address private cruiseMissile;
    address private blockade;
    address private nuke;
    address private forces;

    NationStrengthContract nsc;
    MilitaryContract mil;
    WondersContract1 won1;
    CountryMinter mint;
    TreasuryContract tres;
    KeeperContract keep;
    ForcesContract forc;

    struct War {
        uint256 offenseId;
        uint256 defenseId;
        bool active;
        uint256 dayStarted;
        bool peaceDeclared;
        bool offensePeaceOffered;
        bool defensePeaceOffered;
        uint256 offenseBlockades;
        uint256 defenseBlockades;
        mapping(uint256 => uint256) offenseIdToCruiseMissileLaunchesToday;
        mapping(uint256 => uint256) defenseIdToCruiseMissileLaunchesToday;
    }

    struct OffenseDeployed1 {
        mapping(uint256 => bool) offenseDeployedToday;
        uint256 soldiersDeployed;
        uint256 tanksDeployed;
    }

    struct DefenseDeployed1 {
        mapping(uint256 => bool) defenseDeployedToday;
        uint256 soldiersDeployed;
        uint256 tanksDeployed;
    }

    struct OffenseLosses {
        uint256 warId;
        uint256 nationId;
        uint256 soldiersLost;
        uint256 tanksLost;
        uint256 cruiseMissilesLost;
        uint256 aircraftLost;
        uint256 navyShipsLost;
        uint256 infrastructureLost;
        uint256 technologyLost;
        uint256 landLost;
    }

    struct DefenseLosses {
        uint256 warId;
        uint256 nationId;
        uint256 soldiersLost;
        uint256 tanksLost;
        uint256 cruiseMissilesLost;
        uint256 aircraftLost;
        uint256 navyShipsLost;
        uint256 infrastructureLost;
        uint256 technologyLost;
        uint256 landLost;
    }

    mapping(uint256 => War) public warIdToWar;
    mapping(uint256 => OffenseDeployed1) public warIdToOffenseDeployed1;
    mapping(uint256 => DefenseDeployed1) public warIdToDefenseDeployed1;
    mapping(uint256 => OffenseLosses) public warIdToOffenseLosses;
    mapping(uint256 => DefenseLosses) public warIdToDefenseLosses;
    mapping(uint256 => uint256[]) public idToActiveWars;
    mapping(uint256 => uint256[]) public idToOffensiveWars;
    mapping(uint256 => uint256[]) public idToDeactivatedWars;

    event WarDeclared(
        uint256 indexed warId,
        uint256 indexed offenseId,
        uint256 indexed defenseId
    );

    event PeaceOffered(uint256 indexed warId, uint256 indexed offeredBy);

    event PeaceDeclared(
        uint256 indexed warId,
        uint256 indexed offenseId,
        uint256 indexed defenseId
    );

    ///@dev this function is only callable by the contract owner
    ///@dev this function will be called immediately after contract deployment in order to set contract pointers
    function settings(
        address _countryMinter,
        address _nationStrength,
        address _military,
        address _breakBlockadeAddress,
        address _navalAttackAddress,
        address _airBattleAddress,
        address _groundBattle,
        address _cruiseMissile,
        address _forces,
        address _wonders1,
        address _keeper
    ) public onlyOwner {
        mint = CountryMinter(_countryMinter);
        nationStrength = _nationStrength;
        breakBlockade = _breakBlockadeAddress;
        navalAttack = _navalAttackAddress;
        airBattle = _airBattleAddress;
        groundBattle = _groundBattle;
        nsc = NationStrengthContract(_nationStrength);
        mil = MilitaryContract(_military);
        cruiseMissile = _cruiseMissile;
        forces = _forces;
        won1 = WondersContract1(_wonders1);
        keep = KeeperContract(_keeper);
    }

    function settings2(
        address _treasury,
        address _forces,
        address _blockade,
        address _nuke
    ) public onlyOwner {
        tres = TreasuryContract(_treasury);
        forces = _forces;
        forc = ForcesContract(_forces);
        blockade = _blockade;
        nuke = _nuke;
    }

    modifier onlyCruiseMissileContract() {
        require(
            msg.sender == cruiseMissile,
            "only callable from cruise missile contract"
        );
        _;
    }

    ///@dev this function is only callable from a nation owner and allow a natio nto eclare war on another nation
    ///@notice this function allows a nation to declare war on another nation
    ///@notice when war is declared the nations can attack each other
    ///@param offenseId is the nation id of the nation declaring war
    ///@param defenseId is the nation id of the nation having war declared on it
    ///@notice a nation can only have a maximum of 4 offensive wars (5 with a foreign army base)
    function declareWar(uint256 offenseId, uint256 defenseId) public {
        bool isOwner = mint.checkOwnership(offenseId, msg.sender);
        require(isOwner, "!nation owner");
        bool isOwner2 = mint.checkOwnership(defenseId, msg.sender);
        require (!isOwner2, "cannot declare war on yourself");
        bool check = warCheck(offenseId, defenseId);
        require(check, "war not possible");
        uint day = keep.getGameDay();
        War storage war = warIdToWar[warId];
        war.offenseId = offenseId;
        war.defenseId = defenseId;
        war.active = true;
        war.dayStarted = day;
        war.peaceDeclared = false;
        war.offensePeaceOffered = false;
        war.defensePeaceOffered = false;
        OffenseLosses memory newOffenseLosses = OffenseLosses(
            warId,
            0,
            0,
            0,
            0,
            0,
            0,
            0,
            0,
            0
        );
        warIdToOffenseLosses[warId] = newOffenseLosses;
        DefenseLosses memory newDefenseLosses = DefenseLosses(
            warId,
            0,
            0,
            0,
            0,
            0,
            0,
            0,
            0,
            0
        );
        warIdToDefenseLosses[warId] = newDefenseLosses;
        uint256[] storage offensiveWars = idToOffensiveWars[offenseId];
        uint256 maxOffensiveWars = 4;
        bool foreignArmyBase = won1.getForeignArmyBase(offenseId);
        if (foreignArmyBase) {
            maxOffensiveWars = 5;
        }
        offensiveWars.push(warId);
        require(
            offensiveWars.length <= maxOffensiveWars,
            "you do not have an offensive war slot available"
        );
        uint256[] storage offenseActiveWars = idToActiveWars[offenseId];
        offenseActiveWars.push(warId);
        uint256[] storage defenseActiveWars = idToActiveWars[defenseId];
        defenseActiveWars.push(warId);
        initializeDeployments(warId);
        emit WarDeclared(warId, offenseId, defenseId);
        warId++;
    }

    function warCheck(
        uint256 offenseId,
        uint256 defenseId
    ) internal view returns (bool) {
        (bool isWarOkOffense, ) = mil.getWarPeacePreference(offenseId);
        require(isWarOkOffense == true, "you are in peace mode");
        (bool isWarOkDefense, ) = mil.getWarPeacePreference(defenseId);
        require(isWarOkDefense == true, "nation in peace mode");
        bool isStrengthWithinRange = checkStrength(offenseId, defenseId);
        require(
            isStrengthWithinRange == true,
            "nation strength is not within range to declare war"
        );
        bool defenderInactive = tres.checkInactive(defenseId);
        require(!defenderInactive, "defender inactive");
        bool offenseInactive = tres.checkInactive(offenseId);
        require(!offenseInactive, "nation inactive");
        uint256[] memory defenseActiveWars = idToActiveWars[defenseId];
        //if you get caught spoofing wars to occupy slots your nations will be demonitized
        uint256[] memory activeWars = idToActiveWars[offenseId];
        require(
            defenseActiveWars.length < 50 && activeWars.length < 50,
            "defender or attacker already has 50 active wars"
        );
        for (uint256 i = 0; i < activeWars.length; i++) {
            uint256 war = activeWars[i];
            (uint256 offense, uint256 defense) = getInvolvedParties(war);
            require(
                offense != defenseId && defense != defenseId,
                "already at war with this nation"
            );
        }
        return true;
    }

    function offensiveWarLength(
        uint256 offenseId
    ) public view returns (uint256) {
        uint256[] memory offensiveWars = idToOffensiveWars[offenseId];
        return offensiveWars.length;
    }

    function offensiveWarReturn(
        uint256 offenseId
    ) public view returns (uint256[] memory) {
        uint256[] memory offensiveWars = idToOffensiveWars[offenseId];
        return offensiveWars;
    }

    function nationActiveWarsReturn(
        uint256 offenseId
    ) public view returns (uint256[] memory) {
        uint256[] memory activeWarsArray = idToActiveWars[offenseId];
        return activeWarsArray;
    }

    ///@dev this is an internal function that will be balled by the declare war function and set up several structs that will keep track of each war
    function initializeDeployments(uint256 _warId) internal {
        OffenseDeployed1 storage newOffenseDeployed1 = warIdToOffenseDeployed1[
            _warId
        ];
        newOffenseDeployed1.soldiersDeployed = 0;
        newOffenseDeployed1.tanksDeployed = 0;
        DefenseDeployed1 storage newDefenseDeployed1 = warIdToDefenseDeployed1[
            _warId
        ];
        newDefenseDeployed1.soldiersDeployed = 0;
        newDefenseDeployed1.tanksDeployed = 0;
    }

    ///@dev this is a public view function that will return a boolean value if the nations are able to fight eachother
    ///@notice this function will return a boolean value of true if the nations are able to fight eachother
    ///@notice in order for a war to be declared the offense strength must be within 75% and 133% of the defending nation
    ///@param offenseId is the nation id of the aggressor nation
    ///@param defenseId if the nation id of the defending nation
    ///@return bool will be true if the nations are within range where war is possible
    function checkStrength(
        uint256 offenseId,
        uint256 defenseId
    ) public view returns (bool) {
        uint256 offenseStrength = nsc.getNationStrength(offenseId);
        uint256 defenseStrength = nsc.getNationStrength(defenseId);
        uint256 strengthRatio = ((offenseStrength * 100) / defenseStrength);
        if (strengthRatio < 75) {
            return false;
        } else if (strengthRatio > 133) {
            return false;
        } else {
            return true;
        }
    }

    ///@dev this is a public function that will allow a nation involved in a war to offer peace
    ///@notice this funtion will allow a nation involved in a war to offer peace
    ///@param offerId is the nation offering peace
    ///@param _warId is the war id for the war where peace is being offered
    ///@notice if the offense and the defense offer peace then peace will be declares
    ///@notice an attack will nullify any existing peace offers
    function offerPeace(uint256 offerId, uint256 _warId) public {
        bool isOwner = mint.checkOwnership(offerId, msg.sender);
        require(isOwner, "!nation owner");
        uint256 offenseNation = warIdToWar[_warId].offenseId;
        uint256 defenseNation = warIdToWar[_warId].defenseId;
        require(
            offerId == offenseNation || offerId == defenseNation,
            "nation not involved in this war"
        );
        if (offerId == offenseNation) {
            warIdToWar[_warId].offensePeaceOffered = true;
        }
        if (offerId == defenseNation) {
            warIdToWar[_warId].defensePeaceOffered = true;
        }
        emit PeaceOffered(_warId, offerId);
        bool offensePeaceCheck = warIdToWar[_warId].offensePeaceOffered;
        bool defensePeaceCheck = warIdToWar[_warId].defensePeaceOffered;
        if (offensePeaceCheck == true && defensePeaceCheck == true) {
            warIdToWar[_warId].peaceDeclared = true;
            warIdToWar[_warId].active = false;
            emit PeaceDeclared(_warId, offenseNation, defenseNation);
            removeActiveWar(_warId);
        }
    }

    ///@dev this is a public view function that will return information about a war
    ///@notice this function will return information about a war
    ///@param _warId is the war id of the war being queried
    ///@return offensePeaceOffered is a boolean value that will be true if the offense offered peace
    ///@return defensePeaceOffered is a boolean value that will be true if the defense nation offered peace
    ///@return warActive will return a boolean true if the war is still active
    ///@return peaceDeclared will return a boolean true of peace was declared by both sides
    function returnWar(
        uint256 _warId
    ) public view returns (bool, bool, bool, bool) {
        bool offensePeaceOffered = warIdToWar[_warId].offensePeaceOffered;
        bool defensePeaceOffered = warIdToWar[_warId].defensePeaceOffered;
        bool warActive = warIdToWar[_warId].active;
        bool peaceDeclared = warIdToWar[_warId].peaceDeclared;
        return (
            offensePeaceOffered,
            defensePeaceOffered,
            warActive,
            peaceDeclared
        );
    }

    function returnWarDetails(
        uint256 _warId
    )
        public
        view
        returns (
            uint256,
            uint256,
            bool,
            uint256,
            bool,
            bool,
            bool,
            uint256,
            uint256
        )
    {
        War storage war = warIdToWar[_warId];
        return (
            war.offenseId,
            war.defenseId,
            war.active,
            war.dayStarted,
            war.peaceDeclared,
            war.offensePeaceOffered,
            war.defensePeaceOffered,
            war.offenseBlockades,
            war.defenseBlockades
        );
    }

    ///@dev this is an internal function that will remove the active war from each nation when peace is declared or the war expires
    function removeActiveWar(uint256 _warId) internal {
        (uint256 offenseId, uint256 defenseId) = getInvolvedParties(_warId);
        uint256[] storage offenseActiveWars = idToActiveWars[offenseId];
        uint256[] storage offenseDeactivatedWars = idToDeactivatedWars[
            offenseId
        ];
        for (uint256 i = 0; i < offenseActiveWars.length; i++) {
            if (offenseActiveWars[i] == _warId) {
                offenseDeactivatedWars.push(_warId);
                offenseActiveWars[i] = offenseActiveWars[
                    offenseActiveWars.length - 1
                ];
                offenseActiveWars.pop();
            }
        }
        uint256[] storage offensiveWars = idToOffensiveWars[offenseId];
        for (uint256 i = 0; i < offensiveWars.length; i++) {
            if (offensiveWars[i] == _warId) {
                offensiveWars[i] = offensiveWars[offensiveWars.length - 1];
                offensiveWars.pop();
            }
        }
        uint256[] storage defenseActiveWars = idToActiveWars[defenseId];
        uint256[] storage defenseDeactivatedWars = idToDeactivatedWars[
            defenseId
        ];
        for (uint256 i = 0; i < defenseActiveWars.length; i++) {
            if (defenseActiveWars[i] == _warId) {
                defenseDeactivatedWars.push(_warId);
                defenseActiveWars[i] = defenseActiveWars[
                    defenseActiveWars.length - 1
                ];
                defenseActiveWars.pop();
            }
        }
        warIdToWar[_warId].active = false;
    }

    modifier onlyNavyBattle() {
        require(
            msg.sender == breakBlockade || msg.sender == navalAttack,
            "function only callable from navy battle contract"
        );
        _;
    }

    ///@dev this function is only callable from the navy battle contract and will increment navy casualties
    function addNavyCasualties(
        uint256 _warId,
        uint256 nationId,
        uint256 navyCasualties
    ) public onlyNavyBattle {
        (uint256 offenseId, uint256 defenseId) = getInvolvedParties(_warId);
        if (offenseId == nationId) {
            warIdToOffenseLosses[_warId].navyShipsLost = navyCasualties;
        }
        if (defenseId == nationId) {
            warIdToDefenseLosses[_warId].navyShipsLost = navyCasualties;
        }
    }

    ///@dev this function is only callable from the cruise missile contract and will only allow a nation to launch 2 cruise missiles per war per day
    ///@notice this function will only allow a nation to launch 2 cruise missiles per war per day
    function incrementCruiseMissileAttack(
        uint256 _warId,
        uint256 nationId
    ) public onlyCruiseMissileContract {
        (uint256 offenseId, uint256 defenseId) = getInvolvedParties(_warId);
        uint256 day = keep.getGameDay();
        War storage war = warIdToWar[_warId];
        if (nationId == offenseId) {
            require(
                war.offenseIdToCruiseMissileLaunchesToday[day] < 2,
                "too many launches today"
            );
            war.offenseIdToCruiseMissileLaunchesToday[day] += 1;
        } else if (nationId == defenseId) {
            require(
                war.defenseIdToCruiseMissileLaunchesToday[day] < 2,
                "too many launches today"
            );
            war.defenseIdToCruiseMissileLaunchesToday[day] += 1;
        }
    }

    function getCruiseMissileLaunchesToday(
        uint256 _warId,
        uint256 id
    ) public view returns (uint256) {
        (uint256 offenseId, uint256 defenseId) = getInvolvedParties(_warId);
        uint256 day = keep.getGameDay();
        uint256 launches;
        War storage war = warIdToWar[_warId];
        if (id == offenseId) {
            launches = war.offenseIdToCruiseMissileLaunchesToday[day];
        } else if (id == defenseId) {
            launches = war.defenseIdToCruiseMissileLaunchesToday[day];
        }
        return launches;
    }

    ///@dev this is a public view function that will take a war id as a parameter and return whether the war is active or not
    ///@notice this function will return whether a war is active or not
    ///@param _warId is the warId being queries
    ///@return bool will be true if the war is active
    function isWarActive(uint256 _warId) public view returns (bool) {
        bool isActive = true;
        bool warDoesntExist = warIdToWar[_warId].active;
        (, bool expired) = getDaysLeft(_warId);
        bool peaceDeclared = warIdToWar[_warId].peaceDeclared;
        if (expired == true || peaceDeclared == true) {
            isActive = false;
        } else if (warDoesntExist == false) {
            isActive = false;
        }
        console.log(isActive, "war active");
        return isActive;
    }

    ///@dev this is a public view function that will return the two members f a given warId
    ///@param _warId is the warId of the war being queried
    ///@return offenseId is the nation id of the offensive nation in the war
    ///@return defenseId is the nation id of the defensive nation in the war
    function getInvolvedParties(
        uint256 _warId
    ) public view returns (uint256, uint256) {
        uint256 offenseId = warIdToWar[_warId].offenseId;
        uint256 defenseId = warIdToWar[_warId].defenseId;
        return (offenseId, defenseId);
    }

    ///@dev this is a public view function that will return true if one of the nations has offered peace
    ///@notice this function will return true if one of the nations has offered peace
    ///@param _warId is the war id of the war being queried
    ///@return bool will be true if one of the nation has offered peace
    function isPeaceOffered(uint256 _warId) public view returns (bool) {
        bool peaceOffered = false;
        if (
            warIdToWar[_warId].offensePeaceOffered == true ||
            warIdToWar[_warId].defensePeaceOffered == true
        ) {
            peaceOffered = true;
        }
        return peaceOffered;
    }

    modifier onlyBattle() {
        require(
            msg.sender == groundBattle ||
                msg.sender == airBattle ||
                msg.sender == navalAttack ||
                msg.sender == breakBlockade ||
                msg.sender == blockade ||
                msg.sender == cruiseMissile ||
                msg.sender == nuke,
            "function only callable during an attack"
        );
        _;
    }

    function cancelPeaceOffersUponAttack(uint256 _warId) public onlyBattle {
        warIdToWar[_warId].offensePeaceOffered = false;
        warIdToWar[_warId].defensePeaceOffered = false;
    }

    ///@dev this is a publci view function that will return the number of days left in a war
    ///@dev wars expire after 7 days when days left == 0
    function getDaysLeft(uint256 _warId) public view returns (uint256, bool) {
        uint256 day = keep.getGameDay();
        uint256 warDaysElapsed;
        if (day >= warIdToWar[_warId].dayStarted + 7) {
            warDaysElapsed = 7;
        } else {
            warDaysElapsed = day - warIdToWar[_warId].dayStarted;
        }
        uint256 daysLeft = (7 - warDaysElapsed);
        bool expired = false;
        if (daysLeft == 0) {
            expired = true;
        }
        return (daysLeft, expired);
    }

    modifier onlyAirBattle() {
        require(
            msg.sender == airBattle,
            "function only callable from air battle"
        );
        _;
    }

    ///@dev this function is only callable from the air battle contract
    ///@dev this function will increment air battle casualties
    function addAirBattleCasualties(
        uint256 _warId,
        uint256 nationId,
        uint256 battleCausalties
    ) public onlyAirBattle {
        (uint256 offenseId, uint256 defenseId) = getInvolvedParties(_warId);
        if (offenseId == nationId) {
            warIdToOffenseLosses[_warId].aircraftLost = battleCausalties;
        }
        if (defenseId == nationId) {
            warIdToDefenseLosses[_warId].aircraftLost = battleCausalties;
        }
    }

    modifier onlyForcesContract() {
        require(msg.sender == forces, "only callable from forces");
        _;
    }

    ///@dev this function is only callable from the forces contact
    ///@notice this function will allow a nation to deploy ground forces (soldiers and tanks) to a given war
    function deployForcesToWar(
        uint256 nationId,
        uint256 _warId,
        uint256 soldiersToDeploy,
        uint256 tanksToDeploy
    ) public onlyForcesContract {
        bool isActive = isWarActive(_warId);
        require(isActive, "war not active");
        (uint256 offenseId, uint256 defenseId) = getInvolvedParties(_warId);
        require(
            nationId == offenseId || nationId == defenseId,
            "nation not involved"
        );
        uint256 day = keep.getGameDay();
        if (nationId == offenseId) {
            bool deployedToday = warIdToOffenseDeployed1[_warId]
                .offenseDeployedToday[day];
            require(!deployedToday, "already deployed forces today");
            warIdToOffenseDeployed1[_warId]
                .soldiersDeployed += soldiersToDeploy;
            warIdToOffenseDeployed1[_warId].tanksDeployed += tanksToDeploy;
            warIdToOffenseDeployed1[_warId].offenseDeployedToday[day] = true;
        } else if (nationId == defenseId) {
            bool deployedToday = warIdToDefenseDeployed1[_warId]
                .defenseDeployedToday[day];
            require(!deployedToday, "already deployed forces today");
            warIdToDefenseDeployed1[_warId]
                .soldiersDeployed += soldiersToDeploy;
            warIdToDefenseDeployed1[_warId].tanksDeployed += tanksToDeploy;
            warIdToDefenseDeployed1[_warId].defenseDeployedToday[day] = true;
        }
    }

    ///@dev this is a public view function that will return the number of ground forces a nation has deploed to a war
    ///@param _warId is the war id of the war where the forces are deployed
    ///@param attackerId is the nation id of the nation being queried
    ///@return soldiersDeployed is the soldiers the given nation has deployed to the given war
    ///@return tanksDeployed is the tanks the given nation has deployed to the given war
    function getDeployedGroundForces(
        uint256 _warId,
        uint256 attackerId
    ) public view returns (uint256, uint256) {
        uint256 soldiersDeployed;
        uint256 tanksDeployed;
        (uint256 offenseId, uint256 defenseId) = getInvolvedParties(_warId);
        if (attackerId == offenseId) {
            soldiersDeployed = warIdToOffenseDeployed1[_warId].soldiersDeployed;
            tanksDeployed = warIdToOffenseDeployed1[_warId].tanksDeployed;
        } else if (attackerId == defenseId) {
            soldiersDeployed = warIdToDefenseDeployed1[_warId].soldiersDeployed;
            tanksDeployed = warIdToDefenseDeployed1[_warId].tanksDeployed;
        }
        return (soldiersDeployed, tanksDeployed);
    }

    modifier onlyGroundBattle() {
        require(
            msg.sender == groundBattle,
            "function only callable from navy battle contract"
        );
        _;
    }

    ///@dev this function is only callable from the ground battle contract
    ///@dev this function will decrement deployed ground forces based on casualties
    function decreaseGroundBattleLosses(
        uint256 soldierLosses,
        uint256 tankLosses,
        uint256 attackerId,
        uint256 _warId
    ) public onlyGroundBattle returns (bool) {
        (uint256 offenseId, uint256 defenseId) = getInvolvedParties(_warId);

        if (attackerId == offenseId) {
            require(
                warIdToOffenseDeployed1[_warId].soldiersDeployed >= soldierLosses,
                "underflow: offense soldier losses too high"
            );
            require(
                warIdToOffenseDeployed1[_warId].tanksDeployed >= tankLosses,
                "underflow: offense tank losses too high"
            );
            warIdToOffenseDeployed1[_warId].soldiersDeployed -= soldierLosses;
            warIdToOffenseDeployed1[_warId].tanksDeployed -= tankLosses;
        } else if (attackerId == defenseId) {
            require(
                warIdToDefenseDeployed1[_warId].soldiersDeployed >= soldierLosses,
                "underflow: defense soldier losses too high"
            );
            require(
                warIdToDefenseDeployed1[_warId].tanksDeployed >= tankLosses,
                "underflow: defense tank losses too high"
            );
            warIdToDefenseDeployed1[_warId].soldiersDeployed -= soldierLosses;
            warIdToDefenseDeployed1[_warId].tanksDeployed -= tankLosses;
        } else {
            revert("attacker not involved in this war");
        }

        return true;
    }

    function recallTroopsFromDeactivatedWars(uint256 id) public {
        require(mint.checkOwnership(id, msg.sender), "!nation owner");

        // 1) Recall from ALL ACTIVE wars; deactivate those that are expired.
        uint256[] memory activeWars = idToActiveWars[id];
        for (uint256 i = 0; i < activeWars.length; ) {
            uint256 war = activeWars[i];

            (uint256 offenseId, uint256 defenseId) = getInvolvedParties(war);
            if (id == offenseId) {
                uint256 s = warIdToOffenseDeployed1[war].soldiersDeployed;
                uint256 t = warIdToOffenseDeployed1[war].tanksDeployed;
                if (s > 0) {
                    forc.withdrawSoldiers(s, id);
                    warIdToOffenseDeployed1[war].soldiersDeployed = 0;
                }
                if (t > 0) {
                    forc.withdrawTanks(t, id);
                    warIdToOffenseDeployed1[war].tanksDeployed = 0;
                }
            } else if (id == defenseId) {
                uint256 s = warIdToDefenseDeployed1[war].soldiersDeployed;
                uint256 t = warIdToDefenseDeployed1[war].tanksDeployed;
                if (s > 0) {
                    forc.withdrawSoldiers(s, id);
                    warIdToDefenseDeployed1[war].soldiersDeployed = 0;
                }
                if (t > 0) {
                    forc.withdrawTanks(t, id);
                    warIdToDefenseDeployed1[war].tanksDeployed = 0;
                }
            }

            // If expired, move it out of active list.
            (, bool expired) = getDaysLeft(war);
            if (expired) {
                removeActiveWar(war);
            }

            unchecked { ++i; }
        }

        // 2) Recall from DEACTIVATED wars (guards avoid double-withdraw) and clear the list.
        uint256[] storage deactivatedWars = idToDeactivatedWars[id];
        for (uint256 i = deactivatedWars.length; i > 0; ) {
            uint256 war = deactivatedWars[i - 1];

            (uint256 offenseId, uint256 defenseId) = getInvolvedParties(war);
            if (id == offenseId) {
                uint256 s = warIdToOffenseDeployed1[war].soldiersDeployed;
                uint256 t = warIdToOffenseDeployed1[war].tanksDeployed;
                if (s > 0) {
                    forc.withdrawSoldiers(s, id);
                    warIdToOffenseDeployed1[war].soldiersDeployed = 0;
                }
                if (t > 0) {
                    forc.withdrawTanks(t, id);
                    warIdToOffenseDeployed1[war].tanksDeployed = 0;
                }
            } else if (id == defenseId) {
                uint256 s = warIdToDefenseDeployed1[war].soldiersDeployed;
                uint256 t = warIdToDefenseDeployed1[war].tanksDeployed;
                if (s > 0) {
                    forc.withdrawSoldiers(s, id);
                    warIdToDefenseDeployed1[war].soldiersDeployed = 0;
                }
                if (t > 0) {
                    forc.withdrawTanks(t, id);
                    warIdToDefenseDeployed1[war].tanksDeployed = 0;
                }
            }

            deactivatedWars.pop();
            unchecked { --i; }
        }
    }

}
","keccak256":"0xcd4ac47606ffe8ec1c7fe506890162eb8b10428f506ed6946fd0499864ab5380","license":"BUSL-1.1"},"contracts/WarBucks.sol":{"content":"//SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/// @title ERC20 Contract WarBucks
/// @author OxSnosh
/// @dev This is the contact for the currency used to purchase items in the game
/// @dev Inherits from OpenZeppelin ERC20 and Ownable
/// @dev The deployer of the contract will be the owner
contract WarBucks is ERC20, Ownable {
    address treasury;
    address countryMinter;


    /// @param initialSupply is the inital supply of WarBucks currency
    /// @dev The initial supply is minted to the deployer of the contract
    constructor(uint256 initialSupply) ERC20("WarBucks", "WBX") {
        _mint(msg.sender, initialSupply);
    }

    /// @dev This modifier exists in order to allow the TreasuryContract to mint and burn tokens
    modifier onlyTreasury() {
        require(
            msg.sender == treasury,
            "function only callable from treasury contract"
        );
        _;
    }

    modifier onlyCountryMinter() {
        require(
            msg.sender == countryMinter,
            "function only callable from countryMinter contract"
        );
        _;
    }

    /// @dev This function is called by the owner after deployment in order to update the treasury contract address for the onlyTreasury modifer
    /// @param _treasury is the address of the treasury contract
    /// @param _countryMinter is the address of the countryMinter contract
    function settings(address _treasury, address _countryMinter) public onlyOwner {
        treasury = _treasury;
        countryMinter = _countryMinter;
    }

    /// @dev This function can only be called from the treasury contract
    /// @dev This function enables a nation owner to withdraw in game balance from the treasury contract and mint a corresponding amount of WarBucks tokens into a nations wallet 
    /// @dev WarBucks has 18 decimals as does the in game balance
    /// @param account this is the address of the nation owner and the wallet where the tokens are being minted
    /// @param amount is the amount of tokens being burned
    function mintFromTreasury(
        address account,
        uint256 amount
    ) external onlyTreasury {
        _mint(account, amount);
    }

    /// @dev This function can only be called from the treasury contract
    /// @dev This function enables a nation owner to deposit in game balance into a nation and burn a corresponding amount of WarBucks tokens into a nations wallet
    /// @dev WarBucks has 18 decimals as does the in game balance
    /// @notice This function will burn tokens when they are deposited into a nation. For every token burned, a nations in game balance will increase +1
    /// @param account this is the address of the nation owner and the wallet where the tokens are being burned
    /// @param amount is the amount of tokens being burned
    function burnFromTreasury(
        address account,
        uint256 amount
    ) external onlyTreasury {
        _burn(account, amount);
    }


    function burnFromMint(
        address account,
        uint256 amount
    ) external onlyCountryMinter {
        _burn(account, amount);
    }

    function mint(address account, uint256 amount) public onlyOwner {
        _mint(account, amount);
    }

    function burn(address account, uint256 amount) public onlyOwner {
        _burn(account, amount);
    }
}
","keccak256":"0x6b0d4085f18238e3ee3de0b6c877ef16f509a6491b61db876d0472931577430f","license":"BUSL-1.1"},"contracts/Wonders.sol":{"content":"//SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

import "./Treasury.sol";
import "./Infrastructure.sol";
import "./Improvements.sol";
import "./Forces.sol";
import "./CountryMinter.sol";
import "./Resources.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

///@title WondersContract1
///@author OxSnosh
///@dev this contract inherits from openzeppelin's ownable contract
///@notice this contract will strore information about a nations wonders
contract WondersContract1 is Ownable, ReentrancyGuard {
    address public treasuryAddress;
    address public wondersContract2Address;
    address public wondersContract3Address;
    address public wondersContract4Address;
    address public infrastructureAddress;
    address public countryMinter;
    uint256 public agricultureDevelopmentCost = 30000000 * (10**18);
    uint256 public antiAirDefenseNetworkCost = 50000000 * (10**18);
    uint256 public centralIntelligenceAgencyCost = 40000000 * (10**18);
    uint256 public disasterReliefAgencyCost = 40000000 * (10**18);
    uint256 public empWeaponizationCost = 200000000 * (10**18);
    uint256 public falloutShelterSystemCost = 40000000 * (10**18);
    uint256 public federalAidCommissionCost = 25000000 * (10**18);
    uint256 public federalReserveCost = 100000000 * (10**18);
    uint256 public foreignAirForceBaseCost = 35000000 * (10**18);
    uint256 public foreignArmyBaseCost = 200000000 * (10**18);
    uint256 public foreignNavalBaseCost = 200000000 * (10**18);

    CountryMinter mint;

    struct Wonders1 {
        uint256 wonderCount;
        //Agriculture Development Program
        //$30,000,000
        //Increases land size by 15%
        //Increases citizen income +$2.00,
        //Increases the citizen-bonus for land from 0.2 to 0.5.
        //Requires 3,000 land purchased, 500 technology.
        bool agricultureDevelopmentProgram;
        //Anti-Air Defense Network
        //$50,000,000
        //Reduces odds of incoming aircraft attacks against your nation -25%.
        //Reduces aircraft attack damages against your nation -15%.
        bool antiAirDefenseNetwork;
        //Central Intelligence Agency
        //$40,000,000
        //Increases the number of spies that your nation can support +250 and
        //increases your nation's spy attack strength +10%.
        //Only viewable by the user who owns it.
        bool centralIntelligenceAgency;
        //Disaster Relief Agency
        //$40,000,000
        //The disaster relief agency helps restore your nation and its people after emergency situations
        //Increases population +3%
        //and opens one extra foreign aid slot.
        bool disasterReliefAgency;
        //EMP Weaponization
        //$200,000,000
        //Provides attackers with 5,000 or more technology the option to launch a targeted EMP nuclear attack.
        //Nuclear weapons can target higher infrastructure, higher land, or higher technology damage based on player choice when launching nukes.
        //When you choose to target infrastructure, land, or technology you are trading more damage to your target for less damage for the other two.
        //For instance, if you choose to target infrastructure you will do more base damage to infrastructure but less damage to land and technology.
        //Requires 5,000 technology and a Weapons Research Complex to purchase.
        bool empWeaponization;
        //Fallout Shelter System
        //$40,000,000
        //Allows 50% of your defending soldiers to survive a nuclear strike
        //(Does not prevent nuclear Anarchy but does prevent troops from being totally depleted),
        //Reduces tank, cruise missile, and aircraft, losses from a nuclear strike by -25%,
        //Reduces nuclear vulnerable navy losses by 12%,
        //Reduces nuclear anarchy effects by 1 day.
        //Requires 6,000 infrastructure, 2,000 technology.
        bool falloutShelterSystem;
        //Federal Aid Commission
        //$25,000,000
        //Raises the cap on foreign money aid +50% provided that the foreign aid recipient also has a Federal Aid Commission wonder.
        //Allows two nations with the Federal Aid Commission wonder to send secret foreign aid.
        //Secret foreign aid costs the sender 200% the value of the items that are sent.
        bool federalAidCommission;
        //Federal Reserve
        //$100,000,000
        //Increases the number of banks that can be purchased +2.
        //Requires Stock Market.
        bool federalReserve;
        //Foreign Air Force Base -
        //$35,000,000 -
        //Raises the aircraft limit +20 for your nation and
        //increases the number of aircraft that can be sent in each attack mission +20.
        bool foreignAirForceBase;
        //Foreign Army Base -
        //$200,000,000 -
        //Adds an extra +1 offensive war slot.
        //Requires 8,000 technology to purchase.
        bool foreignArmyBase;
        //Foreign Naval Base -
        //$200,000,000 -
        //Allows +2 naval vessels to be purchased per day (+1 in Peace Mode)
        //and also allows +1 naval deployment per day.
        //Requires 20,000 infrastructure.
        bool foreignNavalBase;
    }

    mapping(uint256 => Wonders1) public idToWonders1;

    event Wonder1Purchased(
        uint256 indexed id,
        uint256 indexed wonderId
    );

    event Wonder1Deleted(
        uint256 indexed id,
        uint256 indexed wonderId
    );

    modifier approvedAddress() {
        require(
            msg.sender == wondersContract2Address ||
                msg.sender == wondersContract3Address ||
                msg.sender == wondersContract4Address,
            "Unable to call"
        );
        _;
    }

    modifier onlyCountryMinter() {
        require(
            msg.sender == countryMinter,
            "only callable from countryMinter"
        );
        _;
    }

    ///@dev this function is only callable by the contract owner
    ///@dev this function will be called immediately after contract deployment in order to set contract pointers
    function settings(
        address _treasuryAddress,
        address _wonderContract2Address,
        address _wonderContract3Address,
        address _wonderContract4Address,
        address _infrastructureAddress,
        address _countryMinter
    ) public onlyOwner {
        treasuryAddress = _treasuryAddress;
        wondersContract2Address = _wonderContract2Address;
        wondersContract3Address = _wonderContract3Address;
        wondersContract4Address = _wonderContract4Address;
        infrastructureAddress = _infrastructureAddress;
        countryMinter = _countryMinter;
        mint = CountryMinter(_countryMinter);
    }

    ///@dev this is a public view function that will return the number of wonders a given nation owns
    ///@notice this function will return the number of wonders a given nation owns
    ///@param id is the nation id of the nation being queried
    ///@return count is the number of wonder a given nation owns
    function getWonderCount(uint256 id) public view returns (uint256 count) {
        count = idToWonders1[id].wonderCount;
        return count;
    }

    ///@dev this function is only callable from other wonder contracts
    ///@dev this function will increment the number of wonders a nation owns when a wonder is purchased in another contract
    function addWonderCount(uint256 id) public approvedAddress {
        idToWonders1[id].wonderCount += 1;
    }

    ///@dev this function is only callable from other wonder contracts
    ///@dev this function will decremeny the number of wonders a nation owns when a wonder is deleted in another contract
    function subtractWonderCount(uint256 id) public approvedAddress {
        idToWonders1[id].wonderCount -= 1;
    }

    ///@dev this function is only callable from the country minter contract
    ///@notice this function will be called when a nation is minted and allow a nation to buy the wonders in this contract
    function generateWonders1(uint256 id) public onlyCountryMinter {
        require(
            idToWonders1[id].wonderCount == 0,
            "Nation already has wonders"
        );
        Wonders1 memory newWonders1 = Wonders1(
            0,
            false,
            false,
            false,
            false,
            false,
            false,
            false,
            false,
            false,
            false,
            false
        );
        idToWonders1[id] = newWonders1;
    }


    ///@dev this function is only callable by the contract owner
    function updateAgricultureDevelopmentCost(
        uint256 newPrice
    ) public onlyOwner {
        agricultureDevelopmentCost = newPrice;
    }

    ///@dev this function is only callable by the contract owner
    function updateAntiAirDefenseNetworkCost(
        uint256 newPrice
    ) public onlyOwner {
        antiAirDefenseNetworkCost = newPrice;
    }

    ///@dev this function is only callable by the contract owner
    function updateCentralIntelligenceAgencyCost(
        uint256 newPrice
    ) public onlyOwner {
        centralIntelligenceAgencyCost = newPrice;
    }

    ///@dev this function is only callable by the contract owner
    function updateDisasterReliefAgencyCost(uint256 newPrice) public onlyOwner {
        disasterReliefAgencyCost = newPrice;
    }

    ///@dev this function is only callable by the contract owner
    function updateEmpWeaponizationCost(uint256 newPrice) public onlyOwner {
        empWeaponizationCost = newPrice;
    }

    ///@dev this function is only callable by the contract owner
    function updateFalloutShelterSystemCost(uint256 newPrice) public onlyOwner {
        falloutShelterSystemCost = newPrice;
    }

    ///@dev this function is only callable by the contract owner
    function updateFederalAidCommissionCost(uint256 newPrice) public onlyOwner {
        federalAidCommissionCost = newPrice;
    }

    ///@dev this function is only callable by the contract owner
    function updateFederalReserveCost(uint256 newPrice) public onlyOwner {
        federalReserveCost = newPrice;
    }

    ///@dev this function is only callable by the contract owner
    function updateForeignAirForceBaseCost(uint256 newPrice) public onlyOwner {
        foreignAirForceBaseCost = newPrice;
    }

    ///@dev this function is only callable by the contract owner
    function updateForeignArmyBaseCost(uint256 newPrice) public onlyOwner {
        foreignArmyBaseCost = newPrice;
    }

    ///@dev this function is only callable by the contract owner
    function updateForeignNavalBaseCost(uint256 newPrice) public onlyOwner {
        foreignNavalBaseCost = newPrice;
    }

    ///@dev this function is only callable from a nation owner and allows a nation to purchase the wonders in this contract
    ///@notice this function allows a nation owner to purchase the wonders in this contract
    ///@param countryId is the nationId of the country purchasing a wonder
    /**@param wonderId is the id of the wonder
     * 1. agricultrual development program
     * 2. air defense network
     * 3. central intelligence agency
     * 4. disaster relief agency
     * 5. emp weaponization
     * 6. fallout shelter system 
     * 7. federal aid commission
     * 8. federal reserve
     * 9. foreign air force base
     * 10. foreign army base
     * 11. foreign naval base
    */
    function buyWonder1(uint256 countryId, uint256 wonderId) public nonReentrant {
        bool isOwner = mint.checkOwnership(countryId, msg.sender);
        require(isOwner, "!nation owner");
        require(wonderId <= 11, "Invalid wonder ID");
        uint256 balance = TreasuryContract(treasuryAddress).checkBalance(
            countryId
        );
        if (wonderId == 1) {
            require(
                balance >= agricultureDevelopmentCost,
                "Insufficient balance"
            );
            bool existingWonder = idToWonders1[countryId]
                .agricultureDevelopmentProgram;
            require(existingWonder == false, "Already owned");
            uint256 techAmount = InfrastructureContract(infrastructureAddress)
                .getTechnologyCount(countryId);
            require(techAmount >= 500, "Requires 500 Tech");
            uint256 landAmount = InfrastructureContract(infrastructureAddress)
                .getLandCount(countryId);
            require(landAmount >= 3000, "Requires 3000 Land");
            idToWonders1[countryId].agricultureDevelopmentProgram = true;
            idToWonders1[countryId].wonderCount += 1;
            require(TreasuryContract(treasuryAddress).spendBalance(
                countryId,
                agricultureDevelopmentCost
            ));
        } else if (wonderId == 2) {
            require(
                balance >= antiAirDefenseNetworkCost,
                "Insufficient balance"
            );
            bool existingWonder = idToWonders1[countryId].antiAirDefenseNetwork;
            require(existingWonder == false, "Already owned");
            idToWonders1[countryId].antiAirDefenseNetwork = true;
            idToWonders1[countryId].wonderCount += 1;
            require(TreasuryContract(treasuryAddress).spendBalance(
                countryId,
                antiAirDefenseNetworkCost
            ));
        } else if (wonderId == 3) {
            require(
                balance >= centralIntelligenceAgencyCost,
                "Insufficient balance"
            );
            bool existingWonder = idToWonders1[countryId]
                .centralIntelligenceAgency;
            require(existingWonder == false, "Already owned");
            idToWonders1[countryId].centralIntelligenceAgency = true;
            idToWonders1[countryId].wonderCount += 1;
            require(TreasuryContract(treasuryAddress).spendBalance(
                countryId,
                centralIntelligenceAgencyCost
            ));
        } else if (wonderId == 4) {
            require(
                balance >= disasterReliefAgencyCost,
                "Insufficient balance"
            );
            bool existingWonder = idToWonders1[countryId].disasterReliefAgency;
            require(existingWonder == false, "Already owned");
            idToWonders1[countryId].disasterReliefAgency = true;
            idToWonders1[countryId].wonderCount += 1;
            require(TreasuryContract(treasuryAddress).spendBalance(
                countryId,
                disasterReliefAgencyCost
            ));
        } else if (wonderId == 5) {
            require(balance >= empWeaponizationCost, "Insufficient balance");
            bool existingWonder = idToWonders1[countryId].empWeaponization;
            require(existingWonder == false, "Already owned");
            bool isWrcThere = WondersContract4(wondersContract4Address)
                .getWeaponsResearchCenter(countryId);
            require(
                isWrcThere == true,
                "Must own Weapons Research Center to purchase"
            );
            uint256 techAmount = InfrastructureContract(infrastructureAddress)
                .getTechnologyCount(countryId);
            require(
                techAmount >= 5000,
                "Must have 5000 Technology to purchase"
            );
            idToWonders1[countryId].empWeaponization = true;
            idToWonders1[countryId].wonderCount += 1;
            require(TreasuryContract(treasuryAddress).spendBalance(
                countryId,
                empWeaponizationCost
            ));
        } else if (wonderId == 6) {
            require(
                balance >= falloutShelterSystemCost,
                "Insufficient balance"
            );
            bool existingWonder = idToWonders1[countryId].falloutShelterSystem;
            require(existingWonder == false, "Already owned");
            uint256 infrastructureAmount = InfrastructureContract(
                infrastructureAddress
            ).getInfrastructureCount(countryId);
            require(
                infrastructureAmount >= 6000,
                "Requires 6000 Infrastructure to purchase"
            );
            uint256 technologyAmount = InfrastructureContract(
                infrastructureAddress
            ).getTechnologyCount(countryId);
            require(technologyAmount >= 2000, "Requires 2000 Tech to purchase");
            idToWonders1[countryId].falloutShelterSystem = true;
            idToWonders1[countryId].wonderCount += 1;
            require(TreasuryContract(treasuryAddress).spendBalance(
                countryId,
                falloutShelterSystemCost
            ));
        } else if (wonderId == 7) {
            require(
                balance >= federalAidCommissionCost,
                "Insufficient balance"
            );
            bool existingWonder = idToWonders1[countryId].federalAidCommission;
            require(existingWonder == false, "Already owned");
            idToWonders1[countryId].federalAidCommission = true;
            idToWonders1[countryId].wonderCount += 1;
            require(TreasuryContract(treasuryAddress).spendBalance(
                countryId,
                federalAidCommissionCost
            ));
        } else if (wonderId == 8) {
            require(balance >= federalReserveCost, "Insufficient balance");
            bool existingWonder = idToWonders1[countryId].federalReserve;
            require(existingWonder == false, "Already owned");
            bool isStockMarket = WondersContract4(wondersContract4Address)
                .getStockMarket(countryId);
            require(
                isStockMarket == true,
                "Required to own stock market to purchase"
            );
            idToWonders1[countryId].federalReserve = true;
            idToWonders1[countryId].wonderCount += 1;
            require(TreasuryContract(treasuryAddress).spendBalance(
                countryId,
                federalReserveCost
            ));
